import React, { useState } from 'react';
import RegisterUser from './RegisterUser';
import ManagerCreate from './ManagerCreate'; // Âà•„ÅÆ„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà„Å™„ÇâOK

import './App.css';
// App.js „ÇÑ RegisterUser.jsx „Å™„Å©
import { supabase } from './supabaseClient'; // ‚Üê src „Å®Âêå„ÅòÈöéÂ±§„Å™„Çâ„Åì„Çå„ÅßOK



function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginMessage, setLoginMessage] = useState('');
  const [role, setRole] = useState('');
  const [currentStep, setCurrentStep] = useState('');
  const [managerNumber, setManagerNumber] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftTimes, setShiftTimes] = useState([]);
  const [selectedDay, setSelectedDay] = useState('');
  const [bulkStart, setBulkStart] = useState('');
  const [bulkEnd, setBulkEnd] = useState('');
  const [selectedDays, setSelectedDays] = useState([]); // ‚Üê „Åì„Åì„ÇíÈÖçÂàó„Å´Â§âÊõ¥
  const [managerAuth, setManagerAuth] = useState(false);
const [managerPass, setManagerPass] = useState('');
const [managerPassError, setManagerPassError] = useState('');
const [managerStep, setManagerStep] = useState('');
const [shiftSummary, setShiftSummary] = useState([]);




  const handleLogin = (e) => {
    e.preventDefault();
    if (id === 'kouki' && password === '0306') {
      setIsLoggedIn(true);
      setLoginMessage('');
    } else {
      setLoginMessage('ID„Åæ„Åü„ÅØ„Éë„Çπ„ÉØ„Éº„Éâ„ÅåÈÅï„ÅÑ„Åæ„Åô');
    }
  };

  const selectRole = (selectedRole) => {
    setRole(selectedRole);
    if (selectedRole === 'staff') setCurrentStep('');
  };

  const handleNext = () => {
    if (!managerNumber.trim()) {
      alert('ÁÆ°ÁêÜÁï™Âè∑„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }
    if (!startDate || !endDate || startDate > endDate) {
      alert('Ê≠£„Åó„ÅÑÈñãÂßãÊó•„ÉªÁµÇ‰∫ÜÊó•„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    const dates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      const yyyy = d.getFullYear();
      const mm = String(d.getMonth() + 1).padStart(2, '0');
      const dd = String(d.getDate()).padStart(2, '0');
      dates.push({ date: `${yyyy}-${mm}-${dd}`, start: '', end: '' });
      d.setDate(d.getDate() + 1);
    }

    setShiftTimes(dates);
    setCurrentStep('shiftInput');
  };

  const getWeekday = (dateStr) => {
    const days = ['Êó•','Êúà','ÁÅ´','Ê∞¥','Êú®','Èáë','Âúü'];
    const d = new Date(dateStr);
    return days[d.getDay()];
  };

  const handleTimeChange = (index, field, value) => {
    const updated = [...shiftTimes];
    updated[index][field] = value;
    setShiftTimes(updated);
  };

  const handleBulkApply = () => {
  const updated = shiftTimes.map(item => {
    const day = getWeekday(item.date);
    if (selectedDays.includes('ÂÖ®„Å¶') || selectedDays.includes(day)) {
      return { ...item, start: bulkStart, end: bulkEnd };
    }
    return item;
  });
  setShiftTimes(updated);
};

  const getColorForDay = (day) => {
  switch (day) {
    case 'Êúà': return '#6c5ce7';
    case 'ÁÅ´': return '#00b894';
    case 'Ê∞¥': return '#fd79a8';
    case 'Êú®': return '#e17055';
    case 'Èáë': return '#0984e3';
    case 'Âúü': return '#fab1a0';
    case 'Êó•': return '#d63031';
    case 'ÂÖ®„Å¶': return '#636e72';
    default: return '#b2bec3';
  }
};


  const toggleSelectedDay = (day) => {
  setSelectedDays((prev) =>
    prev.includes(day)
      ? prev.filter(d => d !== day)  // ÈÅ∏ÊäûËß£Èô§
      : [...prev, day]               // ÈÅ∏ÊäûËøΩÂä†
  );
};


  const handleSubmit = async () => {
    try {
      const results = [];
      for (const shift of shiftTimes) {
        const { data, error } = await supabase
          .from('shifts')
          .insert([{
            manager_number: managerNumber,
            date: shift.date,
            start_time: shift.start,
            end_time: shift.end,
          }]);
        if (error) throw error;
        results.push(data);
      }

      alert('„Ç∑„Éï„Éà„Çí‰øùÂ≠ò„Åó„Åæ„Åó„ÅüÔºÅ');
      setCurrentStep('');
      setRole('');
      setIsLoggedIn(false);
      setId('');
      setPassword('');
      setManagerNumber('');
      setStartDate('');
      setEndDate('');
      setShiftTimes([]);
    } catch (error) {
      console.error('‰øùÂ≠ò„Ç®„É©„Éº:', error.message);
      alert(`‰øùÂ≠ò‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü: ${error.message}`);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="login-wrapper">
        <form className="login-card" onSubmit={handleLogin}>
          <h2>„É≠„Ç∞„Ç§„É≥</h2>
          <input type="text" placeholder="„É≠„Ç∞„Ç§„É≥ID" value={id} onChange={e => setId(e.target.value)} required />
          <input type="password" placeholder="„Éë„Çπ„ÉØ„Éº„Éâ" value={password} onChange={e => setPassword(e.target.value)} required />
          <button type="submit">„É≠„Ç∞„Ç§„É≥</button>
          {loginMessage && <p className="error-msg">{loginMessage}</p>}
        </form>
      </div>
    );
  }

  if (!role) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>ÂΩπËÅ∑„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ</h2>
          <div className="button-row">
            <button onClick={() => selectRole('staff')}>üë§ „Ç¢„É´„Éê„Ç§„Éà</button>
            <button onClick={() => selectRole('manager')}>üëë Â∫óÈï∑</button>
          </div>
        </div>
      </div>
    );
  }

  if (role === 'manager' && !managerAuth) {
  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>Â∫óÈï∑„Éë„Çπ„ÉØ„Éº„Éâ</h2>
        <input
          type="password"
          placeholder="„Éë„Çπ„ÉØ„Éº„Éâ„ÇíÂÖ•Âäõ"
          value={managerPass}
          onChange={(e) => setManagerPass(e.target.value)}
        />
        <button
          onClick={() => {
            if (managerPass === '0306') {
              setManagerAuth(true);
              setManagerPassError('');
            } else {
              setManagerPassError('„Éë„Çπ„ÉØ„Éº„Éâ„ÅåÈÅï„ÅÑ„Åæ„Åô');
            }
          }}
        >
          Ë™çË®º
        </button>
        {managerPassError && <p className="error-msg">{managerPassError}</p>}
      </div>
    </div>
  );
}

if (role === 'manager' && managerAuth && managerStep === '') {
  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>Â∫óÈï∑„É°„Éã„É•„Éº</h2>
        <div className="button-row" style={{ flexDirection: 'column', gap: '1rem' }}>
          <button onClick={() => setManagerStep('create')}>üìÖ „Ç∑„Éï„Éà‰ΩúÊàê</button>
          <button onClick={() => setManagerStep('view')}>üëÄ „Ç∑„Éï„ÉàÁ¢∫Ë™ç</button>
          <button onClick={() => setManagerStep('register')}>‚ûï Êñ∞‰∫∫ÁôªÈå≤</button>
        </div>
        <button onClick={() => {
          setRole('');

          setId('');
          setPassword('');

          setIsLoggedIn(false);
          setManagerAuth(false);
          setManagerPass('');
        }}>„É≠„Ç∞„Ç¢„Ç¶„Éà</button>
      </div>
    </div>
  );
}

if (role === 'manager' && managerAuth && managerStep === 'register') {
  return (
    <div className="login-wrapper">
      <RegisterUser />
    </div>
  );
}

if (role === 'manager' && managerAuth && managerStep === 'create') {
  return (
    <ManagerCreate onBack={() => setManagerStep('')} />
  );
}
if (role === 'manager' && managerAuth && managerStep === 'create') {
  const handleFetchShifts = async () => {
    if (!startDate || !endDate || startDate > endDate) {
      alert('Ê≠£„Åó„ÅÑÈñãÂßãÊó•„ÉªÁµÇ‰∫ÜÊó•„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    const { data: shifts, error: shiftError } = await supabase
      .from('shifts')
      .select('*')
      .gte('date', startDate)
      .lte('date', endDate);

    if (shiftError) {
      alert('„Ç∑„Éï„Éà„Éá„Éº„ÇøÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      return;
    }

    const { data: users, error: userError } = await supabase
      .from('users')
      .select('*');

    if (userError) {
      alert('„É¶„Éº„Ç∂„ÉºÊÉÖÂ†±ÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      return;
    }

    // manager_number „ÅßÂêçÂâç„Çí„Å≤„ÇÇ‰ªò„Åë
    const summary = shifts.map(shift => {
      const user = users.find(u => u.number === shift.manager_number);
      return {
        date: shift.date,
        name: user ? user.name : `‰∏çÊòé (${shift.manager_number})`,
        start: shift.start_time,
        end: shift.end_time
      };
    });

    setShiftSummary(summary);
  };

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>„Ç∑„Éï„Éà‰ΩúÊàê</h2>

        <label>ÈñãÂßãÊó•:</label>
        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />

        <label>ÁµÇ‰∫ÜÊó•:</label>
        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />

        <button onClick={handleFetchShifts} style={{ marginTop: '1rem' }}>Ê¨°„Å∏</button>

        {shiftSummary.length > 0 && (
          <div style={{ marginTop: '1rem', maxHeight: '300px', overflowY: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th style={{ borderBottom: '1px solid #ccc' }}>Êó•‰ªò</th>
                  <th style={{ borderBottom: '1px solid #ccc' }}>ÂêçÂâç</th>
                  <th style={{ borderBottom: '1px solid #ccc' }}>ÈñãÂßã</th>
                  <th style={{ borderBottom: '1px solid #ccc' }}>ÁµÇ‰∫Ü</th>
                </tr>
              </thead>
              <tbody>
                {shiftSummary.map((s, i) => (
                  <tr key={i}>
                    <td>{s.date}</td>
                    <td>{s.name}</td>
                    <td>{s.start}</td>
                    <td>{s.end}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        <button onClick={() => setManagerStep('')} style={{ marginTop: '1rem' }}>Êàª„Çã</button>
      </div>
    </div>
  );
}




  if (role === 'staff' && currentStep === 'shiftPeriod') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>Êñ∞Ë¶èÊèêÂá∫</h2>
          <label>ÁÆ°ÁêÜÁï™Âè∑:</label>
          <input type="text" value={managerNumber} onChange={e => setManagerNumber(e.target.value)} />
          <label>ÈñãÂßãÊó•:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>ÁµÇ‰∫ÜÊó•:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <button onClick={handleNext}>Ê¨°„Å∏</button>
        </div>
      </div>
    );
  }



  if (role === 'staff' && currentStep === 'shiftInput') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>„Ç∑„Éï„ÉàÂÖ•Âäõ</h2>
          <p>ÁÆ°ÁêÜÁï™Âè∑: <strong>{managerNumber}</strong></p>

          <div style={{
  display: 'flex',
  flexWrap: 'nowrap',
  overflowX: 'auto',
  gap: '0.5rem',
  paddingBottom: '1rem',
}}>
  {['ÂÖ®„Å¶', 'Êúà', 'ÁÅ´', 'Ê∞¥', 'Êú®', 'Èáë', 'Âúü', 'Êó•'].map((day) => (
    <button
      key={day}
      onClick={() => toggleSelectedDay(day)}
      style={{
        backgroundColor: selectedDays.includes(day) ? '#95a5a6' : getColorForDay(day),
        color: 'white',
        padding: '0.5rem 1rem',
        border: 'none',
        borderRadius: '6px',
        cursor: 'pointer',
        whiteSpace: 'nowrap',
        minWidth: '60px'
      }}
    >
      {day}
    </button>
  ))}
</div>




          {selectedDays.length > 0 && (
  <div style={{ marginBottom: '1rem' }}>
    <label>ÈñãÂßãÊôÇÈñìÔºö</label>
    <input type="time" value={bulkStart} onChange={e => setBulkStart(e.target.value)} />
