import React, { useState } from 'react';

// 外部コンポーネントはここではダミーとして定義されています
const RegisterUser = ({ onBack }) => <div className="dummy-comp"><h2>新人登録画面</h2><button onClick={onBack}>戻る</button></div>;
const ManagerCreate = () => <div className="dummy-comp"><h2>シフト作成画面</h2></div>;
const StaffShiftView = ({ onBack }) => <div className="dummy-comp"><h2>シフト確認画面</h2><button onClick={onBack}>戻る</button></div>;
const ManagerShiftView = ({ onBack }) => <div className="dummy-comp"><h2>シフト確認 (店長)</h2><button onClick={onBack}>戻る</button></div>;
const StaffShiftEdit = ({ onBack }) => <div className="dummy-comp"><h2>シフト変更画面</h2><button onClick={onBack}>戻る</button></div>;
const ShiftAnalysis = ({ onBack }) => <div className="dummy-comp"><h2>シフト分析画面</h2><button onClick={onBack}>戻る</button></div>;
const ManagerAttendance = ({ onBack }) => <div className="dummy-comp"><h2>退勤管理画面</h2><button onClick={onBack}>戻る</button></div>;
const StaffWorkHours = ({ onBack }) => <div className="dummy-comp"><h2>就労時間画面</h2><button onClick={onBack}>戻る</button></div>;

// Supabaseクライアントは未定義のためダミーとして宣言 (ローカルテスト用)
const supabase = {
  from: () => ({
    insert: () => Promise.resolve({ error: null }),
  }),
};


// お問い合わせ先のURL
const INQUIRY_URL = "https://docs.google.com/forms/d/e/1FAIpQLSci0UYQ7BKfXjhVj8x3WBR5ncFxxCo_lsV11kY5TaI15wlKSQ/viewform?usp=header";

/**
 * お問い合わせボタンコンポーネント
 */
const InquiryButton = () => (
    <a
        href={INQUIRY_URL}
        target="_blank"
        rel="noopener noreferrer"
        style={{
            marginTop: '1.5rem',
            backgroundColor: '#FF9800', // 目立つオレンジ色
            color: 'white',
            border: '2px solid #FB8C00',
            borderRadius: '8px',
            width: '100%',
            height: '50px',
            cursor: 'pointer',
            fontSize: '16px',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            textDecoration: 'none',
            boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
            transition: 'all 0.3s ease',
        }}
        onMouseEnter={(e) => {
            e.target.style.backgroundColor = '#FB8C00';
            e.target.style.transform = 'translateY(-2px)';
            e.target.style.boxShadow = '0 6px 12px rgba(0,0,0,0.3)';
        }}
        onMouseLeave={(e) => {
            e.target.style.backgroundColor = '#FF9800';
            e.target.style.transform = 'translateY(0px)';
            e.target.style.boxShadow = '0 4px 8px rgba(0,0,0,0.2)';
        }}
        title="お問い合わせフォームを開く"
    >
        お問い合わせ・ご意見
    </a>
);


function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginMessage, setLoginMessage] = useState('');
  const [role, setRole] = useState('');
  const [currentStep, setCurrentStep] = useState('');
  const [managerNumber, setManagerNumber] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftTimes, setShiftTimes] = useState([]);
  const [bulkStart, setBulkStart] = useState('');
  const [bulkEnd, setBulkEnd] = useState('');
  const [selectedDays, setSelectedDays] = useState([]);
  const [managerAuth, setManagerAuth] = useState(false);
  const [managerPass, setManagerPass] = useState('');
  const [managerPassError, setManagerPassError] = useState('');
  const [managerStep, setManagerStep] = useState('');

  const [navigationHistory, setNavigationHistory] = useState([]);

  const resetAllInputs = () => {
    setManagerNumber('');
    setStartDate('');
    setEndDate('');
    setShiftTimes([]);
    setBulkStart('');
    setBulkEnd('');
    setSelectedDays([]);
    setManagerPass('');
    setManagerPassError('');
  };

  const pushToHistory = (state) => {
    setNavigationHistory(prev => [...prev, state]);
  };

  const goBack = () => {
    if (navigationHistory.length === 0) return;

    const previousState = navigationHistory[navigationHistory.length - 1];
    const newHistory = navigationHistory.slice(0, -1);

    setNavigationHistory(newHistory);
    setRole(previousState.role || '');
    setCurrentStep(previousState.currentStep || '');
    setManagerAuth(previousState.managerAuth || false);
    setManagerStep(previousState.managerStep || '');
    setIsLoggedIn(previousState.isLoggedIn !== undefined ? previousState.isLoggedIn : true);

    resetAllInputs();
  };

  const shouldShowBackButton = () => {
    return navigationHistory.length > 0;
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (id === 'kouki' && password === '0306') {
      setIsLoggedIn(true);
      setLoginMessage('');
      setNavigationHistory([]);
    } else {
      setLoginMessage('IDまたはパスワードが違います');
    }
  };

  const selectRole = (selectedRole) => {
    pushToHistory({
      role: '',
      currentStep: '',
      managerAuth: false,
      managerStep: '',
      isLoggedIn: true
    });

    setRole(selectedRole);
    if (selectedRole === 'staff') setCurrentStep('');
  };

  const handleNext = () => {
    if (!managerNumber.trim()) {
      // ユーザーに見えるalert()ではなく、コンソールエラーを使用
      console.error('管理番号を入力してください');
      return;
    }
    if (!startDate || !endDate || startDate > endDate) {
       // ユーザーに見えるalert()ではなく、コンソールエラーを使用
      console.error('正しい開始日・終了日を入力してください');
      return;
    }

    pushToHistory({
      role: role,
      currentStep: 'shiftPeriod',
      managerAuth: managerAuth,
      managerStep: managerStep,
      isLoggedIn: true
    });

    const dates = [];
    const d = new Date(startDate);
    // 日付をまたぐ際にエラーが起こらないよう、時間を固定
    d.setHours(12, 0, 0, 0); 
    const end = new Date(endDate);
    end.setHours(12, 0, 0, 0);

    while (d <= end) {
      const yyyy = d.getFullYear();
      const mm = String(d.getMonth() + 1).padStart(2, '0');
      const dd = String(d.getDate()).padStart(2, '0');
      dates.push({ date: `${yyyy}-${mm}-${dd}`, start: '', end: '' });
      d.setDate(d.getDate() + 1);
    }

    setShiftTimes(dates);
    setCurrentStep('shiftInput');
  };

  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const d = new Date(dateStr);
    return days[d.getDay()];
  };

  const handleTimeChange = (index, field, value) => {
    const updated = [...shiftTimes];
    updated[index][field] = value;
    setShiftTimes(updated);
  };

  const handleBulkApply = () => {
    const updated = shiftTimes.map(item => {
      const day = getWeekday(item.date);
      if (selectedDays.includes('全て') || selectedDays.includes(day)) {
        return { ...item, start: bulkStart, end: bulkEnd };
      }
      return item;
    });
    setShiftTimes(updated);
  };

  const getColorForDay = (day) => {
    switch (day) {
      case '月': return '#6c5ce7';
      case '火': return '#00b894';
      case '水': return '#fd79a8';
      case '木': return '#e17055';
      case '金': return '#0984e3';
      case '土': return '#fab1a0';
      case '日': return '#d63031';
      case '全て': return '#636e72';
      default: return '#b2bec3';
    }
  };

  const toggleSelectedDay = (day) => {
    setSelectedDays((prev) =>
      prev.includes(day)
        ? prev.filter(d => d !== day)
        : [...prev, day]
    );
  };

  const handleSubmit = async () => {
    try {
      // ユーザーに見えるalert()の代わりにコンソールログを使用
      console.log('シフトをデータベースに保存中...');
      for (const shift of shiftTimes) {
        // start_timeとend_timeが空でないシフトのみを挿入
        if (shift.start && shift.end) {
          const { error } = await supabase
            .from('shifts')
            .insert([{
              manager_number: managerNumber,
              date: shift.date,
              start_time: shift.start,
              end_time: shift.end,
            }]);
          if (error) throw error;
        }
      }

      console.log('シフトを保存しました！'); // alert()を非表示メッセージに変更
      // 成功後、状態をリセットしてログイン画面に戻る
      setCurrentStep('');
      setRole('');
      setIsLoggedIn(false);
      setId('');
      setPassword('');
      resetAllInputs();
      setNavigationHistory([]);
    } catch (error) {
      // ユーザーに見えるalert()の代わりにコンソールエラーを使用
      console.error(`保存中にエラーが発生しました: ${error.message}`); 
    }
  };

  const BackButton = () => {
    if (!shouldShowBackButton()) return null;

    return (
      <button
        onClick={goBack}
        style={{
          position: 'absolute',
          top: '1rem',
          left: '1rem',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: '2px solid #45a049',
          borderRadius: '8px',
          width: '80px',
          height: '40px',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '4px',
          zIndex: 1000,
          boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.target.style.backgroundColor = '#45a049';
          e.target.style.transform = 'translateY(-2px)';
          e.target.style.boxShadow = '0 6px 12px rgba(0,0,0,0.3)';
        }}
        onMouseLeave={(e) => {
          e.target.style.backgroundColor = '#4CAF50';
          e.target.style.transform = 'translateY(0px)';
          e.target.style.boxShadow = '0 4px 8px rgba(0,0,0,0.2)';
        }}
        title="前のページに戻る"
      >
        ← 戻る
      </button>
    );
  };

  if (!isLoggedIn) {
    return (
      <div className="login-wrapper">
        <style dangerouslySetInnerHTML={{__html: `
            .login-wrapper {
                display: flex;
                justify-content: center;
                align-items: center;
                min-height: 100vh;
                background-color: #f4f7f9;
                font-family: 'Inter', sans-serif;
            }
            .login-card {
                background: white;
                padding: 3rem;
                border-radius: 12px;
                box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
                width: 100%;
                max-width: 400px;
                display: flex;
                flex-direction: column;
                gap: 1rem;
            }
            .login-card h2 {
                text-align: center;
                color: #333;
                font-size: 24px;
                margin-bottom: 1.5rem;
                font-weight: 700;
            }
            .login-card input[type="text"], 
            .login-card input[type="password"],
            .login-card input[type="date"],
            .login-card input[type="time"] {
                padding: 12px;
                border: 1px solid #ddd;
                border-radius: 6px;
                font-size: 16px;
                transition: border-color 0.3s;
            }
            .login-card input:focus {
                border-color: #2196F3;
                outline: none;
            }
            .login-card button {
                padding: 12px;
                color: white;
                border: none;
                border-radius: 8px;
                cursor: pointer;
                font-size: 16px;
                font-weight: 600;
                transition: background-color 0.3s, transform 0.1s, box-shadow 0.3s;
                box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            }
            .login-card button:hover {
                transform: translateY(-1px);
                box-shadow: 0 6px 10px rgba(0, 0, 0, 0.15);
            }
            .login-card button:active {
                transform: translateY(0);
                box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            }
            .error-msg {
                color: #e74c3c;
                text-align: center;
                margin-top: 10px;
            }
            .button-row {
                display: flex;
                gap: 1rem;
                justify-content: space-between;
                margin-top: 1rem;
            }
            .button-row button {
                flex-grow: 1;
                padding: 15px;
            }
            .login-card label {
                font-weight: 600;
                color: #555;
            }
            .dummy-comp {
                padding: 20px;
                text-align: center;
                background: white;
                border-radius: 12px;
                box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
                margin-top: 2rem;
            }
        `}} />
        <form className="login-card" onSubmit={handleLogin}>
          <h2>ログイン</h2>
          <input type="text" placeholder="ログインID" value={id} onChange={e => setId(e.target.value)} required />
          <input type="password" placeholder="パスワード" value={password} onChange={e => setPassword(e.target.value)} required />
          <button type="submit" style={{ backgroundColor: '#2196F3' }}>ログイン</button>
          {loginMessage && <p className="error-msg">{loginMessage}</p>}
        </form>
      </div>
    );
  }

  if (!role) {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ position: 'relative' }}>
          <BackButton />
          <h2>役職を選択してください</h2>
          <div className="button-row">
            <button onClick={() => selectRole('staff')} style={{ backgroundColor: '#1976D2' }}>アルバイト</button>
            <button onClick={() => selectRole('manager')} style={{ backgroundColor: '#1565C0' }}>店長</button>
          </div>
        </div>
      </div>
    );
  }

  if (role === 'manager' && !managerAuth) {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ position: 'relative' }}>
          <BackButton />
          <h2>店長パスワード</h2>
          <input type="password" placeholder="パスワードを入力" value={managerPass} onChange={(e) => setManagerPass(e.target.value)} />
          <button onClick={() => {
            if (managerPass === '0306') {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: false,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setManagerAuth(true);
              setManagerPassError('');
            } else {
              setManagerPassError('パスワードが違います');
            }
          }} style={{ backgroundColor: '#1554A5' }}>認証</button>
          {managerPassError && <p className="error-msg">{managerPassError}</p>}
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === '') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ position: 'relative' }}>
          <BackButton />
          <h2>店長メニュー</h2>
          <div className="button-row" style={{ flexDirection: 'column', gap: '1rem' }}>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('create');
            }} style={{ backgroundColor: '#1E88E5' }}>シフト作成</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('view');
            }} style={{ backgroundColor: '#1976D2' }}>シフト確認</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('attendance');
            }} style={{ backgroundColor: '#0D47A1' }}>退勤管理</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('analysis');
            }} style={{ backgroundColor: '#1565C0' }}>シフト分析</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('register');
            }} style={{ backgroundColor: '#1554A5' }}>新人登録</button>
          </div>
          <button onClick={() => {
            setRole('');
            setId('');
            setPassword('');
            setIsLoggedIn(false);
            setManagerAuth(false);
            resetAllInputs();
            setNavigationHistory([]);
          }} style={{ backgroundColor: '#FF5722', marginTop: '1.5rem' }}>ログアウト</button>

          {/* 店長メニューにお問い合わせボタンを追加 */}
          <InquiryButton />
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'register') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <RegisterUser onBack={() => setManagerStep('')} />
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'create') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <ManagerCreate />
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'view') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <ManagerShiftView onBack={() => setManagerStep('')} />
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'attendance') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <ManagerAttendance onBack={() => setManagerStep('')} />
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'analysis') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <ShiftAnalysis onBack={() => setManagerStep('')} />
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftView') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <StaffShiftView onBack={() => setCurrentStep('')} />
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftEdit') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <StaffShiftEdit onBack={() => setCurrentStep('')} />
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'workHours') {
    return (
      <div style={{ position: 'relative' }}>
        <BackButton />
        <StaffWorkHours onBack={() => setCurrentStep('')} />
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftPeriod') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ position: 'relative' }}>
          <BackButton />
          <h2>新規提出</h2>
          <label>管理番号:</label>
          <input type="text" value={managerNumber} onChange={e => setManagerNumber(e.target.value)} />
          <label>開始日:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>終了日:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <button onClick={handleNext} style={{ backgroundColor: '#1976D2' }}>次へ</button>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftInput') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ position: 'relative' }}>
          <BackButton />
          <h2>シフト入力</h2>
          <p>管理番号: <strong>{managerNumber}</strong></p>

          <div style={{ display: 'flex', overflowX: 'auto', gap: '0.5rem', paddingBottom: '1rem' }}>
            {['全て', '月', '火', '水', '木', '金', '土', '日'].map((day) => (
              <button
                key={day}
                onClick={() => toggleSelectedDay(day)}
                style={{
                  backgroundColor: selectedDays.includes(day) ? '#95a5a6' : getColorForDay(day),
                  color: 'white', padding: '0.5rem 1rem', border: 'none', borderRadius: '6px', cursor: 'pointer',
                }}>
                {day}
              </button>
            ))}
          </div>

          {selectedDays.length > 0 && (
            <div style={{ marginBottom: '1rem' }}>
              <label>開始時間：</label>
              <input type="time" value={bulkStart} onChange={e => setBulkStart(e.target.value)} />
              <label style={{ marginLeft: '1rem' }}>終了時間：</label>
              <input type="time" value={bulkEnd} onChange={e => setBulkEnd(e.target.value)} />
              <button onClick={handleBulkApply} style={{ marginLeft: '1rem', backgroundColor: '#2196F3' }}>一括適用</button>
            </div>
          )}

          <div style={{ maxHeight: '300px', overflowY: 'auto', marginBottom: '1rem' }}>
            {shiftTimes.map((item, i) => (
              <div key={item.date} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem', alignItems: 'center' }}>
                <div style={{ width: '110px' }}>{item.date}（{getWeekday(item.date)}）</div>
                <input type="time" value={item.start} onChange={e => handleTimeChange(i, 'start', e.target.value)} />
                <input type="time" value={item.end} onChange={e => handleTimeChange(i, 'end', e.target.value)} />
              </div>
            ))}
          </div>
          <button onClick={handleSubmit} style={{ backgroundColor: '#1976D2' }}>送信</button>
        </div>
      </div>
    );
  }

  if (role === 'staff') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ position: 'relative' }}>
          <BackButton />
          <h2>アルバイトメニュー</h2>
          <div className="button-row" style={{ flexDirection: 'column', gap: '1rem' }}>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftPeriod');
            }} style={{ backgroundColor: '#1E88E5' }}>新規提出</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftView');
            }} style={{ backgroundColor: '#1976D2' }}>シフト確認</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftEdit');
            }} style={{ backgroundColor: '#1565C0' }}>シフト変更</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('workHours');
            }} style={{ backgroundColor: '#0D47A1' }}>就労時間</button>
          </div>
          <button onClick={() => {
            setRole('');
            setId('');
            setPassword('');
            setIsLoggedIn(false);
            setCurrentStep('');
            resetAllInputs();
            setNavigationHistory([]);
          }} style={{ backgroundColor: '#FF5722', marginTop: '1.5rem' }}>ログアウト</button>

          {/* アルバイトメニューにお問い合わせボタンを追加 */}
          <InquiryButton />
        </div>
      </div>
    );
  }

  return null;
}

export default App;
