import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';


export default function RegisterUser() {
  const [name, setName] = useState('');
  const [managerNumber, setManagerNumber] = useState('');
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState('');

  // 登録済みユーザー取得
  const fetchUsers = async () => {
    const { data, error } = await supabase.from('users').select('*').order('manager_number');
    if (error) {
      console.error('ユーザー取得エラー:', error);
    } else {
      setUsers(data);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // 新規登録
  const handleRegister = async () => {
    if (!name.trim() || !managerNumber.trim()) {
      setMessage('名前と管理番号を入力してください');
      return;
    }

    // 管理番号の重複チェック
    const exists = users.some(user => user.manager_number === managerNumber);
    if (exists) {
      setMessage('管理番号が既に登録されています');
      return;
    }

    const { error } = await supabase.from('users').insert([{ name, manager_number: managerNumber }]);
    if (error) {
      setMessage(`登録エラー: ${error.message}`);
    } else {
      setMessage('登録成功！');
      setName('');
      setManagerNumber('');
      fetchUsers();
    }
  };

  // 削除処理
  const handleDelete = async (id) => {
    const { error } = await supabase.from('users').delete().eq('id', id);
    if (error) {
      setMessage(`削除エラー: ${error.message}`);
    } else {
      setMessage('削除成功！');
      fetchUsers();
    }
  };

  return (
    <div>
      <h3>新人登録</h3>
      <div>
        <input
          type="text"
          placeholder="名前"
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <input
          type="text"
          placeholder="管理番号"
          value={managerNumber}
          onChange={e => setManagerNumber(e.target.value)}
        />
        <button onClick={handleRegister}>登録</button>
      </div>
      {message && <p>{message}</p>}
      <hr />
      <h4>登録済みの管理番号一覧</h4>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.manager_number} - {user.name}
            <button onClick={() => handleDelete(user.id)} style={{ marginLeft: '1rem' }}>
              削除
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
