import React, { useState } from 'react';

// ダミーのSupabaseクライアント（実際の環境では本物を使用）
const supabase = {
  from: (table) => ({
    select: (columns) => ({
      eq: (column, value) => ({
        single: () => Promise.resolve({ data: { manager_number: value }, error: null })
      })
    }),
    insert: (data) => Promise.resolve({ error: null })
  })
};

function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginMessage, setLoginMessage] = useState('');
  const [role, setRole] = useState('');
  const [currentStep, setCurrentStep] = useState('');
  const [managerNumber, setManagerNumber] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftTimes, setShiftTimes] = useState([]);
  const [bulkStartHour, setBulkStartHour] = useState('');
  const [bulkStartMin, setBulkStartMin] = useState('');
  const [bulkEndHour, setBulkEndHour] = useState('');
  const [bulkEndMin, setBulkEndMin] = useState('');
  const [selectedDays, setSelectedDays] = useState([]);
  const [managerAuth, setManagerAuth] = useState(false);
  const [managerPass, setManagerPass] = useState('');
  const [managerPassError, setManagerPassError] = useState('');
  const [managerStep, setManagerStep] = useState('');
  const [navigationHistory, setNavigationHistory] = useState([]);

  const resetAllInputs = () => {
    setManagerNumber('');
    setStartDate('');
    setEndDate('');
    setShiftTimes([]);
    setBulkStartHour('');
    setBulkStartMin('');
    setBulkEndHour('');
    setBulkEndMin('');
    setSelectedDays([]);
    setManagerPass('');
    setManagerPassError('');
  };

  const pushToHistory = (state) => {
    setNavigationHistory(prev => [...prev, state]);
  };

  const goBack = () => {
    if (navigationHistory.length === 0) return;

    const previousState = navigationHistory[navigationHistory.length - 1];
    const newHistory = navigationHistory.slice(0, -1);

    setNavigationHistory(newHistory);
    setRole(previousState.role || '');
    setCurrentStep(previousState.currentStep || '');
    setManagerAuth(previousState.managerAuth || false);
    setManagerStep(previousState.managerStep || '');
    setIsLoggedIn(previousState.isLoggedIn !== undefined ? previousState.isLoggedIn : true);

    resetAllInputs();
  };

  const shouldShowBackButton = () => {
    return navigationHistory.length > 0;
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (id === 'kouki' && password === '0306') {
      setIsLoggedIn(true);
      setLoginMessage('');
      setNavigationHistory([]);
    } else {
      setLoginMessage('IDまたはパスワードが違います');
    }
  };

  const selectRole = (selectedRole) => {
    pushToHistory({
      role: '',
      currentStep: '',
      managerAuth: false,
      managerStep: '',
      isLoggedIn: true
    });

    setRole(selectedRole);
    if (selectedRole === 'staff') setCurrentStep('');
  };

  const handleNext = async () => {
    if (!managerNumber.trim()) {
      alert('管理番号を入力してください');
      return;
    }
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日・終了日を入力してください');
      return;
    }

    try {
      const { data, error } = await supabase
        .from('users')
        .select('manager_number')
        .eq('manager_number', managerNumber)
        .single();

      if (error || !data) {
        alert('管理番号が存在しません。');
        return;
      }
    } catch (err) {
      alert('管理番号が存在しません。');
      return;
    }

    pushToHistory({
      role: role,
      currentStep: 'shiftPeriod',
      managerAuth: managerAuth,
      managerStep: managerStep,
      isLoggedIn: true
    });

    const dates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      const yyyy = d.getFullYear();
      const mm = String(d.getMonth() + 1).padStart(2, '0');
      const dd = String(d.getDate()).padStart(2, '0');
      dates.push({ 
        date: `${yyyy}-${mm}-${dd}`, 
        startHour: '', 
        startMin: '', 
        endHour: '', 
        endMin: '', 
        remarks: '' 
      });
      d.setDate(d.getDate() + 1);
    }

    setShiftTimes(dates);
    setCurrentStep('shiftInput');
  };

  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const d = new Date(dateStr);
    return days[d.getDay()];
  };

  const handleTimeChange = (index, field, value) => {
    const updated = [...shiftTimes];
    updated[index][field] = value;
    setShiftTimes(updated);
  };

  const handleBulkApply = () => {
    const updated = shiftTimes.map(item => {
      const day = getWeekday(item.date);
      if (selectedDays.includes('全て') || selectedDays.includes(day)) {
        return { 
          ...item, 
          startHour: bulkStartHour, 
          startMin: bulkStartMin, 
          endHour: bulkEndHour, 
          endMin: bulkEndMin 
        };
      }
      return item;
    });
    setShiftTimes(updated);
  };

  const getColorForDay = (day) => {
    switch (day) {
      case '月': return '#6c5ce7';
      case '火': return '#00b894';
      case '水': return '#fd79a8';
      case '木': return '#e17055';
      case '金': return '#0984e3';
      case '土': return '#fab1a0';
      case '日': return '#d63031';
      case '全て': return '#636e72';
      default: return '#b2bec3';
    }
  };

  const toggleSelectedDay = (day) => {
    setSelectedDays((prev) =>
      prev.includes(day)
        ? prev.filter(d => d !== day)
        : [...prev, day]
    );
  };

  const handleSubmit = async () => {
    try {
      for (const shift of shiftTimes) {
        const startTime = shift.startHour !== '' && shift.startMin !== '' 
          ? `${String(shift.startHour).padStart(2, '0')}:${String(shift.startMin).padStart(2, '0')}` 
          : '';
        const endTime = shift.endHour !== '' && shift.endMin !== '' 
          ? `${String(shift.endHour).padStart(2, '0')}:${String(shift.endMin).padStart(2, '0')}` 
          : '';

        const { error } = await supabase
          .from('shifts')
          .insert([{
            manager_number: managerNumber,
            date: shift.date,
            start_time: startTime,
            end_time: endTime,
            remarks: shift.remarks,
          }]);
        if (error) throw error;
      }

      alert('シフトを保存しました！');
      setCurrentStep('');
      setRole('staff');
      resetAllInputs();
    } catch (error) {
      alert(`保存中にエラーが発生しました: ${error.message}`);
    }
  };

  const BackButton = () => {
    if (!shouldShowBackButton()) return null;

    return (
      <button
        onClick={goBack}
        style={{
          position: 'absolute',
          top: '0.5rem',
          left: '0.5rem',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: '2px solid #45a049',
          borderRadius: '8px',
          width: 'clamp(70px, 15vw, 90px)',
          height: 'clamp(36px, 8vw, 44px)',
          cursor: 'pointer',
          fontSize: 'clamp(12px, 2.5vw, 15px)',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '4px',
          zIndex: 1000,
          boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
          transition: 'all 0.3s ease'
        }}
        title="前のページに戻る"
      >
        ← 戻る
      </button>
    );
  };

  const styles = {
    loginWrapper: {
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: 'clamp(1rem, 4vw, 2rem)',
      backgroundColor: '#f5f5f5',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    },
    loginCard: {
      backgroundColor: 'white',
      padding: 'clamp(1.5rem, 5vw, 2.5rem)',
      borderRadius: 'clamp(12px, 3vw, 20px)',
      boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
      width: '100%',
      maxWidth: '500px',
      position: 'relative'
    },
    h2: {
      fontSize: 'clamp(1.5rem, 5vw, 2rem)',
      marginBottom: 'clamp(1rem, 4vw, 1.5rem)',
      textAlign: 'center',
      color: '#333',
      paddingTop: shouldShowBackButton() ? 'clamp(2rem, 6vw, 3rem)' : '0'
    },
    input: {
      width: '100%',
      padding: 'clamp(0.75rem, 3vw, 1rem)',
      marginBottom: 'clamp(0.75rem, 3vw, 1rem)',
      border: '2px solid #ddd',
      borderRadius: '8px',
      fontSize: 'clamp(14px, 3.5vw, 16px)',
      boxSizing: 'border-box',
      transition: 'border-color 0.3s'
    },
    button: {
      width: '100%',
      padding: 'clamp(0.75rem, 3.5vw, 1rem)',
      border: 'none',
      borderRadius: '8px',
      color: 'white',
      fontSize: 'clamp(14px, 3.5vw, 16px)',
      fontWeight: 'bold',
      cursor: 'pointer',
      marginBottom: 'clamp(0.5rem, 2vw, 0.75rem)',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
    },
    errorMsg: {
      color: '#d32f2f',
      textAlign: 'center',
      marginTop: '1rem',
      fontSize: 'clamp(12px, 3vw, 14px)',
      fontWeight: 'bold'
    },
    label: {
      display: 'block',
      marginBottom: '0.5rem',
      fontWeight: 'bold',
      fontSize: 'clamp(13px, 3vw, 15px)',
      color: '#555'
    }
  };

  if (!isLoggedIn) {
    return (
      <div style={styles.loginWrapper}>
        <form style={styles.loginCard} onSubmit={handleLogin}>
          <h2 style={styles.h2}>ログイン</h2>
          <input 
            style={styles.input}
            type="text" 
            placeholder="ログインID" 
            value={id} 
            onChange={e => setId(e.target.value)} 
            required 
          />
          <input 
            style={styles.input}
            type="password" 
            placeholder="パスワード" 
            value={password} 
            onChange={e => setPassword(e.target.value)} 
            required 
          />
          <button 
            type="submit" 
            style={{...styles.button, backgroundColor: '#2196F3'}}
          >
            ログイン
          </button>
          {loginMessage && <p style={styles.errorMsg}>{loginMessage}</p>}
        </form>
      </div>
    );
  }

  if (!role) {
    return (
      <div style={styles.loginWrapper}>
        <div style={styles.loginCard}>
          <BackButton />
          <h2 style={styles.h2}>役職を選択してください</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 'clamp(0.75rem, 3vw, 1rem)' }}>
            <button 
              onClick={() => selectRole('staff')} 
              style={{...styles.button, backgroundColor: '#1976D2'}}
            >
              アルバイト
            </button>
            <button 
              onClick={() => selectRole('manager')} 
              style={{...styles.button, backgroundColor: '#1565C0'}}
            >
              店長
            </button>
            <button 
              onClick={() => {
                pushToHistory({
                  role: '',
                  currentStep: '',
                  managerAuth: false,
                  managerStep: '',
                  isLoggedIn: true
                });
                setRole('clockin');
              }} 
              style={{...styles.button, backgroundColor: '#00BCD4'}}
            >
              退勤入力
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (role === 'clockin') {
    return (
      <div style={styles.loginWrapper}>
        <div style={styles.loginCard}>
          <BackButton />
          <h2 style={styles.h2}>退勤入力</h2>
          <p style={{ textAlign: 'center', color: '#666', fontSize: 'clamp(13px, 3vw, 15px)' }}>
            この機能は準備中です
          </p>
        </div>
      </div>
    );
  }

  if (role === 'manager' && !managerAuth) {
    return (
      <div style={styles.loginWrapper}>
        <div style={styles.loginCard}>
          <BackButton />
          <h2 style={styles.h2}>店長パスワード</h2>
          <input 
            style={styles.input}
            type="password" 
            placeholder="パスワードを入力" 
            value={managerPass} 
            onChange={(e) => setManagerPass(e.target.value)} 
          />
          <button 
            onClick={() => {
              if (managerPass === '0306') {
                pushToHistory({
                  role: role,
                  currentStep: currentStep,
                  managerAuth: false,
                  managerStep: managerStep,
                  isLoggedIn: true
                });
                setManagerAuth(true);
                setManagerPassError('');
              } else {
                setManagerPassError('パスワードが違います');
              }
            }} 
            style={{...styles.button, backgroundColor: '#1554A5'}}
          >
            認証
          </button>
          {managerPassError && <p style={styles.errorMsg}>{managerPassError}</p>}
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === '') {
    return (
      <div style={styles.loginWrapper}>
        <div style={styles.loginCard}>
          <BackButton />
          <h2 style={styles.h2}>店長メニュー</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 'clamp(0.75rem, 3vw, 1rem)' }}>
            <button 
              onClick={() => {
                pushToHistory({
                  role: role,
                  currentStep: currentStep,
                  managerAuth: managerAuth,
                  managerStep: '',
                  isLoggedIn: true
                });
                setManagerStep('create');
              }} 
              style={{...styles.button, backgroundColor: '#1E88E5'}}
            >
              シフト作成
            </button>
            <button 
              onClick={() => {
                pushToHistory({
                  role: role,
                  currentStep: currentStep,
                  managerAuth: managerAuth,
                  managerStep: '',
                  isLoggedIn: true
                });
                setManagerStep('view');
              }} 
              style={{...styles.button, backgroundColor: '#1976D2'}}
            >
              シフト確認
            </button>
            <button 
              onClick={() => {
                pushToHistory({
                  role: role,
                  currentStep: currentStep,
                  managerAuth: managerAuth,
                  managerStep: '',
                  isLoggedIn: true
                });
                setManagerStep('attendance');
