                <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                  <div style={{ width: '100%' }}>
                    <label style={{ 
                      fontSize: 'clamp(12px, 2.5vw, 13px)', 
                      display: 'block', 
                      marginBottom: '0.25rem' 
                    }}>
                      開始時間
                    </label>
                    <div style={{ display: 'flex', gap: '0.4rem', alignItems: 'center' }}>
                      <select 
                        value={item.startHour} 
                        onChange={e => handleTimeChange(i, 'startHour', e.target.value)}
                        style={{ 
                          flex: 1, 
                          padding: '0.5rem', 
                          fontSize: 'clamp(12px, 3vw, 14px)',
                          minWidth: 0,
                          boxSizing: 'border-box'
                        }}
                      >
                        <option value="">時</option>
                        {[...Array(37)].map((_, h) => (
                          <option key={h} value={h}>{h}</option>
                        ))}
                      </select>
                      <span style={{ fontSize: 'clamp(12px, 3vw, 14px)' }}>:</span>
                      <select 
                        value={item.startMin} 
                        onChange={e => handleTimeChange(i, 'startMin', e.target.value)}
                        style={{ 
                          flex: 1, 
                          padding: '0.5rem', 
                          fontSize: 'clamp(12px, 3vw, 14px)',
                          minWidth: 0,
                          boxSizing: 'border-box'
                        }}
                      >
                        <option value="">分</option>
                        {[...Array(60)].map((_, m) => (
                          <option key={m} value={m}>{String(m).padStart(2, '0')}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div style={{ width: '100%' }}>
                    <label style={{ 
                      fontSize: 'clamp(12px, 2.5vw, 13px)', 
                      display: 'block', 
                      marginBottom: '0.25rem' 
                    }}>
                      終了時間
                    </label>
                    <div style={{ display: 'flex', gap: '0.4rem', alignItems: 'center' }}>
                      <select 
                        value={item.endHour} 
                        onChange={e => handleTimeChange(i, 'endHour', e.target.value)}
                        style={{ 
                          flex: 1, 
                          padding: '0.5rem', 
                          fontSize: 'clamp(12px, 3vw, 14px)',
                          minWidth: 0,
                          boxSizing: 'border-box'
                        }}
                      >
                        <option value="">時</option>
                        {[...Array(37)].map((_, h) => (
                          <option key={h} value={h}>{h}</option>
                        ))}
                      </select>
                      <span style={{ fontSize: 'clamp(12px, 3vw, 14px)' }}>:</span>
                      <select 
                        value={item.endMin} 
                        onChange={e => handleTimeChange(i, 'endMin', e.target.value)}
                        style={{ 
                          flex: 1, 
                          padding: '0.5rem', 
                          fontSize: 'clamp(12px, 3vw, 14px)',
                          minWidth: 0,
                          boxSizing: 'border-box'
                        }}
                      >
                        <option value="">分</option>
                        {[...Array(60)].map((_, m) => (
                          <option key={m} value={m}>{String(m).padStart(2, '0')}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                </div>
                <div>
                  <label style={{ 
                    fontSize: 'clamp(12px, 2.5vw, 13px)', 
                    display: 'block', 
                    marginBottom: '0.25rem', 
                    fontWeight: 'bold' 
                  }}>
                    備考
                  </label>
                  <textarea 
                    value={item.remarks} 
                    onChange={e => handleTimeChange(i, 'remarks', e.target.value)}
                    placeholder="例：朝遅刻予定、早退など"
                    style={{ 
                      width: '100%', 
                      padding: '0.5rem', 
                      borderRadius: '4px', 
                      border: '2px solid #FF9800',
                      fontSize: 'clamp(12px, 3vw, 14px)',
                      minHeight: '60px',
                      fontFamily: 'inherit',
                      backgroundColor: '#FFF9E6',
                      boxSizing: 'border-box',
                      resize: 'vertical'
                    }}
                  />
                </div>
              </div>
            ))}
          </div>
          <button 
            onClick={handleSubmit} 
            style={{ 
              backgroundColor: '#1976D2', 
              width: '100%', 
              fontSize: 'clamp(14px, 3.5vw, 16px)', 
              padding: '0.75rem',
              border: 'none',
              borderRadius: '6px',
              color: 'white',
              fontWeight: 'bold',
              cursor: 'pointer'
            }}
          >
            送信
          </button>
        </div>
      </div>
    );
  }

  if (role === 'staff') {
    return (
      <div className="login-wrapper">
        <HelpModal isOpen={showHelp} onClose={() => setShowHelp(false)} content={getHelpContent(currentHelpPage)} />
        <div className="login-card" style={{ position: 'relative' }}>
          <BackButton />
          <HelpButton page="staffMenu" />
          <h2>アルバイトメニュー</h2>
          <div className="button-row" style={{ flexDirection: 'column', gap: '1rem' }}>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftPeriod');
            }} style={{ backgroundColor: '#1E88E5' }}>新規提出</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftEdit');
            }} style={{ backgroundColor: '#1976D2' }}>シフト変更</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftView');
            }} style={{ backgroundColor: '#1565C0' }}>シフト確認</button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('workHours');
            }} style={{ backgroundColor: '#0D47A1' }}>就労時間</button>
            <button onClick={() => {
              window.open('https://docs.google.com/forms/d/e/1FAIpQLSci0UYQ7BKfXjhVj8x3WBR5ncFxxCo_lsV11kY5TaI15wlKSQ/viewform?usp=header', '_blank');
            }} style={{ backgroundColor: '#1554A5' }}>お問い合わせ</button>
          </div>
          <button onClick={() => {
            setRole('');
            setId('');
            setPassword('');
            setIsLoggedIn(false);
            setCurrentStep('');
            resetAllInputs();
            setNavigationHistory([]);
          }} style={{ backgroundColor: '#FF5722' }}>ログアウト</button>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
