import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginMessage, setLoginMessage] = useState('');
  const [role, setRole] = useState('');
  const [currentStep, setCurrentStep] = useState('');
  const [managerNumber, setManagerNumber] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftTimes, setShiftTimes] = useState([]);
  const [bulkStartHour, setBulkStartHour] = useState('');
  const [bulkStartMin, setBulkStartMin] = useState('');
  const [bulkEndHour, setBulkEndHour] = useState('');
  const [bulkEndMin, setBulkEndMin] = useState('');
  const [selectedDays, setSelectedDays] = useState([]);
  const [managerAuth, setManagerAuth] = useState(false);
  const [managerPass, setManagerPass] = useState('');
  const [managerPassError, setManagerPassError] = useState('');
  const [managerStep, setManagerStep] = useState('');
  const [navigationHistory, setNavigationHistory] = useState([]);

  const resetAllInputs = () => {
    setManagerNumber('');
    setStartDate('');
    setEndDate('');
    setShiftTimes([]);
    setBulkStartHour('');
    setBulkStartMin('');
    setBulkEndHour('');
    setBulkEndMin('');
    setSelectedDays([]);
    setManagerPass('');
    setManagerPassError('');
  };

  const pushToHistory = (state) => {
    setNavigationHistory(prev => [...prev, state]);
  };

  const goBack = () => {
    if (navigationHistory.length === 0) return;

    const previousState = navigationHistory[navigationHistory.length - 1];
    const newHistory = navigationHistory.slice(0, -1);

    setNavigationHistory(newHistory);
    setRole(previousState.role || '');
    setCurrentStep(previousState.currentStep || '');
    setManagerAuth(previousState.managerAuth || false);
    setManagerStep(previousState.managerStep || '');
    setIsLoggedIn(previousState.isLoggedIn !== undefined ? previousState.isLoggedIn : true);

    resetAllInputs();
  };

  const shouldShowBackButton = () => {
    return navigationHistory.length > 0;
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (id === 'kouki' && password === '0306') {
      setIsLoggedIn(true);
      setLoginMessage('');
      setNavigationHistory([]);
    } else {
      setLoginMessage('IDまたはパスワードが違います');
    }
  };

  const selectRole = (selectedRole) => {
    pushToHistory({
      role: '',
      currentStep: '',
      managerAuth: false,
      managerStep: '',
      isLoggedIn: true
    });

    setRole(selectedRole);
    if (selectedRole === 'staff') setCurrentStep('');
  };

  const handleNext = async () => {
    if (!managerNumber.trim()) {
      alert('管理番号を入力してください');
      return;
    }
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日・終了日を入力してください');
      return;
    }

    try {
      const { data, error } = await supabase
        .from('users')
        .select('manager_number')
        .eq('manager_number', managerNumber)
        .single();

      if (error || !data) {
        alert('管理番号が存在しません。');
        return;
      }
    } catch (err) {
      alert('管理番号が存在しません。');
      return;
    }

    pushToHistory({
      role: role,
      currentStep: 'shiftPeriod',
      managerAuth: managerAuth,
      managerStep: managerStep,
      isLoggedIn: true
    });

    const dates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      const yyyy = d.getFullYear();
      const mm = String(d.getMonth() + 1).padStart(2, '0');
      const dd = String(d.getDate()).padStart(2, '0');
      dates.push({ 
        date: `${yyyy}-${mm}-${dd}`, 
        startHour: '', 
        startMin: '', 
        endHour: '', 
        endMin: '', 
        remarks: '' 
      });
      d.setDate(d.getDate() + 1);
    }

    setShiftTimes(dates);
    setCurrentStep('shiftInput');
  };

  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const d = new Date(dateStr);
    return days[d.getDay()];
  };

  const handleTimeChange = (index, field, value) => {
    const updated = [...shiftTimes];
    updated[index][field] = value;
    setShiftTimes(updated);
  };

  const handleBulkApply = () => {
    const updated = shiftTimes.map(item => {
      const day = getWeekday(item.date);
      if (selectedDays.includes('全て') || selectedDays.includes(day)) {
        return { 
          ...item, 
          startHour: bulkStartHour, 
          startMin: bulkStartMin, 
          endHour: bulkEndHour, 
          endMin: bulkEndMin 
        };
      }
      return item;
    });
    setShiftTimes(updated);
  };

  const getColorForDay = (day) => {
    switch (day) {
      case '月': return '#6c5ce7';
      case '火': return '#00b894';
      case '水': return '#fd79a8';
      case '木': return '#e17055';
      case '金': return '#0984e3';
      case '土': return '#fab1a0';
      case '日': return '#d63031';
      case '全て': return '#636e72';
      default: return '#b2bec3';
    }
  };

  const toggleSelectedDay = (day) => {
    setSelectedDays((prev) =>
      prev.includes(day)
        ? prev.filter(d => d !== day)
        : [...prev, day]
    );
  };

  const handleSubmit = async () => {
    try {
      for (const shift of shiftTimes) {
        const startTime = shift.startHour !== '' && shift.startMin !== '' 
          ? `${String(shift.startHour).padStart(2, '0')}:${String(shift.startMin).padStart(2, '0')}` 
          : '';
        const endTime = shift.endHour !== '' && shift.endMin !== '' 
          ? `${String(shift.endHour).padStart(2, '0')}:${String(shift.endMin).padStart(2, '0')}` 
          : '';

        const { error } = await supabase
          .from('shifts')
          .insert([{
            manager_number: managerNumber,
            date: shift.date,
            start_time: startTime,
            end_time: endTime,
            remarks: shift.remarks,
          }]);
        if (error) throw error;
      }

      alert('シフトを保存しました！');
      setCurrentStep('');
      setRole('staff');
      resetAllInputs();
    } catch (error) {
      alert(`保存中にエラーが発生しました: ${error.message}`);
    }
  };

  const BackButton = () => {
    if (!shouldShowBackButton()) return null;

    return (
      <button
        onClick={goBack}
        style={{
          position: 'fixed',
          top: '0.5rem',
          left: '0.5rem',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: '2px solid #45a049',
          borderRadius: '8px',
          minWidth: '70px',
          height: '44px',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '4px',
          zIndex: 1000,
          boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
          WebkitTapHighlightColor: 'transparent',
          touchAction: 'manipulation'
        }}
        title="前のページに戻る"
      >
        ← 戻る
      </button>
    );
  };

  const buttonStyle = {
    width: '100%',
    minHeight: '50px',
    fontSize: '16px',
    fontWeight: 'bold',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    color: 'white',
    WebkitTapHighlightColor: 'transparent',
    touchAction: 'manipulation',
    padding: '12px 20px'
  };

  const inputStyle = {
    width: '100%',
    padding: '12px',
    fontSize: '16px',
    borderRadius: '6px',
    border: '2px solid #ddd',
    boxSizing: 'border-box',
    WebkitAppearance: 'none',
    touchAction: 'manipulation'
  };

  const selectStyle = {
    width: '100%',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '6px',
    border: '2px solid #ddd',
    backgroundColor: 'white',
    WebkitAppearance: 'none',
    appearance: 'none',
    touchAction: 'manipulation'
  };

  if (!isLoggedIn) {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '1rem',
        boxSizing: 'border-box'
      }}>
        <form onSubmit={handleLogin} style={{
          width: '100%',
          maxWidth: '400px',
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
          boxSizing: 'border-box'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '24px' }}>ログイン</h2>
          <input 
            type="text" 
            placeholder="ログインID" 
            value={id} 
            onChange={e => setId(e.target.value)} 
            required 
            style={{ ...inputStyle, marginBottom: '1rem' }}
          />
          <input 
            type="password" 
            placeholder="パスワード" 
            value={password} 
            onChange={e => setPassword(e.target.value)} 
            required 
            style={{ ...inputStyle, marginBottom: '1rem' }}
          />
          <button type="submit" style={{ ...buttonStyle, backgroundColor: '#2196F3' }}>
            ログイン
          </button>
          {loginMessage && (
            <p style={{ color: '#f44336', textAlign: 'center', marginTop: '1rem', fontSize: '14px' }}>
              {loginMessage}
            </p>
          )}
        </form>
      </div>
    );
  }

  if (!role) {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '1rem',
        boxSizing: 'border-box',
        position: 'relative'
      }}>
        <BackButton />
        <div style={{
          width: '100%',
          maxWidth: '400px',
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
          boxSizing: 'border-box',
          marginTop: '60px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '24px' }}>役職を選択してください</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            <button onClick={() => selectRole('staff')} style={{ ...buttonStyle, backgroundColor: '#1976D2' }}>
              アルバイト
            </button>
            <button onClick={() => selectRole('manager')} style={{ ...buttonStyle, backgroundColor: '#1565C0' }}>
              店長
            </button>
            <button onClick={() => {
              pushToHistory({
                role: '',
                currentStep: '',
                managerAuth: false,
                managerStep: '',
                isLoggedIn: true
              });
              setRole('clockin');
            }} style={{ ...buttonStyle, backgroundColor: '#00BCD4' }}>
              退勤入力
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (role === 'clockin') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>退勤入力コンポーネント（ClockInInput）</p>
        </div>
      </div>
    );
  }

  if (role === 'manager' && !managerAuth) {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '1rem',
        boxSizing: 'border-box',
        position: 'relative'
      }}>
        <BackButton />
        <div style={{
          width: '100%',
          maxWidth: '400px',
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
          boxSizing: 'border-box',
          marginTop: '60px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '24px' }}>店長パスワード</h2>
          <input 
            type="password" 
            placeholder="パスワードを入力" 
            value={managerPass} 
            onChange={(e) => setManagerPass(e.target.value)} 
            style={{ ...inputStyle, marginBottom: '1rem' }}
          />
          <button onClick={() => {
            if (managerPass === '0306') {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: false,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setManagerAuth(true);
              setManagerPassError('');
            } else {
              setManagerPassError('パスワードが違います');
            }
          }} style={{ ...buttonStyle, backgroundColor: '#1554A5' }}>
            認証
          </button>
          {managerPassError && (
            <p style={{ color: '#f44336', textAlign: 'center', marginTop: '1rem', fontSize: '14px' }}>
              {managerPassError}
            </p>
          )}
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === '') {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '1rem',
        boxSizing: 'border-box',
        position: 'relative'
      }}>
        <BackButton />
        <div style={{
          width: '100%',
          maxWidth: '400px',
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
          boxSizing: 'border-box',
          marginTop: '60px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '24px' }}>店長メニュー</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', marginBottom: '1rem' }}>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('create');
            }} style={{ ...buttonStyle, backgroundColor: '#1E88E5' }}>
              シフト作成
            </button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('view');
            }} style={{ ...buttonStyle, backgroundColor: '#1976D2' }}>
              シフト確認
            </button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('attendance');
            }} style={{ ...buttonStyle, backgroundColor: '#0D47A1' }}>
              退勤管理
            </button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: currentStep,
                managerAuth: managerAuth,
                managerStep: '',
                isLoggedIn: true
              });
              setManagerStep('register');
            }} style={{ ...buttonStyle, backgroundColor: '#1554A5' }}>
              新人登録
            </button>
            <button onClick={() => {
              window.open('https://docs.google.com/forms/d/e/1FAIpQLSci0UYQ7BKfXjhVj8x3WBR5ncFxxCo_lsV11kY5TaI15wlKSQ/viewform?usp=header', '_blank');
            }} style={{ ...buttonStyle, backgroundColor: '#1565C0' }}>
              お問い合わせ
            </button>
          </div>
          <button onClick={() => {
            setRole('');
            setId('');
            setPassword('');
            setIsLoggedIn(false);
            setManagerAuth(false);
            resetAllInputs();
            setNavigationHistory([]);
          }} style={{ ...buttonStyle, backgroundColor: '#FF5722' }}>
            ログアウト
          </button>
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'register') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>新人登録コンポーネント（RegisterUser）</p>
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'create') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>シフト作成コンポーネント（ManagerCreate）</p>
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'view') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>シフト確認コンポーネント（ManagerShiftView）</p>
        </div>
      </div>
    );
  }

  if (role === 'manager' && managerAuth && managerStep === 'attendance') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>退勤管理コンポーネント（ManagerAttendance）</p>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftView') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>シフト確認コンポーネント（StaffShiftView）</p>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftEdit') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>シフト変更コンポーネント（StaffShiftEdit）</p>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'workHours') {
    return (
      <div style={{ position: 'relative', minHeight: '100vh', padding: '1rem', boxSizing: 'border-box' }}>
        <BackButton />
        <div style={{ paddingTop: '60px' }}>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>就労時間コンポーネント（StaffWorkHours）</p>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftPeriod') {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '1rem',
        boxSizing: 'border-box',
        position: 'relative'
      }}>
        <BackButton />
        <div style={{
          width: '100%',
          maxWidth: '400px',
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
          boxSizing: 'border-box',
          marginTop: '60px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '24px' }}>新規提出</h2>
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold', fontSize: '14px' }}>管理番号:</label>
          <input 
            type="text" 
            value={managerNumber} 
            onChange={e => setManagerNumber(e.target.value)} 
            style={{ ...inputStyle, marginBottom: '1rem' }}
          />
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold', fontSize: '14px' }}>開始日:</label>
          <input 
            type="date" 
            value={startDate} 
            onChange={e => setStartDate(e.target.value)} 
            style={{ ...inputStyle, marginBottom: '1rem' }}
          />
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold', fontSize: '14px' }}>終了日:</label>
          <input 
            type="date" 
            value={endDate} 
            onChange={e => setEndDate(e.target.value)} 
            style={{ ...inputStyle, marginBottom: '1rem' }}
          />
          <button onClick={handleNext} style={{ ...buttonStyle, backgroundColor: '#1976D2' }}>
            次へ
          </button>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftInput') {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '1rem',
        paddingTop: '70px',
        boxSizing: 'border-box',
        position: 'relative'
      }}>
        <BackButton />
        <div style={{
          width: '100%',
          maxWidth: '600px',
          margin: '0 auto',
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '1.5rem',
          boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
          boxSizing: 'border-box'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '1rem', fontSize: '22px' }}>シフト入力</h2>
          <p style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '14px' }}>
            管理番号: <strong>{managerNumber}</strong>
          </p>

          <div style={{ 
            display: 'flex', 
            overflowX: 'auto', 
            gap: '0.5rem', 
            paddingBottom: '1rem',
            WebkitOverflowScrolling: 'touch'
          }}>
            {['全て', '月', '火', '水', '木', '金', '土', '日'].map((day) => (
              <button
                key={day}
                onClick={() => toggleSelectedDay(day)}
                style={{
                  backgroundColor: selectedDays.includes(day) ? '#95a5a6' : getColorForDay(day),
                  color: 'white',
                  padding: '10px 16px',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 'bold',
                  minWidth: '50px',
                  flexShrink: 0,
                  WebkitTapHighlightColor: 'transparent',
                  touchAction: 'manipulation'
                }}>
                {day}
              </button>
            ))}
          </div>

          {selectedDays.length > 0 && (
            <div style={{ 
              marginBottom: '1rem', 
              padding: '1rem', 
              backgroundColor: '#e3f2fd', 
              borderRadius: '8px',
              border: '2px solid #2196F3'
            }}>
              <div style={{ fontWeight: 'bold', marginBottom: '0.5rem', color: '#1976D2', fontSize: '14px' }}>
                一括設定
              </div>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
                <div>
                  <label style={{ fontSize: '13px', display: 'block', marginBottom: '0.25rem', fontWeight: 'bold' }}>
                    開始時間
                  </label>
                  <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
                    <select 
                      value={bulkStartHour} 
                      onChange={e => setBulkStartHour(e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">時</option>
                      {[...Array(37)].map((_, h) => (
                        <option key={h} value={h}>{h}</option>
                      ))}
                    </select>
                    <span style={{ fontSize: '14px', fontWeight: 'bold' }}>:</span>
                    <select 
                      value={bulkStartMin} 
                      onChange={e => setBulkStartMin(e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">分</option>
                      {[...Array(60)].map((_, m) => (
                        <option key={m} value={m}>{String(m).padStart(2, '0')}</option>
                      ))}
                    </select>
                  </div>
                </div>
                <div>
                  <label style={{ fontSize: '13px', display: 'block', marginBottom: '0.25rem', fontWeight: 'bold' }}>
                    終了時間
                  </label>
                  <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
                    <select 
                      value={bulkEndHour} 
                      onChange={e => setBulkEndHour(e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">時</option>
                      {[...Array(37)].map((_, h) => (
                        <option key={h} value={h}>{h}</option>
                      ))}
                    </select>
                    <span style={{ fontSize: '14px', fontWeight: 'bold' }}>:</span>
                    <select 
                      value={bulkEndMin} 
                      onChange={e => setBulkEndMin(e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">分</option>
                      {[...Array(60)].map((_, m) => (
                        <option key={m} value={m}>{String(m).padStart(2, '0')}</option>
                      ))}
                    </select>
                  </div>
                </div>
                <button 
                  onClick={handleBulkApply} 
                  style={{ 
                    backgroundColor: '#2196F3', 
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    padding: '12px',
                    cursor: 'pointer',
                    fontSize: '14px',
                    fontWeight: 'bold',
                    WebkitTapHighlightColor: 'transparent',
                    touchAction: 'manipulation'
                  }}
                >
                  一括適用
                </button>
              </div>
            </div>
          )}

          <div style={{ 
            maxHeight: '60vh', 
            overflowY: 'auto', 
            marginBottom: '1rem',
            WebkitOverflowScrolling: 'touch'
          }}>
            {shiftTimes.map((item, i) => (
              <div key={item.date} style={{ 
                display: 'flex', 
                flexDirection: 'column',
                gap: '0.75rem', 
                marginBottom: '1.5rem', 
                padding: '1rem',
                backgroundColor: '#f5f5f5',
                borderRadius: '8px',
                border: '1px solid #ddd'
              }}>
                <div style={{ fontWeight: 'bold', fontSize: '16px', color: '#333' }}>
                  {item.date}（{getWeekday(item.date)}）
                </div>

                <div>
                  <label style={{ fontSize: '13px', display: 'block', marginBottom: '0.25rem', fontWeight: 'bold' }}>
                    開始時間
                  </label>
                  <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
                    <select 
                      value={item.startHour} 
                      onChange={e => handleTimeChange(i, 'startHour', e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">時</option>
                      {[...Array(37)].map((_, h) => (
                        <option key={h} value={h}>{h}</option>
                      ))}
                    </select>
                    <span style={{ fontSize: '14px', fontWeight: 'bold' }}>:</span>
                    <select 
                      value={item.startMin} 
                      onChange={e => handleTimeChange(i, 'startMin', e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">分</option>
                      {[...Array(60)].map((_, m) => (
                        <option key={m} value={m}>{String(m).padStart(2, '0')}</option>
                      ))}
                    </select>
                  </div>
                </div>

                <div>
                  <label style={{ fontSize: '13px', display: 'block', marginBottom: '0.25rem', fontWeight: 'bold' }}>
                    終了時間
                  </label>
                  <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'center' }}>
                    <select 
                      value={item.endHour} 
                      onChange={e => handleTimeChange(i, 'endHour', e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">時</option>
                      {[...Array(37)].map((_, h) => (
                        <option key={h} value={h}>{h}</option>
                      ))}
                    </select>
                    <span style={{ fontSize: '14px', fontWeight: 'bold' }}>:</span>
                    <select 
                      value={item.endMin} 
                      onChange={e => handleTimeChange(i, 'endMin', e.target.value)}
                      style={{ ...selectStyle, flex: 1 }}
                    >
                      <option value="">分</option>
                      {[...Array(60)].map((_, m) => (
                        <option key={m} value={m}>{String(m).padStart(2, '0')}</option>
                      ))}
                    </select>
                  </div>
                </div>

                <div>
                  <label style={{ fontSize: '13px', display: 'block', marginBottom: '0.25rem', fontWeight: 'bold' }}>
                    備考
                  </label>
                  <textarea 
                    value={item.remarks} 
                    onChange={e => handleTimeChange(i, 'remarks', e.target.value)}
                    placeholder="例：朝遅刻予定、早退など"
                    style={{ 
                      width: '100%', 
                      padding: '10px', 
                      borderRadius: '6px', 
                      border: '2px solid #FF9800',
                      fontSize: '14px',
                      minHeight: '70px',
                      fontFamily: 'inherit',
                      backgroundColor: '#FFF9E6',
                      boxSizing: 'border-box',
                      resize: 'vertical',
                      WebkitAppearance: 'none'
                    }}
                  />
                </div>
              </div>
            ))}
          </div>

          <button 
            onClick={handleSubmit} 
            style={{ 
              ...buttonStyle, 
              backgroundColor: '#1976D2',
              fontSize: '16px',
              padding: '14px'
            }}
          >
            送信
          </button>
        </div>
      </div>
    );
  }

  if (role === 'staff') {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '1rem',
        boxSizing: 'border-box',
        position: 'relative'
      }}>
        <BackButton />
        <div style={{
          width: '100%',
          maxWidth: '400px',
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
          boxSizing: 'border-box',
          marginTop: '60px'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '24px' }}>アルバイトメニュー</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', marginBottom: '1rem' }}>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftPeriod');
            }} style={{ ...buttonStyle, backgroundColor: '#1E88E5' }}>
              新規提出
            </button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftView');
            }} style={{ ...buttonStyle, backgroundColor: '#1976D2' }}>
              シフト確認
            </button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('shiftEdit');
            }} style={{ ...buttonStyle, backgroundColor: '#1565C0' }}>
              シフト変更
            </button>
            <button onClick={() => {
              pushToHistory({
                role: role,
                currentStep: '',
                managerAuth: managerAuth,
                managerStep: managerStep,
                isLoggedIn: true
              });
              setCurrentStep('workHours');
            }} style={{ ...buttonStyle, backgroundColor: '#0D47A1' }}>
              就労時間
            </button>
            <button onClick={() => {
              window.open('https://docs.google.com/forms/d/e/1FAIpQLSci0UYQ7BKfXjhVj8x3WBR5ncFxxCo_lsV11kY5TaI15wlKSQ/viewform?usp=header', '_blank');
            }} style={{ ...buttonStyle, backgroundColor: '#1554A5' }}>
              お問い合わせ
            </button>
          </div>
          <button onClick={() => {
            setRole('');
            setId('');
            setPassword('');
            setIsLoggedIn(false);
            setCurrentStep('');
            resetAllInputs();
            setNavigationHistory([]);
          }} style={{ ...buttonStyle, backgroundColor: '#FF5722' }}>
            ログアウト
          </button>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
