import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function ManagerCreate() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [dates, setDates] = useState([]);
  const [showTable, setShowTable] = useState(false); // 表示切り替えフラグ

  const fetchShiftData = async () => {
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日と終了日を入力してください');
      return;
    }

    const { data: shifts, error: shiftError } = await supabase
      .from('shifts')
      .select('*')
      .gte('date', startDate)
      .lte('date', endDate);

    if (shiftError) {
      console.error(shiftError);
      alert('シフトデータの取得に失敗しました');
      return;
    }

    const { data: users, error: userError } = await supabase
      .from('users')
      .select('*');

    if (userError) {
      console.error(userError);
      alert('ユーザー情報の取得に失敗しました');
      return;
    }

    const userMapTemp = {};
    users.forEach(user => {
      userMapTemp[String(user.manager_number)]  = user.name;
    });

    // 全日付を生成
    const allDates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      allDates.push(d.toISOString().split('T')[0]);
      d.setDate(d.getDate() + 1);
    }

    setDates(allDates);
    setUserMap(userMapTemp);
    setShiftData(shifts);

    setShowTable(true); // シフト表表示へ切り替え
  };

  const groupedByUser = {};
  shiftData.forEach(shift => {
    const name = userMap[shift.manager_number] || '(不明)';
    if (!groupedByUser[name]) groupedByUser[name] = {};
    groupedByUser[name][shift.date] = `${shift.start_time || ''} ~ ${shift.end_time || ''}`;
  });

  if (!showTable) {
    // 入力画面
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>シフト作成</h2>

          <label>開始日:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>終了日:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />

          <div style={{ marginTop: '1rem' }}>
            <button onClick={fetchShiftData}>次へ</button>
          </div>
        </div>
      </div>
    );
  }

  // シフト表表示画面
  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ overflowX: 'auto' }}>
        <h2>シフト表</h2>
        <table style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>名前</th>
              {dates.map(date => (
                <th key={date} style={{ border: '1px solid #ccc', padding: '0.5rem' }}>{date}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.entries(groupedByUser).map(([name, shifts]) => (
              <tr key={name}>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>{name}</td>
                {dates.map(date => (
                  <td key={date} style={{ border: '1px solid #ccc', padding: '0.5rem' }}>
                    {shifts[date] || ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ManagerCreate;
