import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function StaffShiftView({ onBack }) {
  const [selectedDate, setSelectedDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [userMap, setUserMap] = useState({});
  const [showCalendar, setShowCalendar] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [availableDates, setAvailableDates] = useState([]);

  // 初期化時に利用可能な日付を取得
  useEffect(() => {
    fetchAvailableDates();
    fetchUsers();
  }, []);

  // 利用可能な日付（シフトが作成されている日付）を取得
  const fetchAvailableDates = async () => {
    try {
      const { data, error } = await supabase
        .from('final_shifts')
        .select('date')
        .order('date');

      if (error) {
        console.error('日付取得エラー:', error);
        return;
      }

      const uniqueDates = [...new Set(data.map(item => item.date))];
      setAvailableDates(uniqueDates);
      
      // 最新の日付を初期値として設定
      if (uniqueDates.length > 0) {
        setSelectedDate(uniqueDates[uniqueDates.length - 1]);
      }
    } catch (error) {
      console.error('予期しないエラー:', error);
    }
  };

  // ユーザー情報を取得
  const fetchUsers = async () => {
    try {
      const { data: users, error } = await supabase
        .from('users')
        .select('*');

      if (error) {
        console.error('ユーザー取得エラー:', error);
        return;
      }

      const userMapTemp = {};
      users.forEach(user => {
        userMapTemp[String(user.manager_number)] = user.name;
      });
      setUserMap(userMapTemp);
    } catch (error) {
      console.error('予期しないエラー:', error);
    }
  };

  // 指定日のシフトデータを取得
  const fetchShiftData = async (date) => {
    if (!date) return;

    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('final_shifts')
        .select('*')
        .eq('date', date)
        .order('manager_number');

      if (error) {
        console.error('シフトデータ取得エラー:', error);
        alert('シフトデータの取得に失敗しました');
        return;
      }

      setShiftData(data || []);
    } catch (error) {
      console.error('予期しないエラー:', error);
      alert('エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  // 日付選択時の処理
  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setShowCalendar(false);
    fetchShiftData(date);
  };

  // カレンダーの日付生成
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());
    
    const days = [];
    const currentDate = new Date(startDate);
    
    for (let i = 0; i < 42; i++) {
      const dateStr = currentDate.toISOString().split('T')[0];
      const isCurrentMonth = currentDate.getMonth() === month;
      const hasShift = availableDates.includes(dateStr);
      const isSelected = dateStr === selectedDate;
      
      days.push({
        date: new Date(currentDate),
        dateStr: dateStr,
        day: currentDate.getDate(),
        isCurrentMonth,
        hasShift,
        isSelected
      });
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return days;
  };

  // 月の変更
  const changeMonth = (delta) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + delta);
    setCurrentMonth(newMonth);
  };

  // 時間のフォーマット
  const formatTime = (timeStr) => {
    if (!timeStr) return '';
    return timeStr.slice(0, 5); // HH:MM形式に変換
  };

  // 曜日の取得
  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  const calendarDays = generateCalendarDays();
  const weekdays = ['日', '月', '火', '水', '木', '金', '土'];

  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '600px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
        <h2>シフト確認</h2>
        
        {/* 日付選択セクション */}
        <div style={{ marginBottom: '1.5rem' }}>
          <label>確認したい日付を選択:</label>
          <div style={{ marginTop: '0.5rem' }}>
            <button 
              onClick={() => setShowCalendar(!showCalendar)}
              style={{ 
                padding: '0.5rem 1rem', 
                fontSize: '1rem',
                backgroundColor: '#2196F3',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                minWidth: '150px'
              }}
            >
              {selectedDate ? `${selectedDate} (${getWeekday(selectedDate)})` : '日付を選択'}
            </button>
          </div>

          {/* カレンダー */}
          {showCalendar && (
            <div style={{ 
              marginTop: '1rem', 
              border: '1px solid #ddd', 
              borderRadius: '8px',
              padding: '1rem',
              backgroundColor: '#f9f9f9'
            }}>
              {/* 月変更ヘッダー */}
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                marginBottom: '1rem'
              }}>
                <button onClick={() => changeMonth(-1)} style={{ 
                  backgroundColor: '#607D8B', 
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  padding: '0.5rem',
                  cursor: 'pointer'
                }}>
                  ◀
                </button>
                <h3 style={{ margin: 0 }}>
                  {currentMonth.getFullYear()}年 {currentMonth.getMonth() + 1}月
                </h3>
                <button onClick={() => changeMonth(1)} style={{ 
                  backgroundColor: '#607D8B', 
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  padding: '0.5rem',
                  cursor: 'pointer'
                }}>
                  ▶
                </button>
              </div>

              {/* 曜日ヘッダー */}
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(7, 1fr)', 
                gap: '2px',
                marginBottom: '0.5rem'
              }}>
                {weekdays.map(day => (
                  <div key={day} style={{ 
                    textAlign: 'center', 
                    fontWeight: 'bold',
                    padding: '0.5rem',
                    backgroundColor: '#e0e0e0'
                  }}>
                    {day}
                  </div>
                ))}
              </div>

              {/* カレンダー日付 */}
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(7, 1fr)', 
                gap: '2px'
              }}>
                {calendarDays.map((dayInfo, index) => (
                  <button
                    key={index}
                    onClick={() => dayInfo.hasShift && handleDateSelect(dayInfo.dateStr)}
                    disabled={!dayInfo.hasShift}
                    style={{
                      padding: '0.5rem',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: dayInfo.hasShift ? 'pointer' : 'not-allowed',
                      backgroundColor: dayInfo.isSelected ? '#4CAF50' : 
                                     dayInfo.hasShift ? '#E3F2FD' : 
                                     dayInfo.isCurrentMonth ? 'white' : '#f0f0f0',
                      color: dayInfo.isSelected ? 'white' : 
                             !dayInfo.hasShift ? '#999' :
                             dayInfo.isCurrentMonth ? 'black' : '#666',
                      fontWeight: dayInfo.hasShift ? 'bold' : 'normal',
                      opacity: dayInfo.isCurrentMonth ? 1 : 0.5
                    }}
                  >
                    {dayInfo.day}
                  </button>
                ))}
              </div>
              
              <div style={{ 
                marginTop: '0.5rem', 
                fontSize: '0.8rem', 
                color: '#666',
                textAlign: 'center'
              }}>
                青色: シフトあり | 灰色: シフトなし
              </div>
            </div>
          )}
        </div>

        {/* シフトデータ表示 */}
        {selectedDate && (
          <div>
            <h3>{selectedDate} ({getWeekday(selectedDate)}) のシフト</h3>
            
            {loading ? (
              <div style={{ textAlign: 'center', padding: '2rem' }}>
                読み込み中...
              </div>
            ) : shiftData.length === 0 ? (
              <div style={{ 
                textAlign: 'center', 
                padding: '2rem',
                color: '#666',
                backgroundColor: '#f9f9f9',
                borderRadius: '8px'
              }}>
                この日のシフトはありません
              </div>
            ) : (
              <div style={{ 
                border: '1px solid #ddd',
                borderRadius: '8px',
                overflow: 'hidden'
              }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr style={{ backgroundColor: '#f5f5f5' }}>
                      <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>
                        名前
                      </th>
                      <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                        勤務時間
                      </th>
                      <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                        状態
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {shiftData.map((shift, index) => (
                      <tr key={shift.manager_number} style={{ 
                        backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                      }}>
                        <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                          <strong>{userMap[shift.manager_number] || '不明'}</strong>
                          <div style={{ fontSize: '0.8rem', color: '#666' }}>
                            ({shift.manager_number})
                          </div>
                        </td>
                        <td style={{ 
                          padding: '0.75rem', 
                          textAlign: 'center',
                          borderBottom: '1px solid #eee'
                        }}>
                          {shift.is_off ? (
                            <span style={{ color: '#999', fontStyle: 'italic' }}>休み</span>
                          ) : (
                            <span style={{ fontWeight: 'bold' }}>
                              {formatTime(shift.start_time)} - {formatTime(shift.end_time)}
                            </span>
                          )}
                        </td>
                        <td style={{ 
                          padding: '0.75rem', 
                          textAlign: 'center',
                          borderBottom: '1px solid #eee'
                        }}>
                          <span style={{ 
                            padding: '0.25rem 0.5rem',
                            borderRadius: '12px',
                            fontSize: '0.8rem',
                            backgroundColor: shift.is_off ? '#f44336' : '#4CAF50',
                            color: 'white'
                          }}>
                            {shift.is_off ? '休み' : '出勤'}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        )}

        {/* 戻るボタン */}
        <div style={{ marginTop: '2rem', textAlign: 'center' }}>
          <button onClick={onBack} style={{ 
            backgroundColor: '#607D8B',
            color: 'white',
            padding: '0.75rem 2rem',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}>
            戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default StaffShiftView;
