import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function ManagerCreate() {
  const [selectedDate, setSelectedDate] = useState('');
  const [users, setUsers] = useState([]);
  const [shifts, setShifts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [availableDates, setAvailableDates] = useState([]);
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedStore, setSelectedStore] = useState('A');
  const [customStore, setCustomStore] = useState('');

  useEffect(() => {
    fetchUsers();
    fetchAvailableDates();
  }, []);

  const fetchUsers = async () => {
    try {
      const { data, error } = await supabase
        .from('users')
        .select('*')
        .order('manager_number');
      
      if (error) throw error;
      setUsers(data || []);
    } catch (error) {
      console.error('„É¶„Éº„Ç∂„ÉºÂèñÂæó„Ç®„É©„Éº:', error);
    }
  };

  const fetchAvailableDates = async () => {
    try {
      const { data, error } = await supabase
        .from('shifts')
        .select('date')
        .order('date');

      if (error) throw error;

      const uniqueDates = data ? [...new Set(data.map(item => item.date))].sort() : [];
      setAvailableDates(uniqueDates);
    } catch (error) {
      console.error('Êó•‰ªòÂèñÂæó„Ç®„É©„Éº:', error);
    }
  };

  const fetchShifts = async (date) => {
    if (!date) return;

    setLoading(true);
    try {
      const { data: existingFinal, error: finalError } = await supabase
        .from('final_shifts')
        .select('*')
        .eq('date', date);

      if (finalError) throw finalError;

      if (existingFinal && existingFinal.length > 0) {
        alert('„Åì„ÅÆÊó•‰ªò„ÅÆ„Ç∑„Éï„Éà„ÅØÊó¢„Å´‰ΩúÊàêÊ∏à„Åø„Åß„Åô');
        setLoading(false);
        return;
      }

      const { data: shiftsData, error: shiftsError } = await supabase
        .from('shifts')
        .select('*')
        .eq('date', date)
        .order('manager_number');

      if (shiftsError) throw shiftsError;

      const userShifts = users.map(user => {
        const userShift = shiftsData?.find(s => s.manager_number === user.manager_number);
        return {
          manager_number: user.manager_number,
          name: user.name,
          start_time: userShift?.start_time || '',
          end_time: userShift?.end_time || '',
          is_off: !userShift || !userShift.start_time || !userShift.end_time
        };
      });

      setShifts(userShifts);
    } catch (error) {
      console.error('„Ç∑„Éï„ÉàÂèñÂæó„Ç®„É©„Éº:', error);
      alert('„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü');
    } finally {
      setLoading(false);
    }
  };

  const handleDateSelect = (date) => {
    if (!availableDates.includes(date)) return;
    setSelectedDate(date);
    fetchShifts(date);
  };

  const handleShiftChange = (index, field, value) => {
    const updated = [...shifts];
    updated[index][field] = value;

    if (field === 'is_off') {
      if (value) {
        updated[index].start_time = '';
        updated[index].end_time = '';
      }
    }

    setShifts(updated);
  };

  const handleCreate = async () => {
    if (!selectedDate) {
      alert('Êó•‰ªò„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    // Â∫óËàó„ÅÆÊ±∫ÂÆö
    const storeValue = selectedStore === '„Åù„ÅÆ‰ªñ' ? customStore : selectedStore;
    
    if (!storeValue || storeValue.trim() === '') {
      alert('Â∫óËàó„ÇíÈÅ∏Êäû„Åæ„Åü„ÅØÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    const confirmMsg = `${selectedDate} „ÅÆ„Ç∑„Éï„Éà„Çí‰ΩúÊàê„Åó„Åæ„Åô„ÅãÔºü\nÂ∫óËàó: ${storeValue}`;
    if (!window.confirm(confirmMsg)) return;

    setLoading(true);
    try {
      const finalShifts = shifts.map(shift => ({
        date: selectedDate,
        manager_number: shift.manager_number,
        start_time: shift.is_off ? null : shift.start_time,
        end_time: shift.is_off ? null : shift.end_time,
        is_off: shift.is_off,
        store: storeValue
      }));

      const { error } = await supabase
        .from('final_shifts')
        .insert(finalShifts);

      if (error) throw error;

      alert('„Ç∑„Éï„Éà„Çí‰ΩúÊàê„Åó„Åæ„Åó„ÅüÔºÅ');
      setSelectedDate('');
      setShifts([]);
      setSelectedStore('A');
      setCustomStore('');
      fetchAvailableDates();
    } catch (error) {
      console.error('„Ç∑„Éï„Éà‰ΩúÊàê„Ç®„É©„Éº:', error);
      alert(`„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    const firstDay = new Date(year, month, 1);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());

    const days = [];
    const currentDate = new Date(startDate);

    for (let i = 0; i < 42; i++) {
      const dateStr = currentDate.toISOString().split('T')[0];
      const isCurrentMonth = currentDate.getMonth() === month;
      const hasShift = availableDates.includes(dateStr);

      days.push({
        date: new Date(currentDate),
        dateStr: dateStr,
        day: currentDate.getDate(),
        isCurrentMonth,
        hasShift
      });

      currentDate.setDate(currentDate.getDate() + 1);
    }

    return days;
  };

  const changeMonth = (delta) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + delta);
    setCurrentMonth(newMonth);
  };

  const getWeekday = (dateStr) => {
    const days = ['Êó•', 'Êúà', 'ÁÅ´', 'Ê∞¥', 'Êú®', 'Èáë', 'Âúü'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  const calendarDays = generateCalendarDays();
  const weekdays = ['Êó•', 'Êúà', 'ÁÅ´', 'Ê∞¥', 'Êú®', 'Èáë', 'Âúü'];

  if (!selectedDate) {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ width: '600px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
          <h2>„Ç∑„Éï„Éà‰ΩúÊàê</h2>

          <div style={{
            marginTop: '1rem',
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '1rem',
            backgroundColor: '#f9f9f9'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '1rem'
            }}>
              <button onClick={() => changeMonth(-1)} style={{
                backgroundColor: '#607D8B',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ‚óÄ
              </button>
              <h3 style={{ margin: 0 }}>
                {currentMonth.getFullYear()}Âπ¥ {currentMonth.getMonth() + 1}Êúà
              </h3>
              <button onClick={() => changeMonth(1)} style={{
                backgroundColor: '#607D8B',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ‚ñ∂
              </button>
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(7, 1fr)',
              gap: '2px',
              marginBottom: '0.5rem'
            }}>
              {weekdays.map(day => (
                <div key={day} style={{
                  textAlign: 'center',
                  fontWeight: 'bold',
                  padding: '0.5rem',
                  backgroundColor: '#e0e0e0'
                }}>
                  {day}
                </div>
              ))}
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(7, 1fr)',
              gap: '2px'
            }}>
              {calendarDays.map((dayInfo, index) => (
                <button
                  key={index}
                  onClick={() => dayInfo.hasShift && handleDateSelect(dayInfo.dateStr)}
                  disabled={!dayInfo.hasShift}
                  style={{
                    padding: '0.5rem',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: dayInfo.hasShift ? 'pointer' : 'not-allowed',
                    backgroundColor: dayInfo.hasShift ? '#C8E6C9' :
                                   dayInfo.isCurrentMonth ? 'white' : '#f0f0f0',
                    color: !dayInfo.hasShift ? '#999' :
                           dayInfo.isCurrentMonth ? 'black' : '#666',
                    fontWeight: dayInfo.hasShift ? 'bold' : 'normal',
                    opacity: dayInfo.isCurrentMonth ? 1 : 0.5,
                    transition: 'all 0.3s ease'
                  }}
                  onMouseEnter={(e) => {
                    if (dayInfo.hasShift) {
                      e.target.style.backgroundColor = '#A5D6A7';
                      e.target.style.transform = 'scale(1.05)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (dayInfo.hasShift) {
                      e.target.style.backgroundColor = '#C8E6C9';
                      e.target.style.transform = 'scale(1)';
                    }
                  }}
                >
                  {dayInfo.day}
                </button>
              ))}
            </div>

            <div style={{
              marginTop: '0.5rem',
              fontSize: '0.8rem',
              color: '#666',
              textAlign: 'center'
            }}>
              Á∑ëËâ≤: „Ç∑„Éï„ÉàÊèêÂá∫„ÅÇ„Çä ({availableDates.length}Êó•) | ÁÅ∞Ëâ≤: ÊèêÂá∫„Å™„Åó
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '700px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
        <h2>{selectedDate} ({getWeekday(selectedDate)}) „ÅÆ„Ç∑„Éï„Éà‰ΩúÊàê</h2>

        {/* Â∫óËàóÈÅ∏Êäû„Çª„ÇØ„Ç∑„Éß„É≥ */}
        <div style={{
          backgroundColor: '#f0f8ff',
          border: '2px solid #2196F3',
          borderRadius: '8px',
          padding: '1rem',
          marginBottom: '1.5rem'
        }}>
          <label style={{ 
            display: 'block', 
            fontWeight: 'bold', 
            marginBottom: '0.5rem',
            color: '#1976D2'
          }}>
            üè¢ ÈÖçÂ±ûÂ∫óËàó
          </label>
          <div style={{ display: 'flex', gap: '1rem', alignItems: 'center', flexWrap: 'wrap' }}>
            <select
              value={selectedStore}
              onChange={(e) => {
                setSelectedStore(e.target.value);
                if (e.target.value !== '„Åù„ÅÆ‰ªñ') {
                  setCustomStore('');
                }
              }}
              style={{
                padding: '0.5rem',
                border: '2px solid #2196F3',
                borderRadius: '4px',
                fontSize: '1rem',
                minWidth: '150px',
                cursor: 'pointer'
              }}
            >
              <option value="A">AÂ∫óËàó</option>
              <option value="B">BÂ∫óËàó</option>
              <option value="„Åù„ÅÆ‰ªñ">„Åù„ÅÆ‰ªñ</option>
            </select>

            {selectedStore === '„Åù„ÅÆ‰ªñ' && (
              <input
                type="text"
                value={customStore}
                onChange={(e) => setCustomStore(e.target.value)}
                placeholder="Â∫óËàóÂêç„ÇíÂÖ•Âäõ"
                style={{
                  padding: '0.5rem',
                  border: '2px solid #FF9800',
                  borderRadius: '4px',
                  fontSize: '1rem',
                  minWidth: '200px'
                }}
              />
            )}
          </div>
          <div style={{ marginTop: '0.5rem', fontSize: '0.85rem', color: '#666' }}>
            ÈÅ∏Êäû„Åï„Çå„ÅüÂ∫óËàó: <strong style={{ color: '#2196F3' }}>
              {selectedStore === '„Åù„ÅÆ‰ªñ' ? (customStore || '(Êú™ÂÖ•Âäõ)') : selectedStore}Â∫óËàó
            </strong>
          </div>
        </div>

        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            Ë™≠„ÅøËæº„Åø‰∏≠...
          </div>
        ) : (
          <>
            <div style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              overflow: 'hidden',
              marginBottom: '1rem'
            }}>
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ backgroundColor: '#f5f5f5' }}>
                    <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>
                      ÂêçÂâç
                    </th>
                    <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                      ÈñãÂßãÊôÇÈñì
                    </th>
                    <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                      ÁµÇ‰∫ÜÊôÇÈñì
                    </th>
                    <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                      ‰ºë„Åø
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {shifts.map((shift, index) => (
                    <tr key={shift.manager_number} style={{
                      backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                    }}>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                        <strong>{shift.name}</strong>
                      </td>
                      <td style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>
                        <input
                          type="time"
                          value={shift.start_time}
                          onChange={(e) => handleShiftChange(index, 'start_time', e.target.value)}
                          disabled={shift.is_off}
                          style={{
                            padding: '0.5rem',
                            border: '1px solid #ddd',
                            borderRadius: '4px',
                            opacity: shift.is_off ? 0.5 : 1,
                            backgroundColor: shift.is_off ? '#f5f5f5' : 'white'
                          }}
                        />
                      </td>
                      <td style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>
                        <input
                          type="time"
                          value={shift.end_time}
                          onChange={(e) => handleShiftChange(index, 'end_time', e.target.value)}
                          disabled={shift.is_off}
                          style={{
                            padding: '0.5rem',
                            border: '1px solid #ddd',
                            borderRadius: '4px',
                            opacity: shift.is_off ? 0.5 : 1,
                            backgroundColor: shift.is_off ? '#f5f5f5' : 'white'
                          }}
                        />
                      </td>
                      <td style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>
                        <input
                          type="checkbox"
                          checked={shift.is_off}
                          onChange={(e) => handleShiftChange(index, 'is_off', e.target.checked)}
                          style={{ transform: 'scale(1.2)', cursor: 'pointer' }}
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center' }}>
              <button
                onClick={handleCreate}
                disabled={loading}
                style={{
                  backgroundColor: '#4CAF50',
                  color: 'white',
                  padding: '0.75rem 2rem',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: loading ? 'not-allowed' : 'pointer',
                  opacity: loading ? 0.6 : 1,
                  fontSize: '1rem'
                }}
              >
                {loading ? '‰ΩúÊàê‰∏≠...' : '„Ç∑„Éï„Éà‰ΩúÊàê'}
              </button>

              <button
                onClick={() => {
                  setSelectedDate('');
                  setShifts([]);
                  setSelectedStore('A');
                  setCustomStore('');
                }}
                style={{
                  backgroundColor: '#607D8B',
                  color: 'white',
                  padding: '0.75rem 2rem',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  fontSize: '1rem'
                }}
              >
                „Ç≠„É£„É≥„Çª„É´
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default ManagerCreate;
