import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function StaffWorkHours({ onBack }) {
  const [managerNumber, setManagerNumber] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [workData, setWorkData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear().toString());
  const [selectedMonth, setSelectedMonth] = useState('');
  const [viewMode, setViewMode] = useState('all'); // 'all', 'weekly', 'monthly', 'time'

  const handleAuthentication = async () => {
    if (!managerNumber) {
      setMessage('管理番号を入力してください');
      return;
    }

    setLoading(true);
    try {
      const { data: user, error: userError } = await supabase
        .from('users')
        .select('*')
        .eq('manager_number', managerNumber)
        .single();

      if (userError || !user) {
        setMessage('管理番号が見つかりません');
        setLoading(false);
        return;
      }

      setUserName(user.name);
      setIsAuthenticated(true);
      setMessage('');
      
      fetchWorkData(managerNumber);

    } catch (error) {
      console.error('認証エラー:', error);
      setMessage('エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  const fetchWorkData = async (mgr_number, year = null, month = null) => {
    setLoading(true);
    try {
      let query = supabase
        .from('attendance')
        .select('*')
        .eq('manager_number', mgr_number)
        .order('date', { ascending: false });

      if (year && month) {
        const startDate = `${year}-${month.padStart(2, '0')}-01`;
        const endDate = new Date(year, month, 0);
        const endDateStr = endDate.toISOString().split('T')[0];
        query = query.gte('date', startDate).lte('date', endDateStr);
      } else if (year) {
        const startDate = `${year}-01-01`;
        const endDate = `${year}-12-31`;
        query = query.gte('date', startDate).lte('date', endDate);
      }

      const { data, error } = await query;

      if (error) {
        console.error('データ取得エラー:', error);
        setMessage('データの取得に失敗しました');
        setWorkData([]);
        setLoading(false);
        return;
      }

      if (!data || data.length === 0) {
        setMessage('勤怠データがありません');
        setWorkData([]);
        setLoading(false);
        return;
      }

      setWorkData(data);
      setMessage('');

    } catch (error) {
      console.error('データ取得エラー:', error);
      setMessage('エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  const handleFilter = () => {
    fetchWorkData(managerNumber, selectedYear, selectedMonth);
  };

  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  const formatTime = (timeStr) => {
    if (!timeStr) return '-';
    return timeStr.slice(0, 5);
  };

  const getWeekNumber = (dateStr) => {
    const date = new Date(dateStr);
    const firstDayOfYear = new Date(date.getFullYear(), 0, 1);
    const pastDaysOfYear = (date - firstDayOfYear) / 86400000;
    return Math.ceil((pastDaysOfYear + firstDayOfYear.getDay() + 1) / 7);
  };

  const getTimeSlot = (timeStr) => {
    if (!timeStr) return '不明';
    const hour = parseInt(timeStr.split(':')[0]);
    if (hour >= 6 && hour < 12) return '朝 (6-12時)';
    if (hour >= 12 && hour < 17) return '昼 (12-17時)';
    if (hour >= 17 && hour < 22) return '夕 (17-22時)';
    return '夜 (22-6時)';
  };

  const calculateTotalStats = () => {
    let totalMinutes = 0;
    let totalSalary = 0;
    let workDays = 0;

    workData.forEach(record => {
      if (record.work_minutes) {
        totalMinutes += record.work_minutes;
        workDays++;
      }
      if (record.salary) {
        totalSalary += record.salary;
      }
    });

    const hours = Math.floor(totalMinutes / 60);
    const minutes = totalMinutes % 60;

    return {
      totalHours: hours,
      totalMinutes: minutes,
      totalSalary: totalSalary,
      workDays: workDays
    };
  };

  const getGroupedData = () => {
    if (viewMode === 'all') {
      return [{ key: 'すべて', data: workData }];
    }

    if (viewMode === 'weekly') {
      const grouped = {};
      workData.forEach(record => {
        const weekNum = getWeekNumber(record.date);
        const year = new Date(record.date).getFullYear();
        const key = `${year}年 第${weekNum}週`;
        if (!grouped[key]) grouped[key] = [];
        grouped[key].push(record);
      });
      return Object.keys(grouped).sort().reverse().map(key => ({ key, data: grouped[key] }));
    }

    if (viewMode === 'monthly') {
      const grouped = {};
      workData.forEach(record => {
        const date = new Date(record.date);
        const key = `${date.getFullYear()}年${(date.getMonth() + 1)}月`;
        if (!grouped[key]) grouped[key] = [];
        grouped[key].push(record);
      });
      return Object.keys(grouped).sort().reverse().map(key => ({ key, data: grouped[key] }));
    }

    if (viewMode === 'time') {
      const grouped = {};
      workData.forEach(record => {
        const key = getTimeSlot(record.actual_start);
        if (!grouped[key]) grouped[key] = [];
        grouped[key].push(record);
      });
      const order = ['朝 (6-12時)', '昼 (12-17時)', '夕 (17-22時)', '夜 (22-6時)', '不明'];
      return order.filter(key => grouped[key]).map(key => ({ key, data: grouped[key] }));
    }

    return [{ key: 'すべて', data: workData }];
  };

  const calculateGroupStats = (data) => {
    let totalMinutes = 0;
    let totalSalary = 0;
    let workDays = data.length;

    data.forEach(record => {
      if (record.work_minutes) totalMinutes += record.work_minutes;
      if (record.salary) totalSalary += record.salary;
    });

    const hours = Math.floor(totalMinutes / 60);
    const minutes = totalMinutes % 60;

    return { hours, minutes, totalSalary, workDays };
  };

  const years = Array.from({ length: 10 }, (_, i) => new Date().getFullYear() - i);
  const months = Array.from({ length: 12 }, (_, i) => (i + 1).toString());

  if (!isAuthenticated) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>就労時間確認</h2>
          <p style={{ fontSize: '0.9rem', color: '#666', marginBottom: '1rem' }}>
            自分の勤務記録を確認できます
          </p>
          
          <label>管理番号:</label>
          <input
            type="text"
            value={managerNumber}
            onChange={(e) => setManagerNumber(e.target.value)}
            placeholder="管理番号を入力"
            style={{
              padding: '0.5rem',
              border: '1px solid #ddd',
              borderRadius: '4px',
              width: '100%'
            }}
          />

          <button 
            onClick={handleAuthentication} 
            disabled={loading}
            style={{
              opacity: loading ? 0.6 : 1,
              cursor: loading ? 'not-allowed' : 'pointer',
              marginTop: '1rem'
            }}
          >
            {loading ? '確認中...' : '確認'}
          </button>

          {message && (
            <p style={{ 
              color: message.includes('成功') || message.includes('確認') ? 'green' : 'red', 
              marginTop: '0.5rem',
              fontSize: '0.9rem'
            }}>
              {message}
            </p>
          )}

          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <button onClick={onBack} style={{
              backgroundColor: '#607D8B',
              color: 'white',
              padding: '0.75rem 2rem',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}>
              メニューに戻る
            </button>
          </div>
        </div>
      </div>
    );
  }

  const stats = calculateTotalStats();
  const groupedData = getGroupedData();

  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '900px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1rem' }}>
          <h2 style={{ margin: 0 }}>就労時間確認</h2>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
            <label style={{ fontSize: '0.9rem', fontWeight: 'bold' }}>年度:</label>
            <select
              value={selectedYear}
              onChange={(e) => setSelectedYear(e.target.value)}
              style={{
                padding: '0.5rem',
                border: '1px solid #ddd',
                borderRadius: '4px',
                fontSize: '0.9rem'
              }}
            >
              {years.map(year => (
                <option key={year} value={year}>{year}年</option>
              ))}
            </select>
          </div>
        </div>
        
        <p>管理番号: <strong>{managerNumber}</strong> | 名前: <strong>{userName}</strong></p>

        {/* フィルター */}
        <div style={{
          backgroundColor: '#f9f9f9',
          padding: '1rem',
          borderRadius: '8px',
          marginBottom: '1rem',
          display: 'flex',
          gap: '1rem',
          alignItems: 'center',
          flexWrap: 'wrap'
        }}>
          <label style={{ fontWeight: 'bold' }}>月:</label>
          <select
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(e.target.value)}
            style={{
              padding: '0.5rem',
              border: '1px solid #ddd',
              borderRadius: '4px'
            }}
          >
            <option value="">全ての月</option>
            {months.map(month => (
              <option key={month} value={month}>{month}月</option>
            ))}
          </select>
          <button
            onClick={handleFilter}
            style={{
              backgroundColor: '#2196F3',
              color: 'white',
              padding: '0.5rem 1rem',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            絞り込み
          </button>
          {selectedMonth && (
            <button
              onClick={() => {
                setSelectedMonth('');
                fetchWorkData(managerNumber, selectedYear);
              }}
              style={{
                backgroundColor: '#FF9800',
                color: 'white',
                padding: '0.5rem 1rem',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              月リセット
            </button>
          )}
        </div>

        {/* 表示モード選択 */}
        <div style={{
          backgroundColor: '#E3F2FD',
          padding: '1rem',
          borderRadius: '8px',
          marginBottom: '1rem',
          display: 'flex',
          gap: '0.5rem',
          flexWrap: 'wrap'
        }}>
          <button
            onClick={() => setViewMode('all')}
            style={{
              backgroundColor: viewMode === 'all' ? '#1976D2' : 'white',
              color: viewMode === 'all' ? 'white' : '#1976D2',
              padding: '0.5rem 1rem',
              border: '2px solid #1976D2',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            全表示
          </button>
          <button
            onClick={() => setViewMode('weekly')}
            style={{
              backgroundColor: viewMode === 'weekly' ? '#1976D2' : 'white',
              color: viewMode === 'weekly' ? 'white' : '#1976D2',
              padding: '0.5rem 1rem',
              border: '2px solid #1976D2',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            週別
          </button>
          <button
            onClick={() => setViewMode('monthly')}
            style={{
              backgroundColor: viewMode === 'monthly' ? '#1976D2' : 'white',
              color: viewMode === 'monthly' ? 'white' : '#1976D2',
              padding: '0.5rem 1rem',
              border: '2px solid #1976D2',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            月別
          </button>
          <button
            onClick={() => setViewMode('time')}
            style={{
              backgroundColor: viewMode === 'time' ? '#1976D2' : 'white',
              color: viewMode === 'time' ? 'white' : '#1976D2',
              padding: '0.5rem 1rem',
              border: '2px solid #1976D2',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            時間帯別
          </button>
        </div>

        {/* 全体集計サマリー */}
        <div style={{
          backgroundColor: '#E8F5E9',
          padding: '1rem',
          borderRadius: '8px',
          marginBottom: '1rem',
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
          gap: '1rem'
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '0.9rem', color: '#666' }}>勤務日数</div>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1976D2' }}>
              {stats.workDays}日
            </div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '0.9rem', color: '#666' }}>総労働時間</div>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1976D2' }}>
              {stats.totalHours}時間{stats.totalMinutes}分
            </div>
          </div>
          {stats.totalSalary > 0 && (
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '0.9rem', color: '#666' }}>総給料</div>
              <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#4CAF50' }}>
                ¥{stats.totalSalary.toLocaleString()}
              </div>
            </div>
          )}
        </div>

        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            読み込み中...
          </div>
        ) : workData.length === 0 ? (
          <div style={{
            textAlign: 'center',
            padding: '2rem',
            color: '#666',
            backgroundColor: '#f9f9f9',
            borderRadius: '8px'
          }}>
            {message || '勤怠データがありません'}
          </div>
        ) : (
          <div>
            {groupedData.map((group, groupIndex) => {
              const groupStats = calculateGroupStats(group.data);
              
              return (
                <div key={groupIndex} style={{ marginBottom: '2rem' }}>
                  {viewMode !== 'all' && (
                    <div style={{
                      backgroundColor: '#FFF3E0',
                      padding: '0.75rem',
                      borderRadius: '8px',
                      marginBottom: '0.5rem',
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'center'
                    }}>
                      <h3 style={{ margin: 0, color: '#E65100' }}>{group.key}</h3>
                      <div style={{ fontSize: '0.9rem', color: '#666' }}>
                        {groupStats.workDays}日 | {groupStats.hours}h{groupStats.minutes}m
                        {groupStats.totalSalary > 0 && ` | ¥${groupStats.totalSalary.toLocaleString()}`}
                      </div>
                    </div>
                  )}
                  
                  <div style={{
                    border: '1px solid #ddd',
                    borderRadius: '8px',
                    overflow: 'auto',
                    maxHeight: '400px'
                  }}>
                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                      <thead style={{ position: 'sticky', top: 0, backgroundColor: '#f5f5f5' }}>
                        <tr>
                          <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>
                            日付
                          </th>
                          <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                            店舗
                          </th>
                          <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                            開始時刻
                          </th>
                          <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                            終了時刻
                          </th>
                          <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                            休憩(分)
                          </th>
                          <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                            労働時間
                          </th>
                          {group.data.some(d => d.salary) && (
                            <th style={{ padding: '0.75rem', textAlign: 'right', borderBottom: '1px solid #ddd' }}>
                              給料
                            </th>
                          )}
                        </tr>
                      </thead>
                      <tbody>
                        {group.data.map((record, index) => {
                          const hours = Math.floor(record.work_minutes / 60);
                          const minutes = record.work_minutes % 60;

                          return (
                            <tr key={record.id} style={{
                              backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                            }}>
                              <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                                <strong>{record.date}</strong> ({getWeekday(record.date)})
                              </td>
                              <td style={{ 
                                padding: '0.75rem', 
                                textAlign: 'center', 
                                borderBottom: '1px solid #eee',
                                fontWeight: 'bold',
                                color: '#1976D2'
                              }}>
                                {record.store}店舗
                              </td>
                              <td style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>
                                {formatTime(record.actual_start)}
                              </td>
                              <td style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>
                                {formatTime(record.actual_end)}
                              </td>
                              <td style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>
                                {record.break_minutes || 0}分
                              </td>
                              <td style={{ 
                                padding: '0.75rem', 
                                textAlign: 'center', 
                                borderBottom: '1px solid #eee',
                                fontWeight: 'bold'
                              }}>
                                {hours}時間{minutes}分
                              </td>
                              {group.data.some(d => d.salary) && (
                                <td style={{ 
                                  padding: '0.75rem', 
                                  textAlign: 'right', 
                                  borderBottom: '1px solid #eee',
                                  fontWeight: 'bold',
                                  color: record.salary ? '#4CAF50' : '#999'
                                }}>
                                  {record.salary ? `¥${record.salary.toLocaleString()}` : '-'}
                                </td>
                              )}
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        <div style={{ marginTop: '1rem', textAlign: 'center' }}>
          <button onClick={onBack} style={{
            backgroundColor: '#607D8B',
            color: 'white',
            padding: '0.75rem 2rem',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}>
            メニューに戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default StaffWorkHours;
