import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function ShiftAnalysis({ onBack }) {
  const [users, setUsers] = useState([]);
  const [shiftData, setShiftData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [viewType, setViewType] = useState('yearly');
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [selectedUser, setSelectedUser] = useState('all');
  const [analysisData, setAnalysisData] = useState({});

  useEffect(() => {
    fetchUsers();
    fetchShiftData();
  }, []);

  useEffect(() => {
    if (shiftData.length > 0) {
      calculateAnalysis();
    }
  }, [shiftData, selectedYear, selectedUser, viewType]);

  const fetchUsers = async () => {
    try {
      const { data: usersData, error } = await supabase
        .from('users')
        .select('*')
        .order('manager_number');

      if (error) {
        console.error('ユーザー取得エラー:', error);
        return;
      }

      setUsers(usersData || []);
    } catch (error) {
      console.error('ユーザー取得エラー:', error);
    }
  };

  const fetchShiftData = async () => {
    setLoading(true);
    try {
      const { data: finalShifts, error } = await supabase
        .from('final_shifts')
        .select('*')
        .order('date');

      if (error) {
        console.error('シフトデータ取得エラー:', error);
        return;
      }

      setShiftData(finalShifts || []);
    } catch (error) {
      console.error('シフトデータ取得エラー:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateWorkingHours = (startTime, endTime) => {
    if (!startTime || !endTime || startTime === '00:00' && endTime === '00:00') return 0;
    
    const start = new Date(`1970-01-01T${startTime}`);
    const end = new Date(`1970-01-01T${endTime}`);
    
    if (end < start) {
      end.setDate(end.getDate() + 1);
    }
    
    return (end - start) / (1000 * 60 * 60); // 時間単位
  };

  const getUserName = (managerNumber) => {
    const user = users.find(u => u.manager_number === managerNumber);
    return user ? user.name : `ユーザー${managerNumber}`;
  };

  const calculateAnalysis = () => {
    const currentDate = new Date();
    const analysisResult = {};

    // ユーザーごとの分析
    users.forEach(user => {
      const userShifts = shiftData.filter(shift => 
        shift.manager_number === user.manager_number &&
        !shift.is_off &&
        shift.start_time &&
        shift.end_time
      );

      const yearlyHours = userShifts
        .filter(shift => new Date(shift.date).getFullYear() === selectedYear)
        .reduce((total, shift) => total + calculateWorkingHours(shift.start_time, shift.end_time), 0);

      // 月別データ
      const monthlyData = {};
      for (let month = 1; month <= 12; month++) {
        const monthlyHours = userShifts
          .filter(shift => {
            const shiftDate = new Date(shift.date);
            return shiftDate.getFullYear() === selectedYear && shiftDate.getMonth() + 1 === month;
          })
          .reduce((total, shift) => total + calculateWorkingHours(shift.start_time, shift.end_time), 0);
        monthlyData[month] = monthlyHours;
      }

      // 週別データ（直近12週間）
      const weeklyData = [];
      for (let i = 11; i >= 0; i--) {
        const weekStart = new Date(currentDate);
        weekStart.setDate(weekStart.getDate() - (weekStart.getDay()) - (7 * i));
        const weekEnd = new Date(weekStart);
        weekEnd.setDate(weekStart.getDate() + 6);

        const weeklyHours = userShifts
          .filter(shift => {
            const shiftDate = new Date(shift.date);
            return shiftDate >= weekStart && shiftDate <= weekEnd;
          })
          .reduce((total, shift) => total + calculateWorkingHours(shift.start_time, shift.end_time), 0);

        weeklyData.push({
          week: `${weekStart.getMonth() + 1}/${weekStart.getDate()}`,
          hours: weeklyHours
        });
      }

      analysisResult[user.manager_number] = {
        name: user.name,
        yearlyHours,
        monthlyData,
        weeklyData,
        totalShifts: userShifts.filter(shift => new Date(shift.date).getFullYear() === selectedYear).length
      };
    });

    setAnalysisData(analysisResult);
  };

  const renderYearlyView = () => {
    if (selectedUser === 'all') {
      const sortedUsers = Object.values(analysisData).sort((a, b) => b.yearlyHours - a.yearlyHours);
      
      return (
        <div style={{ marginTop: '2rem' }}>
          <h3>{selectedYear}年 総勤務時間ランキング</h3>
          <div style={{ 
            border: '1px solid #ddd', 
            borderRadius: '8px', 
            overflow: 'hidden',
            backgroundColor: 'white'
          }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '1rem', textAlign: 'left' }}>順位</th>
                  <th style={{ padding: '1rem', textAlign: 'left' }}>名前</th>
                  <th style={{ padding: '1rem', textAlign: 'right' }}>総勤務時間</th>
                  <th style={{ padding: '1rem', textAlign: 'right' }}>シフト回数</th>
                  <th style={{ padding: '1rem', textAlign: 'right' }}>平均時間/回</th>
                </tr>
              </thead>
              <tbody>
                {sortedUsers.map((user, index) => (
                  <tr key={user.name} style={{
                    backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                  }}>
                    <td style={{ padding: '1rem', fontWeight: 'bold' }}>
                      {index + 1}位
                    </td>
                    <td style={{ padding: '1rem', fontWeight: 'bold' }}>{user.name}</td>
                    <td style={{ padding: '1rem', textAlign: 'right' }}>
                      {user.yearlyHours.toFixed(1)}時間
                    </td>
                    <td style={{ padding: '1rem', textAlign: 'right' }}>
                      {user.totalShifts}回
                    </td>
                    <td style={{ padding: '1rem', textAlign: 'right' }}>
                      {user.totalShifts > 0 ? (user.yearlyHours / user.totalShifts).toFixed(1) : '0'}時間
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    } else {
      const userData = analysisData[selectedUser];
      if (!userData) return <div>データがありません</div>;

      return (
        <div style={{ marginTop: '2rem' }}>
          <h3>{userData.name}さんの{selectedYear}年勤務実績</h3>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
            gap: '1rem',
            marginBottom: '2rem'
          }}>
            <div style={{
              backgroundColor: '#E3F2FD',
              padding: '1.5rem',
              borderRadius: '8px',
              textAlign: 'center'
            }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1976D2' }}>
                {userData.yearlyHours.toFixed(1)}
              </div>
              <div style={{ color: '#666', marginTop: '0.5rem' }}>総勤務時間（時間）</div>
            </div>
            
            <div style={{
              backgroundColor: '#E8F5E8',
              padding: '1.5rem',
              borderRadius: '8px',
              textAlign: 'center'
            }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#4CAF50' }}>
                {userData.totalShifts}
              </div>
              <div style={{ color: '#666', marginTop: '0.5rem' }}>総シフト回数</div>
            </div>
            
            <div style={{
              backgroundColor: '#FFF3E0',
              padding: '1.5rem',
              borderRadius: '8px',
              textAlign: 'center'
            }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#FF9800' }}>
                {userData.totalShifts > 0 ? (userData.yearlyHours / userData.totalShifts).toFixed(1) : '0'}
              </div>
              <div style={{ color: '#666', marginTop: '0.5rem' }}>平均勤務時間/回（時間）</div>
            </div>
          </div>
        </div>
      );
    }
  };

  const renderMonthlyView = () => {
    if (selectedUser === 'all') {
      const months = ['1月', '2月', '3月', '4月', '5月', '6月', '7月', '8月', '9月', '10月', '11月', '12月'];
      
      return (
        <div style={{ marginTop: '2rem' }}>
          <h3>{selectedYear}年 月別勤務時間比較</h3>
          <div style={{ overflowX: 'auto' }}>
            <table style={{ 
              width: '100%', 
              borderCollapse: 'collapse',
              backgroundColor: 'white',
              border: '1px solid #ddd',
              borderRadius: '8px'
            }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '0.75rem', textAlign: 'left', position: 'sticky', left: 0, backgroundColor: '#f5f5f5' }}>名前</th>
                  {months.map(month => (
                    <th key={month} style={{ padding: '0.75rem', textAlign: 'center', minWidth: '80px' }}>
                      {month}
                    </th>
                  ))}
                  <th style={{ padding: '0.75rem', textAlign: 'center', backgroundColor: '#e3f2fd' }}>合計</th>
                </tr>
              </thead>
              <tbody>
                {Object.values(analysisData).map((user, index) => (
                  <tr key={user.name} style={{
                    backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                  }}>
                    <td style={{ 
                      padding: '0.75rem', 
                      fontWeight: 'bold',
                      position: 'sticky',
                      left: 0,
                      backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                    }}>
                      {user.name}
                    </td>
                    {months.map((month, monthIndex) => {
                      const hours = user.monthlyData[monthIndex + 1] || 0;
                      return (
                        <td key={month} style={{ 
                          padding: '0.75rem', 
                          textAlign: 'center',
                          color: hours > 0 ? '#333' : '#999'
                        }}>
                          {hours > 0 ? `${hours.toFixed(1)}h` : '-'}
                        </td>
                      );
                    })}
                    <td style={{ 
                      padding: '0.75rem', 
                      textAlign: 'center', 
                      fontWeight: 'bold',
                      backgroundColor: '#e3f2fd'
                    }}>
                      {user.yearlyHours.toFixed(1)}h
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    } else {
      const userData = analysisData[selectedUser];
      if (!userData) return <div>データがありません</div>;

      const months = ['1月', '2月', '3月', '4月', '5月', '6月', '7月', '8月', '9月', '10月', '11月', '12月'];
      const maxHours = Math.max(...Object.values(userData.monthlyData));

      return (
        <div style={{ marginTop: '2rem' }}>
          <h3>{userData.name}さんの{selectedYear}年 月別勤務時間</h3>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
            gap: '1rem',
            marginTop: '1rem'
          }}>
            {months.map((month, index) => {
              const hours = userData.monthlyData[index + 1] || 0;
              const percentage = maxHours > 0 ? (hours / maxHours) * 100 : 0;
              
              return (
                <div key={month} style={{
                  backgroundColor: 'white',
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  padding: '1rem',
                  textAlign: 'center'
                }}>
                  <div style={{ fontWeight: 'bold', marginBottom: '0.5rem' }}>{month}</div>
                  <div style={{
                    height: '100px',
                    backgroundColor: '#f5f5f5',
                    borderRadius: '4px',
                    position: 'relative',
                    marginBottom: '0.5rem'
                  }}>
                    <div style={{
                      position: 'absolute',
                      bottom: 0,
                      left: 0,
                      right: 0,
                      height: `${percentage}%`,
                      backgroundColor: hours > 0 ? '#1976D2' : '#ddd',
                      borderRadius: '4px',
                      transition: 'height 0.3s ease'
                    }}></div>
                  </div>
                  <div style={{ 
                    fontSize: '1.2rem', 
                    fontWeight: 'bold',
                    color: hours > 0 ? '#1976D2' : '#999'
                  }}>
                    {hours > 0 ? `${hours.toFixed(1)}h` : '-'}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      );
    }
  };

  const renderWeeklyView = () => {
    if (selectedUser === 'all') {
      // 全員の週別データを表示
      const weekLabels = analysisData[Object.keys(analysisData)[0]]?.weeklyData.map(w => w.week) || [];
      
      return (
        <div style={{ marginTop: '2rem' }}>
          <h3>直近12週間の勤務時間推移</h3>
          <div style={{ overflowX: 'auto' }}>
            <table style={{ 
              width: '100%', 
              borderCollapse: 'collapse',
              backgroundColor: 'white',
              border: '1px solid #ddd',
              borderRadius: '8px'
            }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '0.75rem', textAlign: 'left', position: 'sticky', left: 0, backgroundColor: '#f5f5f5' }}>名前</th>
                  {weekLabels.map(week => (
                    <th key={week} style={{ padding: '0.75rem', textAlign: 'center', minWidth: '70px' }}>
                      {week}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {Object.values(analysisData).map((user, index) => (
                  <tr key={user.name} style={{
                    backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                  }}>
                    <td style={{ 
                      padding: '0.75rem', 
                      fontWeight: 'bold',
                      position: 'sticky',
                      left: 0,
                      backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                    }}>
                      {user.name}
                    </td>
                    {user.weeklyData.map((weekData, weekIndex) => (
                      <td key={weekIndex} style={{ 
                        padding: '0.75rem', 
                        textAlign: 'center',
                        color: weekData.hours > 0 ? '#333' : '#999'
                      }}>
                        {weekData.hours > 0 ? `${weekData.hours.toFixed(1)}` : '-'}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    } else {
      const userData = analysisData[selectedUser];
      if (!userData) return <div>データがありません</div>;

      const maxWeeklyHours = Math.max(...userData.weeklyData.map(w => w.hours));

      return (
        <div style={{ marginTop: '2rem' }}>
          <h3>{userData.name}さんの直近12週間勤務時間</h3>
          <div style={{
            display: 'flex',
            alignItems: 'end',
            justifyContent: 'space-between',
            gap: '0.5rem',
            padding: '2rem 1rem 1rem',
            backgroundColor: 'white',
            border: '1px solid #ddd',
            borderRadius: '8px',
            overflowX: 'auto'
          }}>
            {userData.weeklyData.map((weekData, index) => {
              const percentage = maxWeeklyHours > 0 ? (weekData.hours / maxWeeklyHours) * 100 : 0;
              
              return (
                <div key={index} style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  minWidth: '60px'
                }}>
                  <div style={{
                    width: '40px',
                    height: `${Math.max(percentage * 2, weekData.hours > 0 ? 20 : 5)}px`,
                    backgroundColor: weekData.hours > 0 ? '#1976D2' : '#ddd',
                    borderRadius: '4px 4px 0 0',
                    marginBottom: '0.5rem',
                    transition: 'height 0.3s ease'
                  }}></div>
                  <div style={{ 
                    fontSize: '0.8rem', 
                    textAlign: 'center',
                    color: weekData.hours > 0 ? '#333' : '#999',
                    marginBottom: '0.25rem'
                  }}>
                    {weekData.hours > 0 ? `${weekData.hours.toFixed(1)}h` : '-'}
                  </div>
                  <div style={{ 
                    fontSize: '0.7rem', 
                    color: '#666',
                    textAlign: 'center',
                    whiteSpace: 'nowrap'
                  }}>
                    {weekData.week}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      );
    }
  };

  const renderCurrentView = () => {
    switch(viewType) {
      case 'yearly':
        return renderYearlyView();
      case 'monthly':
        return renderMonthlyView();
      case 'weekly':
        return renderWeeklyView();
      default:
        return renderYearlyView();
    }
  };

  if (loading) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>シフト分析</h2>
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            読み込み中...
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '95vw', maxWidth: '1200px', maxHeight: '90vh', overflowY: 'auto' }}>
        <h2>シフト分析</h2>
        
        {/* フィルター */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '1rem',
          marginBottom: '2rem',
          padding: '1rem',
          backgroundColor: '#f5f5f5',
          borderRadius: '8px'
        }}>
          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>表示タイプ</label>
            <select 
              value={viewType} 
              onChange={(e) => setViewType(e.target.value)}
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #ddd',
                borderRadius: '4px'
              }}
            >
              <option value="yearly">年間総計</option>
              <option value="monthly">月別詳細</option>
              <option value="weekly">週別推移</option>
            </select>
          </div>
          
          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>年度</label>
            <select 
              value={selectedYear} 
              onChange={(e) => setSelectedYear(parseInt(e.target.value))}
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #ddd',
                borderRadius: '4px'
              }}
            >
              {[2023, 2024, 2025].map(year => (
                <option key={year} value={year}>{year}年</option>
              ))}
            </select>
          </div>
          
          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>対象者</label>
            <select 
              value={selectedUser} 
              onChange={(e) => setSelectedUser(e.target.value)}
              style={{
                width: '100%',
                padding: '0.5rem',
                border: '1px solid #ddd',
                borderRadius: '4px'
              }}
            >
              <option value="all">全員</option>
              {users.map(user => (
                <option key={user.manager_number} value={user.manager_number}>
                  {user.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* 分析結果表示 */}
        {Object.keys(analysisData).length === 0 ? (
          <div style={{
            textAlign: 'center',
            padding: '3rem',
            backgroundColor: '#f9f9f9',
            borderRadius: '8px',
            color: '#666'
          }}>
            シフトデータがありません
          </div>
        ) : (
          renderCurrentView()
        )}

        {/* 戻るボタン */}
        <div style={{ marginTop: '2rem', textAlign: 'center' }}>
          <button 
            onClick={onBack}
            style={{
              backgroundColor: '#607D8B',
              color: 'white',
              padding: '0.75rem 2rem',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '1rem'
            }}
          >
            メニューに戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default ShiftAnalysis;
