import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function StaffShiftView({ onBack }) {
  const [selectedDate, setSelectedDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [userMap, setUserMap] = useState({});
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [availableDates, setAvailableDates] = useState([]);
  const [viewMode, setViewMode] = useState('list');
  const [currentView, setCurrentView] = useState('calendar');
  const [debugInfo, setDebugInfo] = useState(''); // デバッグ情報用

  // 初期化時に利用可能な日付を取得
  useEffect(() => {
    console.log('StaffShiftView初期化開始');
    setDebugInfo('初期化中...');
    fetchAvailableDates();
    fetchUsers();
  }, []);

  // 利用可能な日付を取得
  const fetchAvailableDates = async () => {
    console.log('fetchAvailableDates開始');
    setDebugInfo(prev => prev + '\n日付データ取得中...');
    
    try {
      console.log('Supabase接続テスト:', supabase);
      
      const { data, error } = await supabase
        .from('final_shifts')
        .select('date')
        .order('date');

      console.log('final_shifts取得結果:', { data, error });
      setDebugInfo(prev => prev + `\nfinal_shifts結果: ${data ? data.length : 0}件`);

      if (error) {
        console.error('日付取得エラー:', error);
        setDebugInfo(prev => prev + `\nエラー: ${error.message}`);
        
        // エラーの場合、テーブル名を変えて再試行
        console.log('shiftsテーブルで再試行...');
        const { data: shiftsData, error: shiftsError } = await supabase
          .from('shifts')
          .select('date')
          .order('date');
          
        console.log('shifts取得結果:', { shiftsData, shiftsError });
        setDebugInfo(prev => prev + `\nshifts結果: ${shiftsData ? shiftsData.length : 0}件`);
        
        if (shiftsError) {
          console.error('shiftsテーブルエラー:', shiftsError);
          setDebugInfo(prev => prev + `\nshiftsエラー: ${shiftsError.message}`);
          return;
        }
        
        if (shiftsData) {
          const uniqueDates = [...new Set(shiftsData.map(item => item.date))];
          setAvailableDates(uniqueDates);
          setDebugInfo(prev => prev + `\n利用可能日付: ${uniqueDates.length}日`);
        }
        return;
      }

      if (data) {
        const uniqueDates = [...new Set(data.map(item => item.date))];
        setAvailableDates(uniqueDates);
        console.log('利用可能な日付:', uniqueDates);
        setDebugInfo(prev => prev + `\n利用可能日付: ${uniqueDates.length}日`);
      }
    } catch (error) {
      console.error('予期しないエラー:', error);
      setDebugInfo(prev => prev + `\n予期しないエラー: ${error.message}`);
    }
  };

  // ユーザー情報を取得
  const fetchUsers = async () => {
    console.log('fetchUsers開始');
    setDebugInfo(prev => prev + '\nユーザー情報取得中...');
    
    try {
      const { data: users, error } = await supabase
        .from('users')
        .select('*');

      console.log('users取得結果:', { users, error });
      setDebugInfo(prev => prev + `\nusers結果: ${users ? users.length : 0}件`);

      if (error) {
        console.error('ユーザー取得エラー:', error);
        setDebugInfo(prev => prev + `\nusersエラー: ${error.message}`);
        return;
      }

      const userMapTemp = {};
      if (users) {
        users.forEach(user => {
          userMapTemp[String(user.manager_number)] = user.name;
        });
        console.log('ユーザーマップ:', userMapTemp);
      }
      setUserMap(userMapTemp);
      setDebugInfo(prev => prev + `\nユーザーマップ作成完了: ${Object.keys(userMapTemp).length}名`);
    } catch (error) {
      console.error('ユーザー取得予期しないエラー:', error);
      setDebugInfo(prev => prev + `\nユーザー取得エラー: ${error.message}`);
    }
  };

  // 指定日のシフトデータを取得
  const fetchShiftData = async (date) => {
    if (!date) return;

    console.log('fetchShiftData開始:', date);
    setLoading(true);
    setDebugInfo(prev => prev + `\n${date}のシフト取得中...`);
    
    try {
      // まずfinal_shiftsから取得を試行
      const { data, error } = await supabase
        .from('final_shifts')
        .select('*')
        .eq('date', date)
        .order('manager_number');

      console.log('シフトデータ取得結果:', { data, error });
      setDebugInfo(prev => prev + `\nシフトデータ: ${data ? data.length : 0}件`);

      if (error) {
        console.error('シフトデータ取得エラー:', error);
        setDebugInfo(prev => prev + `\nシフトエラー: ${error.message}`);
        
        // エラーの場合、shiftsテーブルで再試行
        console.log('shiftsテーブルでシフトデータ再試行...');
        const { data: shiftsData, error: shiftsError } = await supabase
          .from('shifts')
          .select('*')
          .eq('date', date)
          .order('manager_number');
          
        console.log('shiftsシフトデータ結果:', { shiftsData, shiftsError });
        
        if (shiftsError) {
          console.error('shiftsシフトデータエラー:', shiftsError);
          alert(`シフトデータの取得に失敗しました: ${shiftsError.message}`);
          setDebugInfo(prev => prev + `\nshiftsシフトエラー: ${shiftsError.message}`);
          return;
        }
        
        // shiftsテーブルからのデータを変換してセット
        if (shiftsData) {
          const convertedData = shiftsData.map(shift => ({
            ...shift,
            is_off: !shift.start_time || !shift.end_time
          }));
          setShiftData(convertedData);
          setDebugInfo(prev => prev + `\nshiftsからシフトデータ設定: ${convertedData.length}件`);
        }
        
        setCurrentView('shift');
        return;
      }

      setShiftData(data || []);
      setCurrentView('shift');
      setDebugInfo(prev => prev + `\nシフトデータ設定完了: ${(data || []).length}件`);
    } catch (error) {
      console.error('シフトデータ取得予期しないエラー:', error);
      alert(`エラーが発生しました: ${error.message}`);
      setDebugInfo(prev => prev + `\nシフト取得エラー: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // 日付選択時の処理
  const handleDateSelect = (date) => {
    console.log('日付選択:', date);
    setSelectedDate(date);
    fetchShiftData(date);
  };

  // カレンダーに戻る
  const handleBackToCalendar = () => {
    setCurrentView('calendar');
    setSelectedDate('');
    setShiftData([]);
  };

  // カレンダーの日付生成
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());
    
    const days = [];
    const currentDate = new Date(startDate);
    
    for (let i = 0; i < 42; i++) {
      const dateStr = currentDate.toISOString().split('T')[0];
      const isCurrentMonth = currentDate.getMonth() === month;
      const hasShift = availableDates.includes(dateStr);
      
      days.push({
        date: new Date(currentDate),
        dateStr: dateStr,
        day: currentDate.getDate(),
        isCurrentMonth,
        hasShift
      });
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return days;
  };

  // 月の変更
  const changeMonth = (delta) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + delta);
    setCurrentMonth(newMonth);
  };

  // 時間のフォーマット
  const formatTime = (timeStr) => {
    if (!timeStr) return '';
    return timeStr.slice(0, 5);
  };

  // 曜日の取得
  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  // タイムライン表示用の時間スロット生成
  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 0; hour < 24; hour++) {
      slots.push(`${hour.toString().padStart(2, '0')}:00`);
    }
    return slots;
  };

  // 時間文字列を分に変換
  const timeToMinutes = (timeStr) => {
    if (!timeStr) return 0;
    const [hours, minutes] = timeStr.split(':').map(Number);
    return hours * 60 + minutes;
  };

  // 勤務時間が指定時間に含まれるかチェック
  const isWorkingAtTime = (shift, hour) => {
    if (shift.is_off) return false;
    
    const startMinutes = timeToMinutes(shift.start_time);
    const endMinutes = timeToMinutes(shift.end_time);
    const checkMinutes = hour * 60;
    
    return checkMinutes >= startMinutes && checkMinutes < endMinutes;
  };

  const calendarDays = generateCalendarDays();
  const weekdays = ['日', '月', '火', '水', '木', '金', '土'];
  const timeSlots = generateTimeSlots();

  // カレンダー表示
  if (currentView === 'calendar') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ width: '600px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
          <h2>シフト確認</h2>
          
          {/* デバッグ情報表示 */}
          <details style={{ marginBottom: '1rem', fontSize: '0.8rem', backgroundColor: '#f0f0f0', padding: '0.5rem', borderRadius: '4px' }}>
            <summary>デバッグ情報 (クリックして表示)</summary>
            <pre style={{ whiteSpace: 'pre-wrap', fontSize: '0.7rem' }}>{debugInfo}</pre>
            <p>利用可能日付数: {availableDates.length}</p>
            <p>ユーザー数: {Object.keys(userMap).length}</p>
            <p>利用可能日付: {availableDates.slice(0, 5).join(', ')}{availableDates.length > 5 ? '...' : ''}</p>
          </details>
          
          {/* カレンダー */}
          <div style={{ 
            marginTop: '1rem', 
            border: '1px solid #ddd', 
            borderRadius: '8px',
            padding: '1rem',
            backgroundColor: '#f9f9f9'
          }}>
            {/* 月変更ヘッダー */}
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              marginBottom: '1rem'
            }}>
              <button onClick={() => changeMonth(-1)} style={{ 
                backgroundColor: '#607D8B', 
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ◀
              </button>
              <h3 style={{ margin: 0 }}>
                {currentMonth.getFullYear()}年 {currentMonth.getMonth() + 1}月
              </h3>
              <button onClick={() => changeMonth(1)} style={{ 
                backgroundColor: '#607D8B', 
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ▶
              </button>
            </div>

            {/* 曜日ヘッダー */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(7, 1fr)', 
              gap: '2px',
              marginBottom: '0.5rem'
            }}>
              {weekdays.map(day => (
                <div key={day} style={{ 
                  textAlign: 'center', 
                  fontWeight: 'bold',
                  padding: '0.5rem',
                  backgroundColor: '#e0e0e0'
                }}>
                  {day}
                </div>
              ))}
            </div>

            {/* カレンダー日付 */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(7, 1fr)', 
              gap: '2px'
            }}>
              {calendarDays.map((dayInfo, index) => (
                <button
                  key={index}
                  onClick={() => dayInfo.hasShift && handleDateSelect(dayInfo.dateStr)}
                  disabled={!dayInfo.hasShift}
                  style={{
                    padding: '0.5rem',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: dayInfo.hasShift ? 'pointer' : 'not-allowed',
                    backgroundColor: dayInfo.hasShift ? '#E3F2FD' : 
                                   dayInfo.isCurrentMonth ? 'white' : '#f0f0f0',
                    color: !dayInfo.hasShift ? '#999' :
                           dayInfo.isCurrentMonth ? 'black' : '#666',
                    fontWeight: dayInfo.hasShift ? 'bold' : 'normal',
                    opacity: dayInfo.isCurrentMonth ? 1 : 0.5
                  }}
                >
                  {dayInfo.day}
                </button>
              ))}
            </div>
            
            <div style={{ 
              marginTop: '0.5rem', 
              fontSize: '0.8rem', 
              color: '#666',
              textAlign: 'center'
            }}>
              青色: シフトあり | 灰色: シフトなし
            </div>
          </div>

          {/* 戻るボタン */}
          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <button onClick={onBack} style={{ 
              backgroundColor: '#607D8B',
              color: 'white',
              padding: '0.75rem 2rem',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '1rem'
            }}>
              メニューに戻る
            </button>
          </div>
        </div>
      </div>
    );
  }

  // シフト詳細表示
  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '800px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
        <h2>{selectedDate} ({getWeekday(selectedDate)}) のシフト</h2>
        
        {/* デバッグ情報表示 */}
        <details style={{ marginBottom: '1rem', fontSize: '0.8rem', backgroundColor: '#f0f0f0', padding: '0.5rem', borderRadius: '4px' }}>
          <summary>デバッグ情報</summary>
          <pre style={{ whiteSpace: 'pre-wrap', fontSize: '0.7rem' }}>{debugInfo}</pre>
          <p>シフトデータ数: {shiftData.length}</p>
          <p>ユーザーマップ: {JSON.stringify(userMap, null, 2)}</p>
        </details>
        
        {/* 表示モード切替 */}
        <div style={{ marginBottom: '1rem', display: 'flex', gap: '0.5rem' }}>
          <button
            onClick={() => setViewMode('list')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: viewMode === 'list' ? '#2196F3' : '#f0f0f0',
              color: viewMode === 'list' ? 'white' : 'black',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            リスト表示
          </button>
          <button
            onClick={() => setViewMode('timeline')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: viewMode === 'timeline' ? '#2196F3' : '#f0f0f0',
              color: viewMode === 'timeline' ? 'white' : 'black',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            タイムライン表示
          </button>
        </div>
        
        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            読み込み中...
          </div>
        ) : shiftData.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '2rem',
            color: '#666',
            backgroundColor: '#f9f9f9',
            borderRadius: '8px'
          }}>
            この日のシフトはありません
          </div>
        ) : viewMode === 'list' ? (
          /* リスト表示 */
          <div style={{ 
            border: '1px solid #ddd',
            borderRadius: '8px',
            overflow: 'hidden'
          }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>
                    名前
                  </th>
                  <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                    勤務時間
                  </th>
                  <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                    状態
                  </th>
                </tr>
              </thead>
              <tbody>
                {shiftData.map((shift, index) => (
                  <tr key={shift.manager_number} style={{ 
                    backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                  }}>
                    <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                      <strong>{userMap[shift.manager_number] || `不明(${shift.manager_number})`}</strong>
                    </td>
                    <td style={{ 
                      padding: '0.75rem', 
                      textAlign: 'center',
                      borderBottom: '1px solid #eee'
                    }}>
                      {shift.is_off ? (
                        <span style={{ color: '#999', fontStyle: 'italic' }}>休み</span>
                      ) : (
                        <span style={{ fontWeight: 'bold' }}>
                          {formatTime(shift.start_time)} - {formatTime(shift.end_time)}
                        </span>
                      )}
                    </td>
                    <td style={{ 
                      padding: '0.75rem', 
                      textAlign: 'center',
                      borderBottom: '1px solid #eee'
                    }}>
                      <span style={{ 
                        padding: '0.25rem 0.5rem',
                        borderRadius: '12px',
                        fontSize: '0.8rem',
                        backgroundColor: shift.is_off ? '#f44336' : '#4CAF50',
                        color: 'white'
                      }}>
                        {shift.is_off ? '休み' : '出勤'}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          /* タイムライン表示 */
          <div style={{ 
            border: '1px solid #ddd',
            borderRadius: '8px',
            overflow: 'auto',
            maxHeight: '500px'
          }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', minWidth: '600px' }}>
              <thead style={{ position: 'sticky', top: 0, backgroundColor: '#f5f5f5' }}>
                <tr>
                  <th style={{ 
                    padding: '0.5rem', 
                    textAlign: 'left', 
                    borderBottom: '1px solid #ddd',
                    minWidth: '100px',
                    backgroundColor: '#f5f5f5'
                  }}>
                    時間
                  </th>
                  {shiftData.map(shift => (
                    <th key={shift.manager_number} style={{ 
                      padding: '0.5rem', 
                      textAlign: 'center', 
                      borderBottom: '1px solid #ddd',
                      minWidth: '80px',
                      backgroundColor: '#f5f5f5'
                    }}>
                      {userMap[shift.manager_number] || `不明(${shift.manager_number})`}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {timeSlots.map((timeSlot, index) => (
                  <tr key={timeSlot} style={{ 
                    backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                  }}>
                    <td style={{ 
                      padding: '0.25rem 0.5rem', 
                      borderBottom: '1px solid #eee',
                      fontWeight: 'bold',
                      backgroundColor: index % 2 === 0 ? '#f8f8f8' : '#f0f0f0'
                    }}>
                      {timeSlot}
                    </td>
                    {shiftData.map(shift => (
                      <td key={shift.manager_number} style={{ 
                        padding: '0.25rem', 
                        borderBottom: '1px solid #eee',
                        textAlign: 'center',
                        backgroundColor: isWorkingAtTime(shift, parseInt(timeSlot.split(':')[0])) ? 
                          '#4CAF50' : 
                          shift.is_off ? '#ffebee' : 'transparent'
                      }}>
                        {isWorkingAtTime(shift, parseInt(timeSlot.split(':')[0])) && '●'}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* 戻るボタン */}
        <div style={{ marginTop: '2rem', textAlign: 'center', display: 'flex', gap: '1rem', justifyContent: 'center' }}>
          <button onClick={handleBackToCalendar} style={{ 
            backgroundColor: '#2196F3',
            color: 'white',
            padding: '0.75rem 2rem',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}>
            日付選択に戻る
          </button>
          <button onClick={onBack} style={{ 
            backgroundColor: '#607D8B',
            color: 'white',
            padding: '0.75rem 2rem',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}>
            メニューに戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default StaffShiftView;
