// client/src/App.js
import React, { useState } from 'react';

function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    const res = await fetch('http://localhost:3001/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id, password })
    });

    const data = await res.json();
    if (res.ok) {
      setMessage(`✅ ようこそ ${data.name} さん`);
    } else {
      setMessage(`❌ ${data.message}`);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: '2rem auto' }}>
      <h2>ログイン</h2>
      <form onSubmit={handleLogin}>
        <input type="text" placeholder="ID" value={id} onChange={e => setId(e.target.value)} required />
        <br /><br />
        <input type="password" placeholder="パスワード" value={password} onChange={e => setPassword(e.target.value)} required />
        <br /><br />
        <button type="submit">ログイン</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default App;
