import React, { useState } from 'react';
import './App.css'; // „Çπ„Çø„Ç§„É™„É≥„Ç∞„ÇíÂàÜÈõ¢

function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState('');
  const [message, setMessage] = useState('');
  const [menu, setMenu] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [dates, setDates] = useState([]);

  const handleLogin = async (e) => {
    e.preventDefault();

    const res = await fetch('http://localhost:3001/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id, password }),
    });

    if (res.ok) {
      setIsLoggedIn(true);
      setMessage('');
    } else {
      const data = await res.json();
      setMessage(data.message);
    }
  };

  const selectRole = (selectedRole) => setRole(selectedRole);

  const generateDates = () => {
    if (!startDate || !endDate) return;
    const start = new Date(startDate);
    const end = new Date(endDate);
    const dateArray = [];
    while (start <= end) {
      dateArray.push({
        date: new Date(start).toISOString().split('T')[0],
        startTime: '',
        endTime: '',
      });
      start.setDate(start.getDate() + 1);
    }
    setDates(dateArray);
  };

  if (!isLoggedIn) {
    return (
      <div className="login-wrapper">
        <form className="login-card" onSubmit={handleLogin}>
          <h2>„É≠„Ç∞„Ç§„É≥</h2>
          <input
            type="text"
            placeholder="„É≠„Ç∞„Ç§„É≥ID"
            value={id}
            onChange={e => setId(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="„Éë„Çπ„ÉØ„Éº„Éâ"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          <button type="submit">„É≠„Ç∞„Ç§„É≥</button>
          {message && <p className="error-msg">{message}</p>}
        </form>
      </div>
    );
  }

  if (!role) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>ÂΩπËÅ∑„ÇíÈÅ∏Êäû</h2>
          <div className="button-row">
            <button onClick={() => selectRole('staff')}>üë§ „Ç¢„É´„Éê„Ç§„Éà</button>
            <button onClick={() => selectRole('manager')}>üëë Â∫óÈï∑</button>
          </div>
        </div>
      </div>
    );
  }

  if (role === 'staff' && !menu) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>ÊèêÂá∫ÊñπÊ≥ï„ÇíÈÅ∏Êäû</h2>
          <div className="button-row">
            <button onClick={() => setMenu('Á¢∫Ë™ç')}>Á¢∫Ë™ç</button>
            <button onClick={() => setMenu('Êñ∞Ë¶è')}>Êñ∞Ë¶èÊèêÂá∫</button>
            <button onClick={() => setMenu('Â§âÊõ¥')}>Â§âÊõ¥</button>
          </div>
        </div>
      </div>
    );
  }

  if (role === 'staff' && menu === 'Êñ∞Ë¶è') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>„Ç∑„Éï„ÉàÊñ∞Ë¶èÊèêÂá∫</h2>
          <input
            type="text"
            placeholder="ÁÆ°ÁêÜÁï™Âè∑"
            value={id}
            onChange={e => setId(e.target.value)}
          />
          <input
            type="date"
            value={startDate}
            onChange={e => setStartDate(e.target.value)}
          />
          <input
            type="date"
            value={endDate}
            onChange={e => setEndDate(e.target.value)}
          />
          <button onClick={generateDates}>Ê¨°„Å∏</button>

          {dates.length > 0 && (
            <div>
              <h3>„Ç∑„Éï„ÉàÂÖ•Âäõ</h3>
              {dates.map((entry, index) => (
                <div key={index} style={{ display: 'flex', gap: '1rem', marginBottom: '0.5rem' }}>
                  <span>{entry.date}</span>
                  <input
                    type="time"
                    value={entry.startTime}
                    onChange={e => {
                      const newDates = [...dates];
                      newDates[index].startTime = e.target.value;
                      setDates(newDates);
                    }}
                  />
                  <input
                    type="time"
                    value={entry.endTime}
                    onChange={e => {
                      const newDates = [...dates];
                      newDates[index].endTime = e.target.value;
                      setDates(newDates);
                    }}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>„Çà„ÅÜ„Åì„Åù„ÄÅ{role === 'manager' ? 'Â∫óÈï∑' : '„Ç¢„É´„Éê„Ç§„Éà'}„Åï„ÇìÔºÅ</h2>
      </div>
    </div>
  );
}

export default App;
