import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function StaffShiftView({ onBack }) {
  const [selectedDate, setSelectedDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [userMap, setUserMap] = useState({});
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [availableDates, setAvailableDates] = useState([]);
  const [viewMode, setViewMode] = useState('list');
  const [currentView, setCurrentView] = useState('calendar');

  // 初期化処理
  useEffect(() => {
    fetchAvailableDates();
    fetchUsers();
  }, []);

  // 利用可能な日付を取得（両方のテーブルをチェック）
  const fetchAvailableDates = async () => {
    try {
      let allDates = [];
      
      const { data: finalShifts } = await supabase
        .from('final_shifts')
        .select('date')
        .order('date');

      if (finalShifts?.length > 0) {
        allDates = [...allDates, ...finalShifts.map(item => item.date)];
      }

      const { data: shifts } = await supabase
        .from('shifts')
        .select('date')
        .order('date');

      if (shifts?.length > 0) {
        allDates = [...allDates, ...shifts.map(item => item.date)];
      }

      const uniqueDates = [...new Set(allDates)].sort();
      setAvailableDates(uniqueDates);
    } catch (error) {
      console.error('日付取得エラー:', error);
    }
  };

  // ユーザー情報を取得
  const fetchUsers = async () => {
    try {
      const { data: users, error } = await supabase
        .from('users')
        .select('*');

      if (error) {
        console.error('ユーザー取得エラー:', error);
        return;
      }

      const userMapTemp = {};
      if (users) {
        users.forEach(user => {
          userMapTemp[String(user.manager_number)] = user.name;
        });
      }
      setUserMap(userMapTemp);
    } catch (error) {
      console.error('予期しないエラー:', error);
    }
  };

  // 指定日のシフトデータを取得
  const fetchShiftData = async (date) => {
    if (!date) return;

    setLoading(true);
    try {
      let shiftDataResult = [];
      
      const { data: finalShifts } = await supabase
        .from('final_shifts')
        .select('*')
        .eq('date', date)
        .order('manager_number');

      if (finalShifts?.length > 0) {
        shiftDataResult = finalShifts;
      } else {
        const { data: shifts } = await supabase
          .from('shifts')
          .select('*')
          .eq('date', date)
          .order('manager_number');

        if (shifts?.length > 0) {
          shiftDataResult = shifts.map(shift => ({
            ...shift,
            is_off: !shift.start_time || !shift.end_time || shift.start_time === '' || shift.end_time === ''
          }));
        }
      }

      setShiftData(shiftDataResult || []);
      setCurrentView('shift');
    } catch (error) {
      console.error('シフトデータ取得エラー:', error);
      alert('エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  // 日付選択処理
  const handleDateSelect = (date) => {
    setSelectedDate(date);
    fetchShiftData(date);
  };

  // カレンダーに戻る
  const handleBackToCalendar = () => {
    setCurrentView('calendar');
    setSelectedDate('');
    setShiftData([]);
  };

  // カレンダー日付生成
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());
    
    const days = [];
    const currentDate = new Date(startDate);
    
    for (let i = 0; i < 42; i++) {
      const dateStr = currentDate.toISOString().split('T')[0];
      const isCurrentMonth = currentDate.getMonth() === month;
      const hasShift = availableDates.includes(dateStr);
      
      days.push({
        date: new Date(currentDate),
        dateStr: dateStr,
        day: currentDate.getDate(),
        isCurrentMonth,
        hasShift
      });
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return days;
  };

  const changeMonth = (delta) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + delta);
    setCurrentMonth(newMonth);
  };

  const formatTime = (timeStr) => {
    if (!timeStr) return '';
    return timeStr.slice(0, 5);
  };

  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    return days[new Date(dateStr).getDay()];
  };

  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 0; hour < 24; hour++) {
      slots.push(`${hour.toString().padStart(2, '0')}:00`);
    }
    return slots;
  };

  const timeToMinutes = (timeStr) => {
    if (!timeStr) return 0;
    const [hours, minutes] = timeStr.split(':').map(Number);
    return hours * 60 + minutes;
  };

  const isWorkingAtTime = (shift, hour) => {
    if (isOffDay(shift)) return false;
    const startMinutes = timeToMinutes(shift.start_time);
    const endMinutes = timeToMinutes(shift.end_time);
    const checkMinutes = hour * 60;
    return checkMinutes >= startMinutes && checkMinutes < endMinutes;
  };

  const isOffDay = (shift) => {
    return shift.is_off === true || 
           !shift.start_time || 
           !shift.end_time || 
           shift.start_time === '' || 
           shift.end_time === '' ||
           (shift.start_time === '00:00' && shift.end_time === '00:00');
  };

  const calendarDays = generateCalendarDays();
  const weekdays = ['日', '月', '火', '水', '木', '金', '土'];
  const timeSlots = generateTimeSlots();

  // ===== カレンダー表示 =====
  if (currentView === 'calendar') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ width: '600px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
          <h2>シフト確認</h2>
          
          {/* カレンダー部分 */}
          <div style={{ 
            marginTop: '1rem', 
            border: '1px solid #ddd', 
            borderRadius: '8px',
            padding: '1rem',
            backgroundColor: '#f9f9f9'
          }}>
            {/* 月変更ヘッダー */}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
              <button onClick={() => changeMonth(-1)} style={{ backgroundColor: '#607D8B', color: 'white', border: 'none', borderRadius: '4px', padding: '0.5rem' }}>
                ◀
              </button>
              <h3 style={{ margin: 0 }}>
                {currentMonth.getFullYear()}年 {currentMonth.getMonth() + 1}月
              </h3>
              <button onClick={() => changeMonth(1)} style={{ backgroundColor: '#607D8B', color: 'white', border: 'none', borderRadius: '4px', padding: '0.5rem' }}>
                ▶
              </button>
            </div>

            {/* 曜日 */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', gap: '2px', marginBottom: '0.5rem' }}>
              {weekdays.map(day => (
                <div key={day} style={{ textAlign: 'center', fontWeight: 'bold', padding: '0.5rem', backgroundColor: '#e0e0e0' }}>
                  {day}
                </div>
              ))}
            </div>

            {/* 日付マス */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', gap: '2px' }}>
              {calendarDays.map((dayInfo, index) => (
                <button
                  key={index}
                  onClick={() => dayInfo.hasShift && handleDateSelect(dayInfo.dateStr)}
                  disabled={!dayInfo.hasShift}
                  style={{
                    padding: '0.5rem',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: dayInfo.hasShift ? 'pointer' : 'not-allowed',
                    backgroundColor: dayInfo.hasShift ? '#E3F2FD' : dayInfo.isCurrentMonth ? 'white' : '#f0f0f0',
                    color: !dayInfo.hasShift ? '#999' : dayInfo.isCurrentMonth ? 'black' : '#666',
                    fontWeight: dayInfo.hasShift ? 'bold' : 'normal',
                    opacity: dayInfo.isCurrentMonth ? 1 : 0.5
                  }}
                >
                  {dayInfo.day}
                </button>
              ))}
            </div>
            <div style={{ marginTop: '0.5rem', fontSize: '0.8rem', color: '#666', textAlign: 'center' }}>
              青色: シフトあり | 灰色: シフトなし
            </div>
          </div>

          {/* メニューに戻る */}
          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <button onClick={onBack} style={{ backgroundColor: '#607D8B', color: 'white', padding: '0.75rem 2rem', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
              メニューに戻る
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ===== シフト詳細表示 =====
  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '800px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
        <h2>{selectedDate} ({getWeekday(selectedDate)}) のシフト</h2>
        
        {/* 表示モード切替 */}
        <div style={{ marginBottom: '1rem', display: 'flex', gap: '0.5rem', justifyContent: 'center' }}>
          <button
            onClick={() => setViewMode('list')}
            style={{ padding: '0.5rem 1rem', backgroundColor: viewMode === 'list' ? '#2196F3' : '#f0f0f0', color: viewMode === 'list' ? 'white' : 'black', border: 'none', borderRadius: '4px' }}
          >
            リスト表示
          </button>
          <button
            onClick={() => setViewMode('timeline')}
            style={{ padding: '0.5rem 1rem', backgroundColor: viewMode === 'timeline' ? '#2196F3' : '#f0f0f0', color: viewMode === 'timeline' ? 'white' : 'black', border: 'none', borderRadius: '4px' }}
          >
            タイムライン表示
          </button>
        </div>
        
        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>読み込み中...</div>
        ) : shiftData.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '2rem', color: '#666', backgroundColor: '#f9f9f9', borderRadius: '8px' }}>
            この日のシフトはありません
          </div>
        ) : viewMode === 'list' ? (
          /* ===== リスト表示 ===== */
          <div style={{ border: '1px solid #ddd', borderRadius: '8px', overflow: 'hidden' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '0.75rem', textAlign: 'left' }}>名前</th>
                  <th style={{ padding: '0.75rem', textAlign: 'center' }}>勤務時間</th>
                  <th style={{ padding: '0.75rem', textAlign: 'center' }}>状態</th>
                </tr>
              </thead>
              <tbody>
                {shiftData.map((shift, index) => (
                  <tr key={shift.manager_number || index} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9' }}>
                    <td style={{ padding: '0.75rem' }}>
                      <strong>{userMap[shift.manager_number] || `管理番号: ${shift.manager_number}`}</strong>
                    </td>
                    <td style={{ padding: '0.75rem', textAlign: 'center' }}>
                      {isOffDay(shift) ? <span style={{ color: '#999' }}>休み</span> : `${formatTime(shift.start_time)} - ${formatTime(shift.end_time)}`}
                    </td>
                    <td style={{ padding: '0.75rem', textAlign: 'center' }}>
                      <span style={{ padding: '0.25rem 0.5rem', borderRadius: '12px', fontSize: '0.8rem', backgroundColor: isOffDay(shift) ? '#f44336' : '#4CAF50', color: 'white' }}>
                        {isOffDay(shift) ? '休み' : '出勤'}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          /* ===== タイムライン表示 ===== */
          <div style={{ border: '1px solid #ddd', borderRadius: '8px', overflow: 'auto', maxHeight: '500px' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', minWidth: '600px' }}>
              <thead style={{ position: 'sticky', top: 0, backgroundColor: '#f5f5f5' }}>
                <tr>
                  <th style={{ padding: '0.5rem', textAlign: 'left' }}>時間</th>
                  {shiftData.map((shift, index) => (
                    <th key={shift.manager_number || index} style={{ padding: '0.5rem', textAlign: 'center' }}>
                      {userMap[shift.manager_number] || `管理番号: ${shift.manager_number}`}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {timeSlots.map((timeSlot, index) => (
                  <tr key={timeSlot} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9' }}>
                    <td style={{ padding: '0.25rem 0.5rem', fontWeight: 'bold' }}>{timeSlot}</td>
                    {shiftData.map((shift, shiftIndex) => (
                      <td key={shift.manager_number || shiftIndex} style={{
                        textAlign: 'center',
                        backgroundColor: isWorkingAtTime(shift, parseInt(timeSlot.split(':')[0]))
                          ? '#4CAF50'
                          : isOffDay(shift)
                          ? '#ffebee'
                          : 'transparent'
                      }}>
                        {isWorkingAtTime(shift, parseInt(timeSlot.split(':')[0])) && '●'}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* 戻るボタン */}
        <div style={{ marginTop: '2rem', textAlign: 'center', display: 'flex', gap: '1rem', justifyContent: 'center' }}>
          <button onClick={handleBackToCalendar} style={{ backgroundColor: '#2196F3', color: 'white', padding: '0.75rem 2rem', border: 'none', borderRadius: '4px' }}>
            日付選択に戻る
          </button>
          <button onClick={onBack} style={{ backgroundColor: '#607D8B', color: 'white', padding: '0.75rem 2rem', border: 'none', borderRadius: '4px' }}>
            メニューに戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default StaffShiftView;
