import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function ManagerCreate() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [dates, setDates] = useState([]);
  const [showTable, setShowTable] = useState(false);

  const fetchShiftData = async () => {
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日と終了日を入力してください');
      return;
    }

    const { data: shifts, error: shiftError } = await supabase
      .from('shifts')
      .select('*')
      .gte('date', startDate)
      .lte('date', endDate);

    if (shiftError) {
      console.error(shiftError);
      alert('シフトデータの取得に失敗しました');
      return;
    }

    const { data: users, error: userError } = await supabase
      .from('users')
      .select('*');

    if (userError) {
      console.error(userError);
      alert('ユーザー情報の取得に失敗しました');
      return;
    }

    const userMapTemp = {};
    users.forEach(user => {
      userMapTemp[String(user.manager_number)] = user.name;
    });

    const allDates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      allDates.push(d.toISOString().split('T')[0]);
      d.setDate(d.getDate() + 1);
    }

    setDates(allDates);
    setUserMap(userMapTemp);
    setShiftData(shifts);
    setShowTable(true);
  };

  // 秒を除いて時刻を整形（HH:mm）
  const formatTime = (timeStr) => {
    if (!timeStr) return '';
    // 例: 08:30:00 → 08:30 に
    return timeStr.length >= 5 ? timeStr.slice(0, 5) : timeStr;
  };

  const groupedByUser = {};
  shiftData.forEach(shift => {
    const name = userMap[String(shift.manager_number)] || '(不明)';
    if (!groupedByUser[name]) groupedByUser[name] = {};
    groupedByUser[name][shift.date] = `${formatTime(shift.start_time)} ~ ${formatTime(shift.end_time)}`;
  });

  if (!showTable) {
    return (
      <div style={styles.wrapper}>
        <div style={styles.card}>
          <h2 style={styles.title}>シフト作成</h2>

          <label style={styles.label}>開始日:</label>
          <input
            type="date"
            value={startDate}
            onChange={e => setStartDate(e.target.value)}
            style={styles.input}
          />
          <label style={styles.label}>終了日:</label>
          <input
            type="date"
            value={endDate}
            onChange={e => setEndDate(e.target.value)}
            style={styles.input}
          />

          <div style={{ marginTop: '1rem' }}>
            <button onClick={fetchShiftData} style={styles.button}>次へ</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.wrapper}>
      <div style={{ ...styles.card, overflowX: 'auto' }}>
        <h2 style={styles.title}>シフト表</h2>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>名前</th>
              {dates.map(date => (
                <th key={date} style={styles.th}>{date}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.entries(groupedByUser).map(([name, shifts]) => (
              <tr key={name}>
                <td style={styles.td}>{name}</td>
                {dates.map(date => (
                  <td key={date} style={styles.td}>
                    {shifts[date] || ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

const styles = {
  wrapper: {
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    backgroundColor: '#f7f9fc',
    minHeight: '100vh',
    padding: '2rem',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-start',
  },
  card: {
    backgroundColor: '#fff',
    padding: '2rem',
    borderRadius: '12px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '1200px',
  },
  title: {
    marginBottom: '1.5rem',
    color: '#333',
    textAlign: 'center',
  },
  label: {
    display: 'block',
    marginTop: '1rem',
    marginBottom: '0.3rem',
    fontWeight: '600',
    color: '#555',
  },
  input: {
    width: '100%',
    padding: '0.4rem 0.6rem',
    fontSize: '1rem',
    borderRadius: '6px',
    border: '1px solid #ccc',
    boxSizing: 'border-box',
  },
  button: {
    backgroundColor: '#4a90e2',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    padding: '0.6rem 1.2rem',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background-color 0.25s ease',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    tableLayout: 'auto',
  },
  th: {
    border: '1px solid #ccc',
    padding: '0.7rem 0.8rem',
    backgroundColor: '#f0f4f8',
    color: '#333',
    position: 'sticky',
    top: 0,
    zIndex: 10,
  },
  td: {
    border: '1px solid #ccc',
    padding: '0.5rem 0.7rem',
    textAlign: 'center',
    color: '#333',
    whiteSpace: 'nowrap',
  },
};

export default ManagerCreate;
