import React, { useState, useEffect, useMemo } from 'react';
import { supabase } from './supabaseClient';

// ----------------------------------------------------------------------
// å…±é€šãƒ˜ãƒ«ãƒ‘ãƒ¼é–¢æ•°
// ----------------------------------------------------------------------

/**
 * å‹¤å‹™æ™‚é–“ã‚’è¨ˆç®—ã™ã‚‹é–¢æ•° (åˆ†å˜ä½)
 * HH:MMå½¢å¼ã®æ™‚åˆ»ã‚’åˆ†ã«å¤‰æ›ã—ã€24æ™‚è¶Šãˆã«å¯¾å¿œã€ä¼‘æ†©æ™‚é–“ã‚’å¼•ã„ãŸç´”ç²‹ãªåŠ´åƒæ™‚é–“ã‚’åˆ†ã§è¿”ã™ã€‚
 * @param {string} start - å®Ÿéš›é–‹å§‹æ™‚åˆ» (HH:MM)
 * @param {string} end - å®Ÿéš›çµ‚äº†æ™‚åˆ» (HH:MM)
 * @param {number} breakMinutes - ä¼‘æ†©æ™‚é–“ (åˆ†)
 * @returns {number} ç´”ç²‹ãªåŠ´åƒæ™‚é–“ (åˆ†)
 */
const calculateWorkMinutes = (start, end, breakMinutes) => {
  if (!start || !end) return 0;
  
  // ç§’æ•°ã‚’ç„¡è¦–ã—ã€HH:MMå½¢å¼ã®ã¿ã‚’åˆ†ã«å¤‰æ›
  const formatAsMinutes = (time) => {
    if (!time) return 0;
    const parts = time.split(':').map(Number);
    // æ™‚ * 60 + åˆ†
    return parts[0] * 60 + parts[1]; 
  };

  let startMinutes = formatAsMinutes(start);
  let endMinutes = formatAsMinutes(end);
  let breakMins = breakMinutes || 0;

  // çµ‚äº†æ™‚åˆ»ãŒé–‹å§‹æ™‚åˆ»ã‚ˆã‚Šå‰ã®å ´åˆï¼ˆæ—¥ã‚’è·¨ã„ã å ´åˆï¼‰
  if (endMinutes < startMinutes) {
    endMinutes += 24 * 60; // 1æ—¥åˆ†(1440åˆ†)åŠ ç®—
  }

  const workMinutes = endMinutes - startMinutes;
  
  // ä¼‘æ†©æ™‚é–“ã‚’å¼•ã
  return Math.max(0, workMinutes - breakMins);
};

/**
 * åˆ†ã‚’ã€ŒHæ™‚é–“Måˆ†ã€ã«å¤‰æ›ã™ã‚‹
 * @param {number} totalMinutes - ç·åˆ†æ•°
 * @returns {string} ãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆã•ã‚ŒãŸæ™‚é–“æ–‡å­—åˆ—
 */
const formatMinutes = (totalMinutes) => {
    const hours = Math.floor(totalMinutes / 60);
    const minutes = totalMinutes % 60;
    if (totalMinutes < 0) return '0æ™‚é–“0åˆ†'; 
    return `${hours}æ™‚é–“${minutes}åˆ†`;
};


// ----------------------------------------------------------------------
// SummaryView (å‹¤å‹™æ™‚é–“é›†è¨ˆãƒ¢ãƒ¼ãƒ‰ã®ã‚µãƒ–ã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆ)
// ----------------------------------------------------------------------

const SummaryView = ({ userMap, availableDates, onBackToCalendar }) => {
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [filter, setFilter] = useState('monthly'); // 'monthly', 'weekly', 'daily'
  // åˆæœŸå€¤ã¯ç¾åœ¨ã®æ—¥ä»˜ã§è¨­å®š
  const [selectedPeriod, setSelectedPeriod] = useState(new Date().toISOString().split('T')[0].substring(0, 7)); 
  const [weekdayFilter, setWeekdayFilter] = useState(''); 
  const [timeFilter, setTimeFilter] = useState(''); 
  const [selectedUser, setSelectedUser] = useState(''); 
  const [loading, setLoading] = useState(true);

  const daysOfWeek = ['æ—¥', 'æœˆ', 'ç«', 'æ°´', 'æœ¨', 'é‡‘', 'åœŸ'];
  const timePresets = [
    { label: 'ã™ã¹ã¦', value: '' },
    { label: 'åˆå‰ (00:00-11:59)', value: 'morning' },
    { label: 'åˆå¾Œ (12:00-17:59)', value: 'afternoon' },
    { label: 'å¤œé–“ (18:00-23:59)', value: 'night' },
  ];

  useEffect(() => {
    fetchAllAttendanceRecords();
  }, []);

  const userList = useMemo(() => {
    const users = Object.entries(userMap).map(([manager_number, name]) => ({ manager_number: String(manager_number), name }));
    users.sort((a, b) => a.name.localeCompare(b.name));
    return [{ manager_number: '', name: 'å…¨å¾“æ¥­å“¡' }, ...users];
  }, [userMap]);


  const fetchAllAttendanceRecords = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('attendance')
        .select('*')
        .not('work_minutes', 'is', null) 
        .order('date', { ascending: false });

      if (error) {
        console.error('é›†è¨ˆãƒ‡ãƒ¼ã‚¿å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
        alert('é›†è¨ˆãƒ‡ãƒ¼ã‚¿ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ');
        return;
      }
      setAttendanceRecords(data || []);
    } catch (error) {
      console.error('ãƒ‡ãƒ¼ã‚¿å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    } finally {
      setLoading(false);
    }
  };

  const aggregatedData = useMemo(() => {
    let recordsToAggregate = attendanceRecords.filter(record => record.work_minutes > 0);

    // 1. ãƒ¦ãƒ¼ã‚¶ãƒ¼ãƒ•ã‚£ãƒ«ã‚¿
    if (selectedUser) {
        recordsToAggregate = recordsToAggregate.filter(record => 
            String(record.manager_number) === String(selectedUser)
        );
    }

    // 2. æœŸé–“ãƒ•ã‚£ãƒ«ã‚¿ (æœˆåˆ¥/æ—¥åˆ¥)
    recordsToAggregate = recordsToAggregate.filter(record => {
      const dateStr = record.date;
      
      switch (filter) {
        case 'monthly':
        case 'weekly':
          return dateStr.startsWith(selectedPeriod);
        case 'daily':
          return dateStr === selectedPeriod;
        default:
          return true;
      }
    });

    // 3. æ›œæ—¥ãƒ»æ™‚é–“å¸¯ãƒ•ã‚£ãƒ«ã‚¿ãƒªãƒ³ã‚°
    const filteredData = recordsToAggregate.filter(record => {
      // æ›œæ—¥ãƒ•ã‚£ãƒ«ã‚¿
      if (weekdayFilter) {
        const dayIndex = new Date(record.date).getDay();
        if (daysOfWeek[dayIndex] !== weekdayFilter) return false;
      }
      
      // æ™‚é–“å¸¯ãƒ•ã‚£ãƒ«ã‚¿ (å®Ÿéš›ã®å‹¤å‹™æ™‚é–“å¸¯ãŒãƒ•ã‚£ãƒ«ã‚¿ã®æ™‚é–“å¸¯ã¨é‡è¤‡ã—ã¦ã„ã‚‹ã‹ãƒã‚§ãƒƒã‚¯)
      if (timeFilter) {
        if (!record.actual_start || !record.actual_end) return false;

        let startMinutes = parseInt(record.actual_start.split(':')[0]) * 60 + parseInt(record.actual_start.split(':')[1]);
        let endMinutes = parseInt(record.actual_end.split(':')[0]) * 60 + parseInt(record.actual_end.split(':')[1]);
        if (endMinutes < startMinutes) endMinutes += 24 * 60; 
        
        let hasOverlap = false;

        switch (timeFilter) {
          case 'morning': // 0åˆ† - 719åˆ† (00:00 - 11:59)
            hasOverlap = (startMinutes < 720 && endMinutes > 0);
            break;
          case 'afternoon': // 720åˆ† - 1079åˆ† (12:00 - 17:59)
            hasOverlap = (startMinutes < 1080 && endMinutes > 720);
            break;
          case 'night': // 1080åˆ† - 1439åˆ† (18:00 - 23:59)
            hasOverlap = (startMinutes < 1440 && endMinutes > 1080);
            break;
        }
        
        if (!hasOverlap) return false;
      }
      
      return true;
    });

    // 4. å€‹äººã”ã¨ã®é›†è¨ˆ
    const totals = {};
    filteredData.forEach(record => {
      const managerNumber = record.manager_number;
      const minutes = record.work_minutes; 
      
      if (!totals[managerNumber]) {
        totals[managerNumber] = { 
          manager_number: managerNumber,
          name: userMap[managerNumber] || `ç®¡ç†ç•ªå·: ${managerNumber}`,
          totalMinutes: 0
        };
      }
      totals[managerNumber].totalMinutes += minutes;
    });

    // çµæœã‚’é…åˆ—ã«å¤‰æ›ã—ã€å‹¤å‹™æ™‚é–“ãŒå¤šã„é †ã«ã‚½ãƒ¼ãƒˆ
    return Object.values(totals).sort((a, b) => b.totalMinutes - a.totalMinutes);

  }, [attendanceRecords, filter, selectedPeriod, weekdayFilter, timeFilter, selectedUser, userMap]);

  
  // æœŸé–“é¸æŠè¦ç´ ã®ç”Ÿæˆ
  const renderPeriodSelector = () => {
    // åˆ©ç”¨å¯èƒ½ãªæ—¥ä»˜ã‹ã‚‰YYYY-MMå½¢å¼ã®ãƒªã‚¹ãƒˆã‚’ä½œæˆ
    const monthList = [...new Set(availableDates.map(d => d.substring(0, 7)))].sort((a, b) => b.localeCompare(a));
    const dayList = availableDates.sort((a, b) => b.localeCompare(a));
    
    switch(filter) {
      case 'monthly':
      case 'weekly':
        return (
          <select 
            value={selectedPeriod} 
            onChange={(e) => setSelectedPeriod(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            {monthList.map(month => (
              <option key={month} value={month}>{month.replace('-', 'å¹´')}æœˆ</option>
            ))}
          </select>
        );
      case 'daily':
        return (
          <select 
            value={selectedPeriod} 
            onChange={(e) => setSelectedPeriod(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            {dayList.map(day => (
              <option key={day} value={day}>{day}</option>
            ))}
          </select>
        );
      default:
        return null;
    }
  };

  return (
    <div className="login-card" style={{ width: '900px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
      <h2>å‹¤å‹™æ™‚é–“é›†è¨ˆ</h2>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', marginBottom: '1.5rem', padding: '1rem', border: '1px solid #eee', borderRadius: '8px', backgroundColor: '#fff' }}>
        
        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>å¾“æ¥­å“¡é¸æŠ:</label>
          <select 
            value={selectedUser} 
            onChange={(e) => setSelectedUser(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            {userList.map(user => (
              <option key={user.manager_number} value={user.manager_number}>{user.name}</option>
            ))}
          </select>
        </div>
        
        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>æœŸé–“é¸æŠ:</label>
          <select 
            value={filter} 
            onChange={(e) => {
              const newFilter = e.target.value;
              setFilter(newFilter);
              // ãƒ•ã‚£ãƒ«ã‚¿ãƒ¼å¤‰æ›´æ™‚ã«é¸æŠæœŸé–“ã‚‚ãƒªã‚»ãƒƒãƒˆ/èª¿æ•´
              if (newFilter === 'monthly' || newFilter === 'weekly') {
                setSelectedPeriod(new Date().toISOString().split('T')[0].substring(0, 7)); 
              } else if (newFilter === 'daily') {
                setSelectedPeriod(new Date().toISOString().split('T')[0]); 
              }
            }}
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            <option value="monthly">æœˆåˆ¥é›†è¨ˆ</option>
            <option value="weekly">é€±åˆ¥é›†è¨ˆ (æœˆå˜ä½ã§é¸æŠ)</option>
            <option value="daily">æ—¥åˆ¥é›†è¨ˆ</option>
          </select>
        </div>
        
        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>å¯¾è±¡æœŸé–“:</label>
          {renderPeriodSelector()}
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>æ›œæ—¥ãƒ•ã‚£ãƒ«ã‚¿:</label>
          <select 
            value={weekdayFilter} 
            onChange={(e) => setWeekdayFilter(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            <option value="">ã™ã¹ã¦</option>
            {daysOfWeek.map(day => <option key={day} value={day}>{day}æ›œæ—¥</option>)}
          </select>
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
