  if (end < start) {
    end += 24 * 60;
  }
  const overlapStart = Math.max(start, periodStartMinutes);
  const overlapEnd = Math.min(end, periodEndMinutes);
  if (overlapEnd <= overlapStart) {
    return 0;
  }
