import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function ManagerCreate() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [dates, setDates] = useState([]);
  const [showTable, setShowTable] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');
  const [editingShifts, setEditingShifts] = useState([]);

  const fetchShiftData = async () => {
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日と終了日を入力してください');
      return;
    }

    const { data: shifts, error: shiftError } = await supabase
      .from('shifts')
      .select('*')
      .gte('date', startDate)
      .lte('date', endDate);

    if (shiftError) {
      console.error(shiftError);
      alert('シフトデータの取得に失敗しました');
      return;
    }

    const { data: users, error: userError } = await supabase
      .from('users')
      .select('*');

    if (userError) {
      console.error(userError);
      alert('ユーザー情報の取得に失敗しました');
      return;
    }

    const userManagerNumbers = new Set(users.map(user => String(user.manager_number)));
    const userMapTemp = {};
    users.forEach(user => {
      userMapTemp[String(user.manager_number)] = user.name;
    });

    const filteredShifts = shifts.filter(shift => userManagerNumbers.has(String(shift.manager_number)));

    const allDates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      allDates.push(d.toISOString().split('T')[0]);
      d.setDate(d.getDate() + 1);
    }

    setDates(allDates);
    setUserMap(userMapTemp);
    setShiftData(filteredShifts);
    setShowTable(true);
    setSelectedDate(allDates[0]);
  };

  const getUsersByDate = (date) => {
    const users = [];
    const grouped = {};
    shiftData.forEach(shift => {
      if (shift.date === date && (shift.start_time !== '00:00' || shift.end_time !== '00:00')) {
        const name = userMap[String(shift.manager_number)] || '(不明)';
        grouped[name] = shift;
      }
    });
    return grouped;
  };

  const renderTimeBlocks = (start, end, color) => {
    const blocks = [];
    const startMin = parseInt(start.split(':')[0]) * 60 + parseInt(start.split(':')[1]);
    const endMin = parseInt(end.split(':')[0]) * 60 + parseInt(end.split(':')[1]);
    for (let i = 0; i < 96; i++) {
      const min = i * 15;
      const inRange = min >= startMin && min < endMin;
      blocks.push(
        <td
          key={i}
          style={{
            width: '5px',
            height: '20px',
            backgroundColor: inRange ? color : 'transparent'
          }}
        ></td>
      );
    }
    return blocks;
  };

  if (!showTable) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>シフト作成</h2>
          <label>開始日:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>終了日:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <div style={{ marginTop: '1rem' }}>
            <button onClick={fetchShiftData}>次へ</button>
          </div>
        </div>
      </div>
    );
  }

  const shiftsForSelected = getUsersByDate(selectedDate);

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>シフト表</h2>
        <div className="shift-table-wrapper">
          <table>
            <thead>
              <tr>
                <th>名前</th>
                <th>開始</th>
                <th>終了</th>
                <th colSpan="96">15分単位</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(shiftsForSelected).map(([name, shift]) => (
                <tr key={name}>
                  <td>{name}</td>
                  <td><input type="time" defaultValue={shift.start_time} /></td>
                  <td><input type="time" defaultValue={shift.end_time} /></td>
                  {renderTimeBlocks(shift.start_time, shift.end_time, '#f9e79f')}
                </tr>
              ))}
            </tbody>
          </table>
          <div style={{ marginTop: '1rem' }}>
            <label>日付選択：</label>
            <select value={selectedDate} onChange={e => setSelectedDate(e.target.value)}>
              {dates.map(date => <option key={date} value={date}>{date}</option>)}
            </select>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ManagerCreate;
