import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function ManagerCreate() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [dates, setDates] = useState([]);
  const [showTable, setShowTable] = useState(false); // 表示切り替えフラグ

  const fetchShiftData = async () => {
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日と終了日を入力してください');
      return;
    }

    // shifts取得
    const { data: shifts, error: shiftError } = await supabase
      .from('shifts')
      .select('*')
      .gte('date', startDate)
      .lte('date', endDate);

    if (shiftError) {
      console.error(shiftError);
      alert('シフトデータの取得に失敗しました');
      return;
    }

    // users取得
    const { data: users, error: userError } = await supabase
      .from('users')
      .select('*');

    if (userError) {
      console.error(userError);
      alert('ユーザー情報の取得に失敗しました');
      return;
    }

    // ユーザーの管理番号一覧をSetで作成（存在確認用）
    const userManagerNumbers = new Set(users.map(user => String(user.manager_number)));

    // userMapを作成（manager_number→name）
    const userMapTemp = {};
    users.forEach(user => {
      userMapTemp[String(user.manager_number)] = user.name;
    });

    // shiftsのうち、usersに存在しない管理番号のものを除外する
    const filteredShifts = shifts.filter(shift => userManagerNumbers.has(String(shift.manager_number)));

    // 全日付を生成
    const allDates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      allDates.push(d.toISOString().split('T')[0]);
      d.setDate(d.getDate() + 1);
    }

    setDates(allDates);
    setUserMap(userMapTemp);
    setShiftData(filteredShifts);  // フィルター済みのシフトをセット

    setShowTable(true); // シフト表表示へ切り替え
  };

  const groupedByUser = {};
  shiftData.forEach(shift => {
    const name = userMap[String(shift.manager_number)] || '(不明)';
    if (!groupedByUser[name]) groupedByUser[name] = {};
    groupedByUser[name][shift.date] = `${shift.start_time || ''} ~ ${shift.end_time || ''}`;
  });

  if (!showTable) {
    // 入力画面
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>シフト作成</h2>

          <label>開始日:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>終了日:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />

          <div style={{ marginTop: '1rem' }}>
            <button onClick={fetchShiftData}>次へ</button>
          </div>
        </div>
      </div>
    );
  }

  // シフト表表示画面
  return (
  <div
    className="login-wrapper"
    style={{
      width: '100vw',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '1rem',
      boxSizing: 'border-box',
      background: '#f0f4f8',
    }}
  >
    <div
      className="login-card"
      style={{
        width: '100%',
        maxWidth: '100%',
        height: '90vh',
        overflowX: 'auto',
        background: '#fff',
        borderRadius: '10px',
        padding: '1rem',
        boxSizing: 'border-box',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      <h2 style={{ marginBottom: '1rem' }}>シフト表</h2>
      <div style={{ flexGrow: 1, overflow: 'auto' }}>
        <table
          style={{
            borderCollapse: 'collapse',
            width: '100%',
            minWidth: '100%',
            tableLayout: 'fixed',
          }}
        >
          <thead>
            <tr>
              <th
                style={{
                  border: '1px solid #ccc',
                  padding: '0.5rem',
                  minWidth: '120px',
                  background: '#fafafa',
                }}
              >
                名前
              </th>
              {dates.map(date => (
                <th
                  key={date}
                  style={{
                    border: '1px solid #ccc',
                    padding: '0.5rem',
                    minWidth: '90px',
                    background: '#fafafa',
                    whiteSpace: 'nowrap',
                  }}
                >
                  {date}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.entries(groupedByUser).map(([name, shifts]) => (
              <tr key={name}>
                <td
                  style={{
                    border: '1px solid #ccc',
                    padding: '0.5rem',
                    minWidth: '120px',
                    wordBreak: 'break-word',
                    background: '#fff',
                    fontWeight: 'bold',
                  }}
                >
                  {name}
                </td>
                {dates.map(date => (
                  <td
                    key={date}
                    style={{
                      border: '1px solid #ccc',
                      padding: '0.5rem',
                      minWidth: '90px',
                      whiteSpace: 'nowrap',
                      background: '#fff',
                      textAlign: 'center',
                    }}
                  >
                    {shifts[date] || ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  </div>
);
}
export default ManagerCreate;
