import React, { useState, useEffect } from 'react';

function StaffWorkHours({ onBack }) {
  const [managerNumber, setManagerNumber] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [workData, setWorkData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear().toString());
  const [selectedMonth, setSelectedMonth] = useState((new Date().getMonth() + 1).toString());
  const [viewMode, setViewMode] = useState('monthly');
  const [showTimeSettings, setShowTimeSettings] = useState(false);
  const [timeSlots, setTimeSlots] = useState([
    { name: '朝', start: '06:00', end: '12:00' },
    { name: '昼', start: '12:00', end: '17:00' },
    { name: '夕', start: '17:00', end: '22:00' },
    { name: '夜', start: '22:00', end: '06:00' }
  ]);
  const [newSlotName, setNewSlotName] = useState('');
  const [newSlotStart, setNewSlotStart] = useState('');
  const [newSlotEnd, setNewSlotEnd] = useState('');
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      fetchWorkData(managerNumber, selectedYear, selectedMonth);
    }
  }, [selectedYear, selectedMonth, isAuthenticated, managerNumber]);

  const handleAuthentication = async () => {
    if (!managerNumber) {
      setMessage('管理番号を入力してください');
      return;
    }
    setLoading(true);
    try {
      setUserName('山田太郎');
      setIsAuthenticated(true);
      setMessage('');
      fetchWorkData(managerNumber, selectedYear, selectedMonth);
    } catch (error) {
      setMessage('エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  const fetchWorkData = async () => {
    setLoading(true);
    try {
      const demoData = [
        { id: 1, date: '2025-10-05', store: 'A', actual_start: '09:00', actual_end: '18:00', break_minutes: 60, work_minutes: 540, salary: 8100 },
        { id: 2, date: '2025-10-06', store: 'B', actual_start: '10:00', actual_end: '19:00', break_minutes: 60, work_minutes: 480, salary: 7200 },
        { id: 3, date: '2025-10-07', store: 'A', actual_start: '06:30', actual_end: '13:30', break_minutes: 30, work_minutes: 420, salary: 6300 },
        { id: 4, date: '2025-10-08', store: 'C', actual_start: '22:00', actual_end: '06:00', break_minutes: 60, work_minutes: 480, salary: 7680 },
      ];
      setWorkData(demoData);
      setMessage('');
    } catch (error) {
      setMessage('データの取得に失敗しました');
      setWorkData([]);
    } finally {
      setLoading(false);
    }
  };

  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    return days[new Date(dateStr).getDay()];
  };

  const formatTime = (timeStr) => timeStr ? timeStr.slice(0, 5) : '-';

  const calculateTotalStats = () => {
    let totalMinutes = 0, totalSalary = 0, workDays = 0;
    workData.forEach(record => {
      if (record.work_minutes) {
        totalMinutes += record.work_minutes;
        workDays++;
      }
      if (record.salary) totalSalary += record.salary;
    });
    return {
      totalHours: Math.floor(totalMinutes / 60),
      totalMinutes: totalMinutes % 60,
      totalSalary,
      workDays
    };
  };

  const addTimeSlot = () => {
    if (!newSlotName || !newSlotStart || !newSlotEnd) {
      alert('すべての項目を入力してください');
      return;
    }
    setTimeSlots([...timeSlots, { name: newSlotName, start: newSlotStart, end: newSlotEnd }]);
    setNewSlotName('');
    setNewSlotStart('');
    setNewSlotEnd('');
  };

  const deleteTimeSlot = (index) => {
    setTimeSlots(timeSlots.filter((_, i) => i !== index));
  };

  const years = Array.from({ length: 10 }, (_, i) => new Date().getFullYear() - i);
  const months = Array.from({ length: 12 }, (_, i) => (i + 1).toString());

  if (!isAuthenticated) {
    return (
      <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: isMobile ? '1rem' : '2rem', backgroundColor: '#f5f5f5' }}>
        <div style={{ backgroundColor: 'white', padding: isMobile ? '1.5rem' : '2rem', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', width: '100%', maxWidth: '400px' }}>
          <h2 style={{ marginTop: 0, textAlign: 'center', fontSize: isMobile ? '1.3rem' : '1.5rem' }}>就労時間確認</h2>
          <p style={{ fontSize: '0.9rem', color: '#666', marginBottom: '1.5rem', textAlign: 'center' }}>自分の勤務記録を確認できます</p>
          
          <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '500' }}>管理番号:</label>
          <input
            type="text"
            value={managerNumber}
            onChange={(e) => setManagerNumber(e.target.value)}
            placeholder="管理番号を入力"
            style={{ padding: '0.75rem', border: '1px solid #ddd', borderRadius: '4px', width: '100%', boxSizing: 'border-box', fontSize: '1rem' }}
          />

          <button 
            onClick={handleAuthentication} 
            disabled={loading}
            style={{ width: '100%', padding: '0.75rem', marginTop: '1rem', backgroundColor: '#1976D2', color: 'white', border: 'none', borderRadius: '4px', fontSize: '1rem', fontWeight: 'bold', cursor: loading ? 'not-allowed' : 'pointer', opacity: loading ? 0.6 : 1 }}
          >
            {loading ? '確認中...' : '確認'}
          </button>

          {message && <p style={{ color: message.includes('成功') || message.includes('確認') ? 'green' : 'red', marginTop: '1rem', fontSize: '0.9rem', textAlign: 'center' }}>{message}</p>}

          <div style={{ marginTop: '2rem' }}>
            <button onClick={onBack} style={{ width: '100%', backgroundColor: '#607D8B', color: 'white', padding: '0.75rem', border: 'none', borderRadius: '4px', cursor: 'pointer', fontSize: '1rem' }}>
              メニューに戻る
            </button>
          </div>
        </div>
      </div>
    );
  }

  const stats = calculateTotalStats();

  return (
    <div style={{ minHeight: '100vh', padding: isMobile ? '1rem' : '1.5rem', backgroundColor: '#f5f5f5' }}>
      <div style={{ backgroundColor: 'white', borderRadius: '8px', padding: isMobile ? '1rem' : '1.5rem', maxWidth: '1200px', margin: '0 auto' }}>
        {/* ヘッダー */}
        <div style={{ display: 'flex', flexDirection: isMobile ? 'column' : 'row', alignItems: isMobile ? 'flex-start' : 'center', justifyContent: 'space-between', marginBottom: '1rem', gap: '1rem' }}>
          <h2 style={{ margin: 0, fontSize: isMobile ? '1.3rem' : '1.5rem' }}>就労時間確認</h2>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', fontSize: '0.9rem' }}>
            <label style={{ fontWeight: 'bold' }}>年度:</label>
            <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)} style={{ padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}>
              {years.map(year => <option key={year} value={year}>{year}年</option>)}
            </select>
          </div>
        </div>
        
        <p style={{ fontSize: '0.85rem', margin: '0 0 1rem 0', color: '#555' }}>
          管理番号: <strong>{managerNumber}</strong> | 名前: <strong>{userName}</strong>
        </p>

        {/* 月選択 */}
        <div style={{ backgroundColor: '#f9f9f9', padding: isMobile ? '0.75rem' : '1rem', borderRadius: '8px', marginBottom: '1rem', display: 'flex', flexDirection: isMobile ? 'column' : 'row', gap: '0.75rem', alignItems: isMobile ? 'stretch' : 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', flex: isMobile ? 1 : 0 }}>
            <label style={{ fontWeight: 'bold', fontSize: '0.9rem', whiteSpace: 'nowrap' }}>月:</label>
            <select value={selectedMonth} onChange={(e) => setSelectedMonth(e.target.value)} style={{ padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px', flex: 1 }}>
              {months.map(month => <option key={month} value={month}>{month}月</option>)}
            </select>
          </div>
          
          <button
            onClick={() => setShowTimeSettings(!showTimeSettings)}
            style={{ backgroundColor: '#FF9800', color: 'white', padding: '0.5rem 1rem', border: 'none', borderRadius: '4px', cursor: 'pointer', fontSize: '0.85rem', fontWeight: 'bold', whiteSpace: 'nowrap' }}
          >
            ⚙️ 時間帯設定
          </button>
        </div>

        {/* 時間帯設定パネル */}
        {showTimeSettings && (
          <div style={{ backgroundColor: '#fff', border: '2px solid #FF9800', borderRadius: '8px', padding: isMobile ? '0.75rem' : '1rem', marginBottom: '1rem' }}>
            <h3 style={{ marginTop: 0, color: '#FF9800', fontSize: '1rem' }}>時間帯設定</h3>
            
            <div style={{ marginBottom: '1rem' }}>
              {timeSlots.map((slot, index) => (
                <div key={index} style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem', padding: '0.5rem', backgroundColor: '#f9f9f9', borderRadius: '4px', fontSize: '0.85rem', flexWrap: 'wrap' }}>
                  <strong style={{ minWidth: '50px' }}>{slot.name}</strong>
                  <span>{slot.start} - {slot.end}</span>
                  <button onClick={() => deleteTimeSlot(index)} style={{ marginLeft: 'auto', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '4px', padding: '0.25rem 0.5rem', cursor: 'pointer', fontSize: '0.75rem', whiteSpace: 'nowrap' }}>
                    削除
                  </button>
                </div>
              ))}
            </div>

            <div style={{ display: 'flex', flexDirection: isMobile ? 'column' : 'row', gap: '0.5rem', padding: '0.75rem', backgroundColor: '#E3F2FD', borderRadius: '4px' }}>
              <input type="text" placeholder="名前" value={newSlotName} onChange={(e) => setNewSlotName(e.target.value)} style={{ padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px', flex: 1, fontSize: '0.9rem' }} />
              <input type="time" value={newSlotStart} onChange={(e) => setNewSlotStart(e.target.value)} style={{ padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px', flex: 1, fontSize: '0.9rem' }} />
              <span style={{ display: 'flex', alignItems: 'center' }}>-</span>
              <input type="time" value={newSlotEnd} onChange={(e) => setNewSlotEnd(e.target.value)} style={{ padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px', flex: 1, fontSize: '0.9rem' }} />
              <button onClick={addTimeSlot} style={{ backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', padding: '0.5rem 1rem', cursor: 'pointer', fontWeight: 'bold', whiteSpace: 'nowrap' }}>追加</button>
            </div>

            <button onClick={() => setShowTimeSettings(false)} style={{ marginTop: '1rem', backgroundColor: '#607D8B', color: 'white', border: 'none', borderRadius: '4px', padding: '0.5rem 1rem', cursor: 'pointer', width: '100%' }}>
              閉じる
            </button>
          </div>
        )}

        {/* ビューモード選択 */}
        <div style={{ backgroundColor: '#E3F2FD', padding: isMobile ? '0.5rem' : '0.75rem', borderRadius: '8px', marginBottom: '1rem', display: 'flex', flexWrap: 'wrap', gap: '0.3rem', justifyContent: 'center' }}>
          {['monthly', 'weekly', 'daily', 'time', 'all'].map(mode => {
            const labels = { monthly: '月別', weekly: '週別', daily: '日別', time: '時間帯別', all: '全表示' };
            return (
              <button key={mode} onClick={() => setViewMode(mode)} style={{
                backgroundColor: viewMode === mode ? '#1976D2' : 'white',
                color: viewMode === mode ? 'white' : '#1976D2',
                padding: isMobile ? '0.35rem 0.6rem' : '0.5rem 0.75rem',
                border: '2px solid #1976D2',
                borderRadius: '4px',
                cursor: 'pointer',
                fontWeight: 'bold',
                fontSize: isMobile ? '0.7rem' : '0.8rem',
                whiteSpace: 'nowrap'
              }}>
                {labels[mode]}
              </button>
            );
          })}
        </div>

        {/* 統計情報 */}
        <div style={{ backgroundColor: '#E8F5E9', padding: isMobile ? '0.75rem' : '1rem', borderRadius: '8px', marginBottom: '1rem', display: 'grid', gridTemplateColumns: isMobile ? 'repeat(2, 1fr)' : 'repeat(auto-fit, minmax(150px, 1fr))', gap: isMobile ? '0.5rem' : '1rem' }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '0.8rem', color: '#666' }}>勤務日数</div>
            <div style={{ fontSize: isMobile ? '1.1rem' : '1.3rem', fontWeight: 'bold', color: '#1976D2' }}>{stats.workDays}日</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '0.8rem', color: '#666' }}>総労働時間</div>
            <div style={{ fontSize: isMobile ? '0.95rem' : '1.3rem', fontWeight: 'bold', color: '#1976D2' }}>{stats.totalHours}h{stats.totalMinutes}m</div>
          </div>
          {stats.totalSalary > 0 && (
            <div style={{ textAlign: 'center', gridColumn: isMobile ? '1 / -1' : 'auto' }}>
              <div style={{ fontSize: '0.8rem', color: '#666' }}>総給料</div>
              <div style={{ fontSize: isMobile ? '1rem' : '1.3rem', fontWeight: 'bold', color: '#4CAF50' }}>¥{stats.totalSalary.toLocaleString()}</div>
            </div>
          )}
        </div>

        {/* データ表示 */}
        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem', color: '#666' }}>読み込み中...</div>
        ) : workData.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '2rem', color: '#666', backgroundColor: '#f9f9f9', borderRadius: '8px' }}>
            {message || '勤怠データがありません'}
          </div>
        ) : isMobile ? (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            {workData.map((record) => {
              const hours = Math.floor(record.work_minutes / 60);
              const minutes = record.work_minutes % 60;
              return (
                <div key={record.id} style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '1rem', backgroundColor: '#fafafa' }}>
                  <div style={{ marginBottom: '0.75rem', fontWeight: 'bold', fontSize: '0.95rem', color: '#333' }}>
                    {record.date} ({getWeekday(record.date)})
                  </div>
                  <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '0.75rem', fontSize: '0.85rem' }}>
                    <div><span style={{ color: '#666', fontWeight: '500' }}>店舗:</span><br/><span style={{ fontWeight: 'bold', color: '#1976D2' }}>{record.store}店舗</span></div>
                    <div><span style={{ color: '#666', fontWeight: '500' }}>開始:</span><br/>{formatTime(record.actual_start)}</div>
                    <div><span style={{ color: '#666', fontWeight: '500' }}>終了:</span><br/>{formatTime(record.actual_end)}</div>
                    <div><span style={{ color: '#666', fontWeight: '500' }}>休憩:</span><br/>{record.break_minutes || 0}分</div>
                    <div style={{ gridColumn: '1 / -1', paddingTop: '0.5rem', borderTop: '1px solid #ddd', fontWeight: 'bold', color: '#1976D2' }}>
                      労働時間: {hours}h{minutes}m
                    </div>
                    {record.salary && (
                      <div style={{ gridColumn: '1 / -1', fontWeight: 'bold', color: '#4CAF50' }}>
                        給料: ¥{record.salary.toLocaleString()}
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div style={{ border: '1px solid #ddd', borderRadius: '8px', overflow: 'auto', maxHeight: '500px' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', minWidth: '600px', fontSize: '0.825rem' }}>
              <thead style={{ position: 'sticky', top: 0, backgroundColor: '#f5f5f5' }}>
                <tr>
                  <th style={{ padding: '0.75rem 0.5rem', textAlign: 'left', borderBottom: '2px solid #ddd', fontSize: '0.8rem', fontWeight: '600' }}>日付</th>
                  <th style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '2px solid #ddd', fontSize: '0.8rem', fontWeight: '600' }}>店舗</th>
                  <th style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '2px solid #ddd', fontSize: '0.8rem', fontWeight: '600' }}>開始</th>
                  <th style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '2px solid #ddd', fontSize: '0.8rem', fontWeight: '600' }}>終了</th>
                  <th style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '2px solid #ddd', fontSize: '0.8rem', fontWeight: '600' }}>休憩</th>
                  <th style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '2px solid #ddd', fontSize: '0.8rem', fontWeight: '600' }}>労働時間</th>
                  {workData.some(d => d.salary) && <th style={{ padding: '0.75rem 0.5rem', textAlign: 'right', borderBottom: '2px solid #ddd', fontSize: '0.8rem', fontWeight: '600' }}>給料</th>}
                </tr>
              </thead>
              <tbody>
                {workData.map((record, index) => {
                  const hours = Math.floor(record.work_minutes / 60);
                  const minutes = record.work_minutes % 60;
                  return (
                    <tr key={record.id} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9' }}>
                      <td style={{ padding: '0.75rem 0.5rem', borderBottom: '1px solid #eee' }}>
                        <strong>{record.date}</strong><br/><span style={{ fontSize: '0.75rem', color: '#666' }}>({getWeekday(record.date)})</span>
                      </td>
                      <td style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '1px solid #eee', fontWeight: 'bold', color: '#1976D2' }}>
                        {record.store}店舗
                      </td>
                      <td style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>{formatTime(record.actual_start)}</td>
                      <td style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>{formatTime(record.actual_end)}</td>
                      <td style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '1px solid #eee' }}>{record.break_minutes || 0}分</td>
                      <td style={{ padding: '0.75rem 0.5rem', textAlign: 'center', borderBottom: '1px solid #eee', fontWeight: 'bold' }}>
                        {hours}h{minutes}m
                      </td>
                      {workData.some(d => d.salary) && (
                        <td style={{ padding: '0.75rem 0.5rem', textAlign: 'right', borderBottom: '1px solid #eee', fontWeight: 'bold', color: record.salary ? '#4CAF50' : '#999' }}>
                          {record.salary ? `¥${record.salary.toLocaleString()}` : '-'}
                        </td>
                      )}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}

        {/* メニューに戻るボタン */}
        <div style={{ marginTop: '1.5rem', textAlign: 'center' }}>
          <button onClick={onBack} style={{ backgroundColor: '#607D8B', color: 'white', padding: '0.75rem 2rem', border: 'none', borderRadius: '4px', cursor: 'pointer', width: '100%', maxWidth: '300px', fontSize: '1rem' }}>
            メニューに戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default StaffWorkHours;
