// client/src/App.js
import React, { useState } from 'react';

function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState('');  // 'manager' or 'staff'
  const [message, setMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    const res = await fetch('http://localhost:3001/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id, password }),
    });

    if (res.ok) {
      setIsLoggedIn(true);
      setMessage('');
    } else {
      const data = await res.json();
      setMessage(data.message);
    }
  };

  const selectRole = (selectedRole) => {
    setRole(selectedRole);
  };

  if (!isLoggedIn) {
    // ログイン画面
    return (
      <div style={{ maxWidth: 400, margin: '2rem auto' }}>
        <h2>ログイン</h2>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="ログインID"
            value={id}
            onChange={e => setId(e.target.value)}
            required
          />
          <br /><br />
          <input
            type="password"
            placeholder="パスワード"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          <br /><br />
          <button type="submit">ログイン</button>
        </form>
        <p style={{ color: 'red' }}>{message}</p>
      </div>
    );
  }

  if (!role) {
    // 役職選択画面
    return (
      <div style={{ maxWidth: 400, margin: '2rem auto' }}>
        <h2>役職を選択してください</h2>
        <button onClick={() => selectRole('staff')} style={{ marginRight: 10, padding: '10px 20px' }}>
          アルバイト
        </button>
        <button onClick={() => selectRole('manager')} style={{ padding: '10px 20px' }}>
          店長
        </button>
      </div>
    );
  }

  // 役職が選択された後の画面例
  return (
    <div style={{ maxWidth: 400, margin: '2rem auto' }}>
      <h2>ようこそ、{role === 'manager' ? '店長' : 'アルバイト'}さん！</h2>
      {/* ここに次の画面の処理を入れてください */}
    </div>
  );
}

export default App;
