        <HelpModal isOpen={showHelp} onClose={() => setShowHelp(false)} content={getHelpContent(currentHelpPage)} />
        <div style={{
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 4px 16px rgba(0,0,0,0.1)',
          width: '100%',
          maxWidth: '400px',
          position: 'relative'
        }}>
          <BackButton />
          <HelpButton page="staffMenu" />
          <h2 style={{ textAlign: 'center', color: '#1976D2', marginBottom: '1.5rem' }}>アルバイトメニュー</h2>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            <button 
              onClick={() => {
                pushToHistory({
                  role: role,
                  currentStep: '',
                  managerAuth: managerAuth,
                  managerStep: managerStep,
                  isLoggedIn: true
                });
                setCurrentStep('shiftPeriod');
              }} 
              style={{ 
                backgroundColor: '#1E88E5',
                color: 'white',
                border: 'none',
                padding: '1rem',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              新規提出
            </button>
            <button 
              onClick={() => {
                pushToHistory({
                  role: role,
                  currentStep: '',
                  managerAuth: managerAuth,
                  managerStep: managerStep,
                  isLoggedIn: true
                });
                alert('シフト変更画面（デモ版では未実装）');
              }} 
              style={{ 
                backgroundColor: '#1976D2',
                color: 'white',
                border: 'none',
                padding: '1rem',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              シフト変更
            </button>
            <button 
              onClick={() => {
                pushToHistory({
                  role: role,
                  currentStep: '',
                  managerAuth: managerAuth,
                  managerStep: managerStep,
                  isLoggedIn: true
                });
                alert('シフト確認画面（デモ版では未実装）');
              }} 
              style={{ 
                backgroundColor: '#1565C0',
                color: 'white',
                border: 'none',
                padding: '1rem',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              シフト確認
            </button>
            <button 
              onClick={() => {
                pushToHistory({
                  role: role,
                  currentStep: '',
                  managerAuth: managerAuth,
                  managerStep: managerStep,
                  isLoggedIn: true
                });
                alert('就労時間画面（デモ版では未実装）');
              }} 
              style={{ 
                backgroundColor: '#0D47A1',
                color: 'white',
                border: 'none',
                padding: '1rem',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              就労時間
            </button>
            <button 
              onClick={() => {
                window.open('https://docs.google.com/forms/d/e/1FAIpQLSci0UYQ7BKfXjhVj8x3WBR5ncFxxCo_lsV11kY5TaI15wlKSQ/viewform?usp=header', '_blank');
              }} 
              style={{ 
                backgroundColor: '#1554A5',
                color: 'white',
                border: 'none',
                padding: '1rem',
                borderRadius: '8px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: 'pointer'
              }}
            >
              お問い合わせ
            </button>
          </div>
          <button 
            onClick={() => {
              setRole('');
              setId('');
              setPassword('');
              setIsLoggedIn(false);
              setCurrentStep('');
              resetAllInputs();
              setNavigationHistory([]);
            }} 
            style={{ 
              width: '100%',
              backgroundColor: '#FF5722',
              color: 'white',
              border: 'none',
              padding: '0.75rem',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: 'pointer',
              marginTop: '1.5rem'
            }}
          >
            ログアウト
          </button>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
