import React, { useState, useEffect, useMemo } from 'react';
import { supabase } from './supabaseClient';

// ----------------------------------------------------------------------
// 共通ヘルパー関数
// ----------------------------------------------------------------------

// 勤務時間を計算する関数 (分単位)
// 24時越えに対応し、休憩時間を引いた純粋な労働時間を分で返す
const calculateWorkMinutes = (start, end, breakMinutes) => {
  if (!start || !end) return 0;
  
  // 仮の日付を設定
  const date = '2000-01-01'; 
  
  const parseTime = (time, baseDate) => {
    const [h, m] = time.split(':').map(Number);
    let dt = new Date(baseDate + 'T' + time + ':00');
    
    // 終了時間が開始時間より早い場合（日を跨いだ場合）は、終了時間に1日加算
    // 簡易的な判定（24時越えは1日分だけ考慮）
    if (h < 12 && time !== start) { 
        // 終了時刻が00:00-11:59の範囲で、かつ開始時刻が12:00以降の場合に日を跨いだとして扱う
        const [sh] = start.split(':').map(Number);
        if (sh >= 12) {
            dt.setDate(dt.getDate() + 1);
        }
    }
    return dt;
  };
  
  const startDt = parseTime(start, date);
  const endDt = parseTime(end, date);

  // 終了時刻が開始時刻より過去にならないよう調整
  if (endDt.getTime() < startDt.getTime()) {
      endDt.setDate(endDt.getDate() + 1);
  }

  const workMilliseconds = endDt.getTime() - startDt.getTime();
  let workMinutes = Math.floor(workMilliseconds / 60000); // ミリ秒から分へ
  
  // 休憩時間を引く
  return Math.max(0, workMinutes - (breakMinutes || 0));
};

// 分を「H時間M分」に変換
const formatMinutes = (totalMinutes) => {
    const hours = Math.floor(totalMinutes / 60);
    const minutes = totalMinutes % 60;
    return `${hours}時間${minutes}分`;
};


// ----------------------------------------------------------------------
// SummaryView (集計モードのサブコンポーネント)
// ----------------------------------------------------------------------

const SummaryView = ({ userMap, availableDates, onBack }) => {
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [filter, setFilter] = useState('monthly'); // 'monthly', 'weekly', 'daily'
  const [selectedPeriod, setSelectedPeriod] = useState(new Date().toISOString().split('T')[0].substring(0, 7)); // 'YYYY-MM' or 'YYYY-MM-DD'
  const [weekdayFilter, setWeekdayFilter] = useState(''); // '日', '月', '火', ...
  const [timeFilter, setTimeFilter] = useState(''); // 'morning', 'afternoon', 'night'
  const [selectedUser, setSelectedUser] = useState(''); // 個別集計用
  const [loading, setLoading] = useState(true);

  const daysOfWeek = ['日', '月', '火', '水', '木', '金', '土'];
  const timePresets = [
    { label: 'すべて', value: '' },
    { label: '午前 (00:00-11:59)', value: 'morning' },
    { label: '午後 (12:00-17:59)', value: 'afternoon' },
    { label: '夜間 (18:00-23:59)', value: 'night' },
  ];

  useEffect(() => {
    fetchAllAttendanceRecords();
  }, []);

  // ユーザー選択肢
  const userList = useMemo(() => {
    const users = Object.entries(userMap).map(([manager_number, name]) => ({ manager_number, name }));
    users.sort((a, b) => a.name.localeCompare(b.name));
    return [{ manager_number: '', name: '全従業員' }, ...users];
  }, [userMap]);


  const fetchAllAttendanceRecords = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('attendance')
        .select('*')
        .not('work_minutes', 'is', null) // work_minutesがnullでないものを取得
        .order('date', { ascending: false });

      if (error) {
        console.error('集計データ取得エラー:', error);
        alert('集計データの取得に失敗しました');
        return;
      }
      setAttendanceRecords(data || []);
    } catch (error) {
      console.error('データ取得エラー:', error);
    } finally {
      setLoading(false);
    }
  };

  // 期間、曜日、時間帯でデータをフィルタリングし、個人ごとに集計
  const aggregatedData = useMemo(() => {
    let recordsToAggregate = attendanceRecords.filter(record => record.work_minutes > 0);

    // 1. ユーザーフィルタ
    if (selectedUser) {
        recordsToAggregate = recordsToAggregate.filter(record => 
            String(record.manager_number) === String(selectedUser)
        );
    }

    // 2. 期間フィルタ
    recordsToAggregate = recordsToAggregate.filter(record => {
      const dateStr = record.date;
      
      switch (filter) {
        case 'monthly':
        case 'weekly': // 週別は月別と同じ選択肢で対応
          return dateStr.startsWith(selectedPeriod);
        case 'daily':
          return dateStr === selectedPeriod;
        default:
          return true;
      }
    });

    // 3. 曜日・時間帯フィルタリング
    const filteredData = recordsToAggregate.filter(record => {
      // 曜日フィルタ
      if (weekdayFilter) {
        const dayIndex = new Date(record.date).getDay();
        if (daysOfWeek[dayIndex] !== weekdayFilter) return false;
      }
      
      // 時間帯フィルタ (簡易的に、その時間帯に勤務時間の重複があるかをチェック)
      if (timeFilter) {
        if (!record.actual_start || !record.actual_end) return false;

        let startMinutes = parseInt(record.actual_start.split(':')[0]) * 60 + parseInt(record.actual_start.split(':')[1]);
        let endMinutes = parseInt(record.actual_end.split(':')[0]) * 60 + parseInt(record.actual_end.split(':')[1]);
        if (endMinutes < startMinutes) endMinutes += 24 * 60; // 日をまたぐ場合
        
        let hasOverlap = false;

        switch (timeFilter) {
          case 'morning': // 00:00 - 11:59 (0分 - 719分)
            hasOverlap = (startMinutes < 720 && endMinutes > 0);
            break;
          case 'afternoon': // 12:00 - 17:59 (720分 - 1079分)
            hasOverlap = (startMinutes < 1080 && endMinutes > 720);
            break;
          case 'night': // 18:00 - 23:59 (1080分 - 1439分)
            hasOverlap = (startMinutes < 1440 && endMinutes > 1080);
            break;
        }
        
        if (!hasOverlap) return false;
      }
      
      return true;
    });

    // 4. 個人ごとの集計
    const totals = {};
    filteredData.forEach(record => {
      const managerNumber = record.manager_number;
      const minutes = record.work_minutes; // DBから取得したwork_minutesを使用
      
      if (!totals[managerNumber]) {
        totals[managerNumber] = { 
          manager_number: managerNumber,
          name: userMap[managerNumber] || `管理番号: ${managerNumber}`,
          totalMinutes: 0
        };
      }
      totals[managerNumber].totalMinutes += minutes;
    });

    // 結果を配列に変換し、勤務時間が多い順にソート
    return Object.values(totals).sort((a, b) => b.totalMinutes - a.totalMinutes);

  }, [attendanceRecords, filter, selectedPeriod, weekdayFilter, timeFilter, selectedUser, userMap]);

  
  // 期間選択要素の生成
  const renderPeriodSelector = () => {
    const dateList = [...new Set(availableDates.map(d => d.substring(0, 7)))].sort((a, b) => b.localeCompare(a));
    const dayList = availableDates.sort((a, b) => b.localeCompare(a));
    
    switch(filter) {
      case 'monthly':
      case 'weekly':
        return (
          <select 
            value={selectedPeriod} 
            onChange={(e) => setSelectedPeriod(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            {dateList.map(month => (
              <option key={month} value={month}>{month.replace('-', '年')}月</option>
            ))}
          </select>
        );
      case 'daily':
        return (
          <select 
            value={selectedPeriod} 
            onChange={(e) => setSelectedPeriod(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            {dayList.map(day => (
              <option key={day} value={day}>{day}</option>
            ))}
          </select>
        );
      default:
        return null;
    }
  };

  return (
    <div className="login-card" style={{ width: '900px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
      <h2>勤務時間集計</h2>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', marginBottom: '1.5rem', padding: '1rem', border: '1px solid #eee', borderRadius: '8px', backgroundColor: '#fff' }}>
        
        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>従業員選択:</label>
          <select 
            value={selectedUser} 
            onChange={(e) => setSelectedUser(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            {userList.map(user => (
              <option key={user.manager_number} value={user.manager_number}>{user.name}</option>
            ))}
          </select>
        </div>
        
        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>期間選択:</label>
          <select 
            value={filter} 
            onChange={(e) => {
              const newFilter = e.target.value;
              setFilter(newFilter);
              // 期間選択を変更した際、選択された期間もリセット/調整
              if (newFilter === 'monthly' || newFilter === 'weekly') {
                setSelectedPeriod(new Date().toISOString().split('T')[0].substring(0, 7)); 
              } else if (newFilter === 'daily') {
                setSelectedPeriod(new Date().toISOString().split('T')[0]); 
              }
            }}
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            <option value="monthly">月別集計</option>
            <option value="weekly">週別集計 (月単位で選択)</option>
            <option value="daily">日別集計</option>
          </select>
        </div>
        
        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>対象期間:</label>
          {renderPeriodSelector()}
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>曜日フィルタ:</label>
          <select 
            value={weekdayFilter} 
            onChange={(e) => setWeekdayFilter(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            <option value="">すべて</option>
            {daysOfWeek.map(day => <option key={day} value={day}>{day}曜日</option>)}
          </select>
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
          <label style={{ fontWeight: 'bold', fontSize: '0.9rem' }}>時間帯フィルタ:</label>
          <select 
            value={timeFilter} 
            onChange={(e) => setTimeFilter(e.target.value)} 
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ddd' }}
          >
            {timePresets.map(p => <option key={p.value} value={p.value}>{p.label}</option>)}
          </select>
        </div>
      </div>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '2rem' }}>読み込み中...</div>
      ) : (
        <div style={{
          border: '1px solid #ddd',
          borderRadius: '8px',
          overflow: 'auto',
          maxHeight: '400px'
        }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead style={{ position: 'sticky', top: 0, backgroundColor: '#f5f5f5' }}>
              <tr>
                <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd', textAlign: 'left' }}>名前</th>
                <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd', textAlign: 'right' }}>総勤務時間</th>
              </tr>
            </thead>
            <tbody>
              {aggregatedData.length > 0 ? (
                aggregatedData.map((data, index) => (
                  <tr key={data.name} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9' }}>
                    <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                      {data.name}
                    </td>
                    <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee', textAlign: 'right', fontWeight: 'bold' }}>
                      {formatMinutes(data.totalMinutes)}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="2" style={{ textAlign: 'center', padding: '1rem', color: '#666' }}>
                    選択した条件に一致するデータがありません。
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}

      <div style={{ marginTop: '2rem', textAlign: 'center' }}>
        <button onClick={onBack} style={{
          backgroundColor: '#607D8B',
          color: 'white',
          padding: '0.75rem 2rem',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}>
          カレンダーに戻る
        </button>
      </div>
    </div>
  );
};


// ----------------------------------------------------------------------
// ManagerAttendance (メインコンポーネント)
// ----------------------------------------------------------------------

function ManagerAttendance({ onBack }) {
  const [selectedDate, setSelectedDate] = useState('');
  const [attendanceData, setAttendanceData] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [loading, setLoading] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [availableDates, setAvailableDates] = useState([]);
  const [currentView, setCurrentView] = useState('calendar'); // 'calendar', 'attendance', 'summary'

  useEffect(() => {
    fetchAvailableDates();
    fetchUsers();
  }, []);
  
  // ... (fetchAvailableDates, fetchUsers, generateCalendarDays, changeMonth, getWeekday は変更なし)

  const fetchAvailableDates = async () => {
    try {
      const { data: finalShifts, error } = await supabase
        .from('final_shifts')
        .select('date')
        .order('date');

      if (error) {
        console.error('日付取得エラー:', error);
        return;
      }

      const uniqueDates = finalShifts ? [...new Set(finalShifts.map(item => item.date))].sort() : [];
      setAvailableDates(uniqueDates);
    } catch (error) {
      console.error('日付取得エラー:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const { data: users, error } = await supabase
        .from('users')
        .select('*');

      if (error) {
        console.error('ユーザー取得エラー:', error);
        return;
      }

      const userMapTemp = {};
      if (users && users.length > 0) {
        users.forEach(user => {
          const managerNumber = user.manager_number;
          if (managerNumber !== null && managerNumber !== undefined) {
            userMapTemp[String(managerNumber)] = user.name || `ユーザー${managerNumber}`;
            userMapTemp[Number(managerNumber)] = user.name || `ユーザー${managerNumber}`;
          }
        });
      }
      setUserMap(userMapTemp);
    } catch (error) {
      console.error('予期しないエラー:', error);
    }
  };

  const fetchAttendanceData = async (date) => {
    if (!date) return;

    setLoading(true);
    try {
      // 1. シフトデータ (予定) を取得
      const { data: finalShifts, error: shiftError } = await supabase
        .from('final_shifts')
        .select('*')
        .eq('date', date)
        .order('manager_number');

      if (shiftError) {
        console.error('シフトデータ取得エラー:', shiftError);
        alert('シフトデータの取得に失敗しました');
        setLoading(false);
        return;
      }

      // 2. 勤怠データ (実績) を取得
      const { data: existingAttendance, error: attendanceError } = await supabase
        .from('attendance')
        .select('*')
        .eq('date', date);

      if (attendanceError) {
        console.error('勤怠データ取得エラー:', attendanceError);
      }

      const attendanceMap = {};
      if (existingAttendance) {
        existingAttendance.forEach(att => {
          attendanceMap[att.manager_number] = att;
        });
      }

      // 3. 予定と実績をマージ
      const data = finalShifts.map(shift => {
        const existing = attendanceMap[shift.manager_number];
        // 予定が休み、または予定の開始/終了時刻が空や00:00の場合は isOff を true にする（ただし編集は可能にする）
        const isOff = shift.is_off || !shift.start_time || !shift.end_time ||
                      (shift.start_time === '00:00' && shift.end_time === '00:00');

        return {
          manager_number: shift.manager_number,
          name: userMap[shift.manager_number] || `管理番号: ${shift.manager_number}`,
          scheduled_start: shift.start_time || '',
          scheduled_end: shift.end_time || '',
          // 実績が未登録の場合は予定を初期値にする
          actual_start: existing?.actual_start || shift.start_time || '',
          actual_end: existing?.actual_end || shift.end_time || '',
          break_minutes: existing?.break_minutes || 0,
          store: shift.store || '',
          is_off: isOff, // 予定ベースの休み判定
          attendance_id: existing?.id || null
        };
      });

      // 休みの人をリストの最後に移動 (ソート)
      const sortedData = data.sort((a, b) => {
        return a.is_off === b.is_off ? 0 : a.is_off ? 1 : -1;
      });

      setAttendanceData(sortedData);
      setCurrentView('attendance');
    } catch (error) {
      console.error('データ取得エラー:', error);
      alert('エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  const handleTimeChange = (index, field, value) => {
    const updated = [...attendanceData];
    updated[index][field] = value;
    setAttendanceData(updated);
  };

  const handleSave = async () => {
    setLoading(true);
    try {
      for (const record of attendanceData) {
        // is_off の人でも、入力があれば保存できるようにチェックを外す

        const workMinutes = calculateWorkMinutes(
            record.actual_start, 
            record.actual_end, 
            record.break_minutes
        );
        
        // 労働時間が0で、かつ実績時刻が空の場合（完全に未入力の場合）はスキップ
        if (workMinutes === 0 && !record.actual_start && !record.actual_end) continue;


        const attendanceRecord = {
          date: selectedDate,
          manager_number: record.manager_number,
          actual_start: record.actual_start,
          actual_end: record.actual_end,
          break_minutes: record.break_minutes || 0,
          work_minutes: workMinutes, // 集計モードで使用するために保存
          salary: null, 
          store: record.store
        };

        if (record.attendance_id) {
          const { error } = await supabase
            .from('attendance')
            .update(attendanceRecord)
            .eq('id', record.attendance_id);

          if (error) {
            console.error('更新エラー:', error);
            alert(`${record.name} の更新に失敗しました`);
            setLoading(false);
            return;
          }
        } else if (workMinutes > 0 || (record.actual_start && record.actual_end)) {
          // workMinutesが0以上、または実績時刻が入力されている場合のみ新規挿入を試みる
          const { error } = await supabase
            .from('attendance')
            .insert([attendanceRecord]);

          if (error) {
            console.error('挿入エラー:', error);
            alert(`${record.name} の保存に失敗しました`);
            setLoading(false);
            return;
          }
        }
      }

      alert('保存しました');
      // 再度データを取得し、work_minutesを更新した状態で表示
      fetchAttendanceData(selectedDate); 
    } catch (error) {
      console.error('保存エラー:', error);
      alert('保存中にエラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  const handleDateSelect = (date) => {
    if (!availableDates.includes(date)) return;
    setSelectedDate(date);
    fetchAttendanceData(date);
  };

  const handleBackToCalendar = () => {
    setCurrentView('calendar');
    setSelectedDate('');
    setAttendanceData([]);
  };

  const changeDate = (delta) => {
    if (!selectedDate || availableDates.length === 0) return;
    const idx = availableDates.indexOf(selectedDate);
    const newIdx = idx + delta;
    if (newIdx >= 0 && newIdx < availableDates.length) {
      const newDate = availableDates[newIdx];
      setSelectedDate(newDate);
      fetchAttendanceData(newDate);
    }
  };

  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    const firstDay = new Date(year, month, 1);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());

    const days = [];
    const currentDate = new Date(startDate);

    for (let i = 0; i < 42; i++) {
      const dateStr = currentDate.toISOString().split('T')[0];
      const isCurrentMonth = currentDate.getMonth() === month;
      const hasShift = availableDates.includes(dateStr);

      days.push({
        date: new Date(currentDate),
        dateStr: dateStr,
        day: currentDate.getDate(),
        isCurrentMonth,
        hasShift
      });

      currentDate.setDate(currentDate.getDate() + 1);
    }

    return days;
  };

  const changeMonth = (delta) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + delta);
    setCurrentMonth(newMonth);
  };

  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  const calendarDays = generateCalendarDays();
  const weekdays = ['日', '月', '火', '水', '木', '金', '土'];


  // ----------------------------------------------------------------------
  // View Rendering
  // ----------------------------------------------------------------------

  if (currentView === 'summary') {
    return (
      <div className="login-wrapper">
        <SummaryView 
          userMap={userMap} 
          availableDates={availableDates} 
          onBack={() => setCurrentView('calendar')} 
        />
      </div>
    );
  }

  if (currentView === 'calendar') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ width: '600px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
            <h2>勤怠管理</h2>
          </div>

          <div style={{ marginBottom: '1rem', display: 'flex', gap: '0.5rem', justifyContent: 'center' }}>
            <button
              onClick={() => setCurrentView('calendar')}
              style={{
                padding: '0.75rem 2rem',
                backgroundColor: '#2196F3',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'not-allowed'
              }}
              disabled
            >
              退勤管理モード
            </button>
            <button
              onClick={() => setCurrentView('summary')}
              style={{
                padding: '0.75rem 2rem',
                backgroundColor: '#FF9800',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              勤務時間集計モード
            </button>
          </div>

          <div style={{
            marginTop: '1rem',
            border: '1px solid #ddd',
            borderRadius: '8px',
            padding: '1rem',
            backgroundColor: '#f9f9f9'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '1rem'
            }}>
              <button onClick={() => changeMonth(-1)} style={{
                backgroundColor: '#607D8B',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ◀
              </button>
              <h3 style={{ margin: 0 }}>
                {currentMonth.getFullYear()}年 {currentMonth.getMonth() + 1}月
              </h3>
              <button onClick={() => changeMonth(1)} style={{
                backgroundColor: '#607D8B',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ▶
              </button>
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(7, 1fr)',
              gap: '2px',
              marginBottom: '0.5rem'
            }}>
              {weekdays.map(day => (
                <div key={day} style={{
                  textAlign: 'center',
                  fontWeight: 'bold',
                  padding: '0.5rem',
                  backgroundColor: '#e0e0e0'
                }}>
                  {day}
                </div>
              ))}
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(7, 1fr)',
              gap: '2px'
            }}>
              {calendarDays.map((dayInfo, index) => (
                <button
                  key={index}
                  onClick={() => dayInfo.hasShift && handleDateSelect(dayInfo.dateStr)}
                  disabled={!dayInfo.hasShift}
                  style={{
                    padding: '0.5rem',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: dayInfo.hasShift ? 'pointer' : 'not-allowed',
                    backgroundColor: dayInfo.hasShift ? '#E3F2FD' :
                                   dayInfo.isCurrentMonth ? 'white' : '#f0f0f0',
                    color: !dayInfo.hasShift ? '#999' :
                           dayInfo.isCurrentMonth ? 'black' : '#666',
                    fontWeight: dayInfo.hasShift ? 'bold' : 'normal',
                    opacity: dayInfo.isCurrentMonth ? 1 : 0.5,
                    transition: 'all 0.3s ease'
                  }}
                >
                  {dayInfo.day}
                </button>
              ))}
            </div>
          </div>

          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <button onClick={onBack} style={{
              backgroundColor: '#607D8B',
              color: 'white',
              padding: '0.75rem 2rem',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}>
              メニューに戻る
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Attendance View (日別勤怠入力)
  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '900px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
        <h2 style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '1rem' }}>
          <button onClick={() => changeDate(-1)}>◀</button>
          {selectedDate} ({getWeekday(selectedDate)})
          <button onClick={() => changeDate(1)}>▶</button>
        </h2>
        <p style={{ textAlign: 'center', color: '#666' }}>
          モード: <strong>退勤管理</strong>
        </p>

        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>読み込み中...</div>
        ) : (
          <div style={{
            border: '1px solid #ddd',
            borderRadius: '8px',
            overflow: 'auto',
            maxHeight: '500px'
          }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead style={{ position: 'sticky', top: 0, backgroundColor: '#f5f5f5' }}>
                <tr>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>名前</th>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>店舗</th>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>予定開始</th>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>予定終了</th>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>実際開始</th>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>実際終了</th>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>休憩(分)</th>
                  <th style={{ padding: '0.75rem', borderBottom: '1px solid #ddd' }}>労働時間</th>
                </tr>
              </thead>
              <tbody>
                {attendanceData.map((record, index) => {
                    const workMinutes = calculateWorkMinutes(
                        record.actual_start,
                        record.actual_end,
                        record.break_minutes
                    );
                  return (
                    <tr key={index} style={{
                      backgroundColor: record.is_off ? '#f0f0f0' : (index % 2 === 0 ? 'white' : '#f9f9f9')
                    }}>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                        {record.name}
                      </td>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee', textAlign: 'center' }}>
                        {record.store}店舗
                      </td>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee', textAlign: 'center' }}>
                        {record.is_off ? <span style={{ color: '#999' }}>休み</span> : record.scheduled_start}
                      </td>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee', textAlign: 'center' }}>
                        {record.is_off ? <span style={{ color: '#999' }}>休み</span> : record.scheduled_end}
                      </td>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                        <input
                          type="time"
                          value={record.actual_start}
                          onChange={(e) => handleTimeChange(index, 'actual_start', e.target.value)}
                          style={{ width: '100px', padding: '0.25rem', backgroundColor: record.is_off ? '#ddd' : 'white' }}
                        />
                      </td>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                        <input
                          type="time"
                          value={record.actual_end}
                          onChange={(e) => handleTimeChange(index, 'actual_end', e.target.value)}
                          style={{ width: '100px', padding: '0.25rem', backgroundColor: record.is_off ? '#ddd' : 'white' }}
                        />
                      </td>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                        <input
                          type="number"
                          value={record.break_minutes}
                          onChange={(e) => handleTimeChange(index, 'break_minutes', Number(e.target.value))}
                          style={{ width: '60px', padding: '0.25rem', backgroundColor: record.is_off ? '#ddd' : 'white' }}
                        />
                      </td>
                      <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee', textAlign: 'center', fontWeight: 'bold' }}>
                        {workMinutes > 0 ? formatMinutes(workMinutes) : '-'}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}

        <div style={{ marginTop: '1rem', display: 'flex', gap: '1rem', justifyContent: 'center' }}>
          <button
            onClick={handleSave}
            disabled={loading}
            style={{
              backgroundColor: '#4CAF50',
              color: 'white',
              padding: '0.75rem 2rem',
              border: 'none',
              borderRadius: '4px',
              cursor: loading ? 'not-allowed' : 'pointer',
              opacity: loading ? 0.6 : 1
            }}
          >
            {loading ? '保存中...' : '確定'}
          </button>
          <button onClick={handleBackToCalendar} style={{
            backgroundColor: '#607D8B',
            color: 'white',
            padding: '0.75rem 2rem',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}>
            カレンダーに戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default ManagerAttendance;
