import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function StaffShiftView({ onBack }) {
  const [selectedDate, setSelectedDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [userMap, setUserMap] = useState({});
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [availableDates, setAvailableDates] = useState([]);
  const [viewMode, setViewMode] = useState('list');
  const [currentView, setCurrentView] = useState('calendar');

  // 初期化処理
  useEffect(() => {
    fetchAvailableDates();
    fetchUsers();
  }, []);

  // 利用可能な日付を取得
  const fetchAvailableDates = async () => {
    try {
      const { data, error } = await supabase
        .from('final_shifts')
        .select('date')
        .order('date');

      if (error) {
        console.error('日付取得エラー:', error);
        return;
      }

      if (data) {
        const uniqueDates = [...new Set(data.map(item => item.date))];
        setAvailableDates(uniqueDates);
      }
    } catch (error) {
      console.error('予期しないエラー:', error);
    }
  };

  // ユーザー情報を取得
  const fetchUsers = async () => {
    try {
      const { data: users, error } = await supabase
        .from('users')
        .select('*');

      if (error) {
        console.error('ユーザー取得エラー:', error);
        return;
      }

      const userMapTemp = {};
      if (users) {
        users.forEach(user => {
          userMapTemp[String(user.manager_number)] = user.name;
        });
      }
      setUserMap(userMapTemp);
    } catch (error) {
      console.error('予期しないエラー:', error);
    }
  };

  // 指定日のシフトデータを取得
  const fetchShiftData = async (date) => {
    if (!date) return;

    setLoading(true);
    try {
      const { data, error } = await supabase
        .from('final_shifts')
        .select('*')
        .eq('date', date)
        .order('manager_number');

      if (error) {
        console.error('シフトデータ取得エラー:', error);
        alert('シフトデータの取得に失敗しました');
        return;
      }

      console.log('取得したシフトデータ:', data); // デバッグ用
      // デバッグ用にグローバル変数に設定
      window.debugShiftData = data;
      window.debugUserMap = userMap;
      
      setShiftData(data || []);
      setCurrentView('shift');
    } catch (error) {
      console.error('予期しないエラー:', error);
      alert('エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  // 日付選択処理
  const handleDateSelect = (date) => {
    setSelectedDate(date);
    fetchShiftData(date);
  };

  // カレンダーに戻る
  const handleBackToCalendar = () => {
    setCurrentView('calendar');
    setSelectedDate('');
    setShiftData([]);
  };

  // カレンダー日付生成
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    
    const firstDay = new Date(year, month, 1);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay());
    
    const days = [];
    const currentDate = new Date(startDate);
    
    for (let i = 0; i < 42; i++) {
      const dateStr = currentDate.toISOString().split('T')[0];
      const isCurrentMonth = currentDate.getMonth() === month;
      const hasShift = availableDates.includes(dateStr);
      
      days.push({
        date: new Date(currentDate),
        dateStr: dateStr,
        day: currentDate.getDate(),
        isCurrentMonth,
        hasShift
      });
      
      currentDate.setDate(currentDate.getDate() + 1);
    }
    
    return days;
  };

  // 月変更
  const changeMonth = (delta) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + delta);
    setCurrentMonth(newMonth);
  };

  // 時間フォーマット
  const formatTime = (timeStr) => {
    if (!timeStr) return '';
    return timeStr.slice(0, 5);
  };

  // 曜日取得
  const getWeekday = (dateStr) => {
    const days = ['日', '月', '火', '水', '木', '金', '土'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  // タイムライン用時間スロット生成
  const generateTimeSlots = () => {
    const slots = [];
    for (let hour = 0; hour < 24; hour++) {
      slots.push(`${hour.toString().padStart(2, '0')}:00`);
    }
    return slots;
  };

  // 時間を分に変換
  const timeToMinutes = (timeStr) => {
    if (!timeStr) return 0;
    const [hours, minutes] = timeStr.split(':').map(Number);
    return hours * 60 + minutes;
  };

  // 指定時間に勤務中かチェック
  const isWorkingAtTime = (shift, hour) => {
    // is_offフィールドがtrueの場合、または勤務時間が設定されていない場合は休み
    if (shift.is_off || !shift.start_time || !shift.end_time) return false;
    
    const startMinutes = timeToMinutes(shift.start_time);
    const endMinutes = timeToMinutes(shift.end_time);
    const checkMinutes = hour * 60;
    
    return checkMinutes >= startMinutes && checkMinutes < endMinutes;
  };

  // 休みかどうかの判定を改善
  const isOffDay = (shift) => {
    return shift.is_off === true || !shift.start_time || !shift.end_time || 
           shift.start_time === '' || shift.end_time === '';
  };

  const calendarDays = generateCalendarDays();
  const weekdays = ['日', '月', '火', '水', '木', '金', '土'];
  const timeSlots = generateTimeSlots();

  // カレンダー表示
  if (currentView === 'calendar') {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ width: '600px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
          <h2>シフト確認</h2>
          
          <div style={{ 
            marginTop: '1rem', 
            border: '1px solid #ddd', 
            borderRadius: '8px',
            padding: '1rem',
            backgroundColor: '#f9f9f9'
          }}>
            {/* 月変更ヘッダー */}
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              marginBottom: '1rem'
            }}>
              <button onClick={() => changeMonth(-1)} style={{ 
                backgroundColor: '#607D8B', 
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ◀
              </button>
              <h3 style={{ margin: 0 }}>
                {currentMonth.getFullYear()}年 {currentMonth.getMonth() + 1}月
              </h3>
              <button onClick={() => changeMonth(1)} style={{ 
                backgroundColor: '#607D8B', 
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '0.5rem',
                cursor: 'pointer'
              }}>
                ▶
              </button>
            </div>

            {/* 曜日ヘッダー */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(7, 1fr)', 
              gap: '2px',
              marginBottom: '0.5rem'
            }}>
              {weekdays.map(day => (
                <div key={day} style={{ 
                  textAlign: 'center', 
                  fontWeight: 'bold',
                  padding: '0.5rem',
                  backgroundColor: '#e0e0e0'
                }}>
                  {day}
                </div>
              ))}
            </div>

            {/* カレンダー日付 */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(7, 1fr)', 
              gap: '2px'
            }}>
              {calendarDays.map((dayInfo, index) => (
                <button
                  key={index}
                  onClick={() => dayInfo.hasShift && handleDateSelect(dayInfo.dateStr)}
                  disabled={!dayInfo.hasShift}
                  style={{
                    padding: '0.5rem',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: dayInfo.hasShift ? 'pointer' : 'not-allowed',
                    backgroundColor: dayInfo.hasShift ? '#E3F2FD' : 
                                   dayInfo.isCurrentMonth ? 'white' : '#f0f0f0',
                    color: !dayInfo.hasShift ? '#999' :
                           dayInfo.isCurrentMonth ? 'black' : '#666',
                    fontWeight: dayInfo.hasShift ? 'bold' : 'normal',
                    opacity: dayInfo.isCurrentMonth ? 1 : 0.5
                  }}
                >
                  {dayInfo.day}
                </button>
              ))}
            </div>
            
            <div style={{ 
              marginTop: '0.5rem', 
              fontSize: '0.8rem', 
              color: '#666',
              textAlign: 'center'
            }}>
              青色: シフトあり | 灰色: シフトなし
            </div>
          </div>

          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <button onClick={onBack} style={{ 
              backgroundColor: '#607D8B',
              color: 'white',
              padding: '0.75rem 2rem',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '1rem'
            }}>
              メニューに戻る
            </button>
          </div>
        </div>
      </div>
    );
  }

  // シフト詳細表示
  return (
    <div className="login-wrapper">
      <div className="login-card" style={{ width: '800px', maxWidth: '95vw', maxHeight: '90vh', overflowY: 'auto' }}>
        <h2>{selectedDate} ({getWeekday(selectedDate)}) のシフト</h2>
        
        {/* 表示モード切替 */}
        <div style={{ marginBottom: '1rem', display: 'flex', gap: '0.5rem', justifyContent: 'center' }}>
          <button
            onClick={() => setViewMode('list')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: viewMode === 'list' ? '#2196F3' : '#f0f0f0',
              color: viewMode === 'list' ? 'white' : 'black',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            リスト表示
          </button>
          <button
            onClick={() => setViewMode('timeline')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: viewMode === 'timeline' ? '#2196F3' : '#f0f0f0',
              color: viewMode === 'timeline' ? 'white' : 'black',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            タイムライン表示
          </button>
        </div>
        
        {loading ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            読み込み中...
          </div>
        ) : shiftData.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '2rem',
            color: '#666',
            backgroundColor: '#f9f9f9',
            borderRadius: '8px'
          }}>
            この日のシフトはありません
          </div>
        ) : viewMode === 'list' ? (
          /* リスト表示 */
          <div style={{ 
            border: '1px solid #ddd',
            borderRadius: '8px',
            overflow: 'hidden'
          }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #ddd' }}>
                    名前
                  </th>
                  <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                    勤務時間
                  </th>
                  <th style={{ padding: '0.75rem', textAlign: 'center', borderBottom: '1px solid #ddd' }}>
                    状態
                  </th>
                </tr>
              </thead>
              <tbody>
                {shiftData.map((shift, index) => (
                  <tr key={shift.manager_number || index} style={{ 
                    backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                  }}>
                    <td style={{ padding: '0.75rem', borderBottom: '1px solid #eee' }}>
                      <strong>{userMap[shift.manager_number] || `管理番号: ${shift.manager_number}`}</strong>
                    </td>
                    <td style={{ 
                      padding: '0.75rem', 
                      textAlign: 'center',
                      borderBottom: '1px solid #eee'
                    }}>
                      {isOffDay(shift) ? (
                        <span style={{ color: '#999', fontStyle: 'italic' }}>休み</span>
                      ) : (
                        <span style={{ fontWeight: 'bold' }}>
                          {formatTime(shift.start_time)} - {formatTime(shift.end_time)}
                        </span>
                      )}
                    </td>
                    <td style={{ 
                      padding: '0.75rem', 
                      textAlign: 'center',
                      borderBottom: '1px solid #eee'
                    }}>
                      <span style={{ 
                        padding: '0.25rem 0.5rem',
                        borderRadius: '12px',
                        fontSize: '0.8rem',
                        backgroundColor: isOffDay(shift) ? '#f44336' : '#4CAF50',
                        color: 'white'
                      }}>
                        {isOffDay(shift) ? '休み' : '出勤'}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          /* タイムライン表示 */
          <div style={{ 
            border: '1px solid #ddd',
            borderRadius: '8px',
            overflow: 'auto',
            maxHeight: '500px'
          }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', minWidth: '600px' }}>
              <thead style={{ position: 'sticky', top: 0, backgroundColor: '#f5f5f5' }}>
                <tr>
                  <th style={{ 
                    padding: '0.5rem', 
                    textAlign: 'left', 
                    borderBottom: '1px solid #ddd',
                    minWidth: '100px',
                    backgroundColor: '#f5f5f5'
                  }}>
                    時間
                  </th>
                  {shiftData.map((shift, index) => (
                    <th key={shift.manager_number || index} style={{ 
                      padding: '0.5rem', 
                      textAlign: 'center', 
                      borderBottom: '1px solid #ddd',
                      minWidth: '80px',
                      backgroundColor: '#f5f5f5'
                    }}>
                      {userMap[shift.manager_number] || `管理番号: ${shift.manager_number}`}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {timeSlots.map((timeSlot, index) => (
                  <tr key={timeSlot} style={{ 
                    backgroundColor: index % 2 === 0 ? 'white' : '#f9f9f9'
                  }}>
                    <td style={{ 
                      padding: '0.25rem 0.5rem', 
                      borderBottom: '1px solid #eee',
                      fontWeight: 'bold',
                      backgroundColor: index % 2 === 0 ? '#f8f8f8' : '#f0f0f0'
                    }}>
                      {timeSlot}
                    </td>
                    {shiftData.map((shift, shiftIndex) => (
                      <td key={shift.manager_number || shiftIndex} style={{ 
                        padding: '0.25rem', 
                        borderBottom: '1px solid #eee',
                        textAlign: 'center',
                        backgroundColor: isWorkingAtTime(shift, parseInt(timeSlot.split(':')[0])) ? 
                          '#4CAF50' : 
                          isOffDay(shift) ? '#ffebee' : 'transparent'
                      }}>
                        {isWorkingAtTime(shift, parseInt(timeSlot.split(':')[0])) && '●'}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* ナビゲーションボタン */}
        <div style={{ marginTop: '2rem', textAlign: 'center', display: 'flex', gap: '1rem', justifyContent: 'center' }}>
          <button onClick={handleBackToCalendar} style={{ 
            backgroundColor: '#2196F3',
            color: 'white',
            padding: '0.75rem 2rem',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}>
            日付選択に戻る
          </button>
          <button onClick={onBack} style={{ 
            backgroundColor: '#607D8B',
            color: 'white',
            padding: '0.75rem 2rem',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}>
            メニューに戻る
          </button>
        </div>
      </div>
    </div>
  );
}

export default StaffShiftView;
