import React, { useState } from 'react';
import './App.css';
import { supabase } from './supabaseClient';

function App() {
  // „É≠„Ç∞„Ç§„É≥Èñ¢ÈÄ£
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginMessage, setLoginMessage] = useState('');

  // ÂΩπËÅ∑ÈÅ∏Êäû
  const [role, setRole] = useState('');

  // „Çπ„ÉÜ„ÉÉ„ÉóÂà∂Âæ°
  const [currentStep, setCurrentStep] = useState('');

  // ÂÖ•Âäõ„Éá„Éº„Çø
  const [managerNumber, setManagerNumber] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftTimes, setShiftTimes] = useState([]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (id === 'kouki' && password === '0306') {
      setIsLoggedIn(true);
      setLoginMessage('');
    } else {
      setLoginMessage('ID„Åæ„Åü„ÅØ„Éë„Çπ„ÉØ„Éº„Éâ„ÅåÈÅï„ÅÑ„Åæ„Åô');
    }
  };

  const selectRole = (selectedRole) => {
    setRole(selectedRole);
    if (selectedRole === 'staff') {
      setCurrentStep('');
    }
  };

  const handleNext = () => {
    if (!managerNumber.trim()) {
      alert('ÁÆ°ÁêÜÁï™Âè∑„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }
    if (!startDate || !endDate || startDate > endDate) {
      alert('Ê≠£„Åó„ÅÑÈñãÂßãÊó•„ÉªÁµÇ‰∫ÜÊó•„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    const dates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      const yyyy = d.getFullYear();
      const mm = String(d.getMonth() + 1).padStart(2, '0');
      const dd = String(d.getDate()).padStart(2, '0');
      dates.push({ date: `${yyyy}-${mm}-${dd}`, start: '', end: '' });
      d.setDate(d.getDate() + 1);
    }

    setShiftTimes(dates);
    setCurrentStep('shiftInput');
  };

  const handleTimeChange = (index, field, value) => {
    const updated = [...shiftTimes];
    updated[index][field] = value;
    setShiftTimes(updated);
  };

  const handleSubmit = async () => {
    try {
      const results = [];

      for (const shift of shiftTimes) {
        const { data, error } = await supabase
          .from('shifts')
          .insert([
            {
              manager_number: managerNumber,
              date: shift.date,
              start_time: shift.start,
              end_time: shift.end,
            }
          ]);

        if (error) throw error;
        results.push(data);
      }

      alert('„Ç∑„Éï„Éà„Çí‰øùÂ≠ò„Åó„Åæ„Åó„ÅüÔºÅ');

      // ÂàùÊúüÂåñ
      setCurrentStep('');
      setRole('');
      setIsLoggedIn(false);
      setId('');
      setPassword('');
      setManagerNumber('');
      setStartDate('');
      setEndDate('');
      setShiftTimes([]);

    } catch (error) {
      console.error('‰øùÂ≠ò„Ç®„É©„Éº:', error.message);
      alert(`‰øùÂ≠ò‰∏≠„Å´„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü: ${error.message}`);
    }
  };
  // Ë°®Á§∫Âá¶ÁêÜ

  if (!isLoggedIn) {
    return (
      <div className="login-wrapper">
        <form className="login-card" onSubmit={handleLogin}>
          <h2>„É≠„Ç∞„Ç§„É≥</h2>
          <input type="text" placeholder="„É≠„Ç∞„Ç§„É≥ID" value={id} onChange={e => setId(e.target.value)} required />
          <input type="password" placeholder="„Éë„Çπ„ÉØ„Éº„Éâ" value={password} onChange={e => setPassword(e.target.value)} required />
          <button type="submit">„É≠„Ç∞„Ç§„É≥</button>
          {loginMessage && <p className="error-msg">{loginMessage}</p>}
        </form>
      </div>
    );
  }

  if (!role) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>ÂΩπËÅ∑„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ</h2>
          <div className="button-row">
            <button onClick={() => selectRole('staff')}>üë§ „Ç¢„É´„Éê„Ç§„Éà</button>
            <button onClick={() => selectRole('manager')}>üëë Â∫óÈï∑</button>
          </div>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftPeriod') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>Êñ∞Ë¶èÊèêÂá∫</h2>
          <label>ÁÆ°ÁêÜÁï™Âè∑:</label>
          <input type="text" value={managerNumber} onChange={e => setManagerNumber(e.target.value)} />
          <label>ÈñãÂßãÊó•:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>ÁµÇ‰∫ÜÊó•:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <button onClick={handleNext}>Ê¨°„Å∏</button>
        </div>
      </div>
    );
  }

  if (role === 'staff' && currentStep === 'shiftInput') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>„Ç∑„Éï„ÉàÂÖ•Âäõ</h2>
          <p>ÁÆ°ÁêÜÁï™Âè∑: <strong>{managerNumber}</strong></p>
          <div style={{ maxHeight: '300px', overflowY: 'auto', marginBottom: '1rem' }}>
            {shiftTimes.map((item, i) => (
              <div key={item.date} style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem', alignItems: 'center' }}>
                <div style={{ width: '110px' }}>{item.date}</div>
                <input type="time" value={item.start} onChange={e => handleTimeChange(i, 'start', e.target.value)} />
                <input type="time" value={item.end} onChange={e => handleTimeChange(i, 'end', e.target.value)} />
              </div>
            ))}
          </div>
          <button onClick={handleSubmit}>ÈÄÅ‰ø°</button>
        </div>
      </div>
    );
  }

  if (role === 'staff') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
