import React, { useState } from 'react';
import './App.css';

function App() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState('');
  const [message, setMessage] = useState('');
  const [currentStep, setCurrentStep] = useState('login'); // login, role, staffMenu, shiftPeriod, shiftInput

  // „Ç∑„Éï„ÉàÊúüÈñì„ÉªÊó•‰ªò„É™„Çπ„Éà
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftDates, setShiftDates] = useState([]);
  const [shiftTimes, setShiftTimes] = useState({});

  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await fetch('http://localhost:3001/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id, password }),
    });

    if (res.ok) {
      setIsLoggedIn(true);
      setMessage('');
      setCurrentStep('role');
    } else {
      const data = await res.json();
      setMessage(data.message);
    }
  };

  const handleNext = () => {
    if (!startDate || !endDate) {
      alert("ÈñãÂßãÊó•„Å®ÁµÇ‰∫ÜÊó•„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ");
      return;
    }

    const s = new Date(startDate);
    const e = new Date(endDate);
    if (s > e) {
      alert("ÈñãÂßãÊó•„ÅØÁµÇ‰∫ÜÊó•„Çà„ÇäÂâç„Å´„Åó„Å¶„Åè„Å†„Åï„ÅÑ");
      return;
    }

    const days = [];
    const timeMap = {};
    for (let d = new Date(s); d <= e; d.setDate(d.getDate() + 1)) {
      const dateStr = d.toISOString().split("T")[0];
      days.push(dateStr);
      timeMap[dateStr] = { start: '', end: '' };
    }

    setShiftDates(days);
    setShiftTimes(timeMap);
    setCurrentStep('shiftInput');
  };

  const handleTimeChange = (date, field, value) => {
    setShiftTimes((prev) => ({
      ...prev,
      [date]: {
        ...prev[date],
        [field]: value,
      }
    }));
  };

  // === ÁîªÈù¢„Çπ„ÉÜ„ÉÉ„ÉóÂà•„Å´Ë°®Á§∫ ===
  if (!isLoggedIn || currentStep === 'login') {
    return (
      <div className="login-wrapper">
        <form className="login-card" onSubmit={handleLogin}>
          <h2>„É≠„Ç∞„Ç§„É≥</h2>
          <input type="text" placeholder="„É≠„Ç∞„Ç§„É≥ID" value={id} onChange={e => setId(e.target.value)} required />
          <input type="password" placeholder="„Éë„Çπ„ÉØ„Éº„Éâ" value={password} onChange={e => setPassword(e.target.value)} required />
          <button type="submit">„É≠„Ç∞„Ç§„É≥</button>
          {message && <p className="error-msg">{message}</p>}
        </form>
      </div>
    );
  }

  if (currentStep === 'role') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>ÂΩπËÅ∑„ÇíÈÅ∏Êäû</h2>
          <div className="button-row">
            <button onClick={() => { setRole('staff'); setCurrentStep('staffMenu'); }}>üë§ „Ç¢„É´„Éê„Ç§„Éà</button>
            <button onClick={() => setRole('manager')}>üëë Â∫óÈï∑</button>
          </div>
        </div>
      </div>
    );
  }

  if (currentStep === 'staffMenu') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>ÊèêÂá∫ÊñπÊ≥ï„ÇíÈÅ∏Êäû</h2>
          <div className="button-row">
            <button onClick={() => setCurrentStep('shiftPeriod')}>üÜï Êñ∞Ë¶èÊèêÂá∫</button>
            <button>üîÅ Â§âÊõ¥</button>
            <button>‚úÖ Á¢∫Ë™ç</button>
          </div>
        </div>
      </div>
    );
  }

  if (currentStep === 'shiftPeriod') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>ÊèêÂá∫ÊúüÈñì„ÇíÂÖ•Âäõ</h2>
          <label>ÈñãÂßãÊó•:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>ÁµÇ‰∫ÜÊó•:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <button onClick={handleNext}>Ê¨°„Å∏</button>
        </div>
      </div>
    );
  }

  if (currentStep === 'shiftInput') {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>„Ç∑„Éï„ÉàÊôÇÈñìÂÖ•Âäõ</h2>
          {shiftDates.map(date => (
            <div key={date} style={{ marginBottom: '1rem' }}>
              <strong>{date}</strong><br />
              <input
                type="time"
                value={shiftTimes[date]?.start || ''}
                onChange={(e) => handleTimeChange(date, 'start', e.target.value)}
              />
              {' '}
              „Äú{' '}
              <input
                type="time"
                value={shiftTimes[date]?.end || ''}
                onChange={(e) => handleTimeChange(date, 'end', e.target.value)}
              />
            </div>
          ))}
          <button onClick={() => alert('ÊèêÂá∫Âá¶ÁêÜÔºàÊú™ÂÆüË£ÖÔºâ')}>ÈÄÅ‰ø°</button>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
