import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function ManagerCreate() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [dates, setDates] = useState([]);
  const [showTable, setShowTable] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [selectedDate, setSelectedDate] = useState('');
  const [editRows, setEditRows] = useState([]);

  const fetchShiftData = async () => {
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日と終了日を入力してください');
      return;
    }

    const { data: shifts, error: shiftError } = await supabase
      .from('shifts')
      .select('*')
      .gte('date', startDate)
      .lte('date', endDate);

    const { data: users, error: userError } = await supabase
      .from('users')
      .select('*');

    if (shiftError || userError) {
      console.error(shiftError || userError);
      alert('データ取得に失敗しました');
      return;
    }

    const userManagerNumbers = new Set(users.map(user => String(user.manager_number)));
    const userMapTemp = {};
    users.forEach(user => {
      userMapTemp[String(user.manager_number)] = user.name;
    });

    const filteredShifts = shifts.filter(shift => userManagerNumbers.has(String(shift.manager_number)));

    const allDates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      allDates.push(d.toISOString().split('T')[0]);
      d.setDate(d.getDate() + 1);
    }

    setDates(allDates);
    setUserMap(userMapTemp);
    setShiftData(filteredShifts);
    setShowTable(true);
  };

  const groupedByUser = {};
  shiftData.forEach(shift => {
    const name = userMap[String(shift.manager_number)] || '(不明)';
    if (!groupedByUser[name]) groupedByUser[name] = {};
    groupedByUser[name][shift.date] = `${shift.start_time || ''} ~ ${shift.end_time || ''}`;
  });

  const generateTimeSlots = () => {
    const slots = [];
    for (let h = 0; h < 24; h++) {
      for (let m = 0; m < 60; m += 15) {
        const hh = h.toString().padStart(2, '0');
        const mm = m.toString().padStart(2, '0');
        slots.push(`${hh}:${mm}`);
      }
    }
    return slots;
  };

  const handleEditStart = (date) => {
    setSelectedDate(date);
    const rows = shiftData
      .filter(shift => shift.date === date && !(shift.start_time === '00:00' && shift.end_time === '00:00'))
      .map(shift => ({
        id: shift.id,
        name: userMap[shift.manager_number],
        manager_number: shift.manager_number,
        start: shift.start_time,
        end: shift.end_time,
        originalStart: shift.start_time,
        originalEnd: shift.end_time
      }));
    setEditRows(rows);
    setIsEditing(true);
  };

  const handleTimeChange = (index, field, value) => {
    const updated = [...editRows];
    updated[index][field] = value;
    setEditRows(updated);
  };

  const handleSave = async () => {
    const updates = editRows.map(row => ({
      id: row.id,
      start_time: row.start,
      end_time: row.end
    }));

    const { error } = await supabase.from('shifts').upsert(updates, { onConflict: ['id'] });

    if (error) {
      alert('保存に失敗しました');
      console.error(error);
    } else {
      alert('保存しました');
      setIsEditing(false);
      fetchShiftData(); // 再取得
    }
  };

  const timeSlots = generateTimeSlots();

  if (!showTable) {
    return (
      <div className="login-wrapper">
        <div className="login-card">
          <h2>シフト作成</h2>
          <label>開始日:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>終了日:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <div style={{ marginTop: '1rem' }}>
            <button onClick={fetchShiftData}>次へ</button>
          </div>
        </div>
      </div>
    );
  }

  if (isEditing) {
    return (
      <div className="login-wrapper">
        <div className="login-card" style={{ overflowX: 'auto' }}>
          <h2>{selectedDate} のシフト入力</h2>
          <table className="shift-edit-table">
            <thead>
              <tr>
                <th>名前</th>
                <th>開始</th>
                <th>終了</th>
                {timeSlots.map((t, i) => (
                  <th key={i} style={{ fontSize: '0.6rem' }}>{t}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {editRows.map((row, rowIndex) => (
                <tr key={rowIndex}>
                  <td>{row.name}</td>
                  <td><input type="time" value={row.start} onChange={e => handleTimeChange(rowIndex, 'start', e.target.value)} /></td>
                  <td><input type="time" value={row.end} onChange={e => handleTimeChange(rowIndex, 'end', e.target.value)} /></td>
                  {timeSlots.map((slot, colIndex) => {
                    const inRequest = slot >= row.originalStart && slot < row.originalEnd;
                    const inFinal = slot >= row.start && slot < row.end;
                    const bgColor = inFinal ? '#ff9999' : inRequest ? '#ffffcc' : 'transparent';
                    return <td key={colIndex} style={{ backgroundColor: bgColor }} />;
                  })}
                </tr>
              ))}
            </tbody>
          </table>
          <div style={{ marginTop: '1rem' }}>
            <button onClick={handleSave}>保存</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fullscreen-table">
      <div className="login-card">
        <h2>シフト表</h2>
        <div className="shift-table-wrapper">
          <table className="shift-table">
            <thead>
              <tr>
                <th>名前</th>
                {dates.map(date => (
                  <th key={date}>{date}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {Object.entries(groupedByUser).map(([name, shifts]) => (
                <tr key={name}>
                  <td>{name}</td>
                  {dates.map(date => (
                    <td key={date}>{shifts[date] || ''}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div style={{ marginTop: '1rem' }}>
          {dates.map(date => (
            <button key={date} onClick={() => handleEditStart(date)} style={{ margin: '0.2rem' }}>
              {date} 作成へ
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ManagerCreate;
