import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function ManagerCreate() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [shiftData, setShiftData] = useState([]);
  const [userMap, setUserMap] = useState({});
  const [dates, setDates] = useState([]);
  const [showTable, setShowTable] = useState(false);

  const fetchShiftData = async () => {
    if (!startDate || !endDate || startDate > endDate) {
      alert('正しい開始日と終了日を入力してください');
      return;
    }

    const { data: shifts, error: shiftError } = await supabase
      .from('shifts')
      .select('*')
      .gte('date', startDate)
      .lte('date', endDate);

    if (shiftError) {
      console.error(shiftError);
      alert('シフトデータの取得に失敗しました');
      return;
    }

    const { data: users, error: userError } = await supabase
      .from('users')
      .select('*');

    if (userError) {
      console.error(userError);
      alert('ユーザー情報の取得に失敗しました');
      return;
    }

    const userMapTemp = {};
    users.forEach(user => {
      userMapTemp[String(user.manager_number)] = user.name;
    });

    const allDates = [];
    const d = new Date(startDate);
    while (d <= new Date(endDate)) {
      allDates.push(d.toISOString().split('T')[0]);
      d.setDate(d.getDate() + 1);
    }

    setDates(allDates);
    setUserMap(userMapTemp);
    setShiftData(shifts);
    setShowTable(true);
  };

  const formatTime = (timeStr) => {
    if (!timeStr) return '';
    return timeStr.length >= 5 ? timeStr.slice(0, 5) : timeStr;
  };

  const groupedByUser = {};
  shiftData.forEach(shift => {
    const name = userMap[String(shift.manager_number)] || '(不明)';
    if (!groupedByUser[name]) groupedByUser[name] = {};
    groupedByUser[name][shift.date] = `${formatTime(shift.start_time)} ~ ${formatTime(shift.end_time)}`;
  });

  if (!showTable) {
  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>シフト作成</h2>

        <label style={{ marginTop: '1rem' }}>開始日:</label>
        <input
          type="date"
          value={startDate}
          onChange={e => setStartDate(e.target.value)}
          style={{ padding: '0.5rem', width: '100%', marginBottom: '1rem', fontSize: '1rem' }}
        />

        <label>終了日:</label>
        <input
          type="date"
          value={endDate}
          onChange={e => setEndDate(e.target.value)}
          style={{ padding: '0.5rem', width: '100%', marginBottom: '1rem', fontSize: '1rem' }}
        />

        <button
          onClick={fetchShiftData}
          style={{
            marginTop: '1rem',
            backgroundColor: '#3498db',
            color: '#fff',
            border: 'none',
            padding: '0.7rem 1.5rem',
            borderRadius: '8px',
            fontSize: '1rem',
            cursor: 'pointer',
            width: '100%',
          }}
        >
          次へ
        </button>
      </div>
    </div>
  );
}


  return (
    <div style={styles.wrapper}>
      <div style={{ ...styles.card, overflowX: 'auto' }}>
        <h2 style={styles.title}>シフト表</h2>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>名前</th>
              {dates.map(date => (
                <th key={date} style={styles.th}>{date}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.entries(groupedByUser).map(([name, shifts], idx) => (
              <tr key={name} style={idx % 2 === 0 ? styles.evenRow : styles.oddRow}>
                <td style={styles.td}>{name}</td>
                {dates.map(date => (
                  <td key={date} style={styles.td}>
                    {shifts[date] || ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

const styles = {
  wrapper: {
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    backgroundColor: '#f7f9fc',
    minHeight: '100vh',
    padding: '2rem',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-start',
  },
  card: {
    backgroundColor: '#fff',
    padding: '2.5rem 2rem',
    borderRadius: '12px',
    boxShadow: '0 6px 18px rgba(0,0,0,0.12)',
    width: '100%',
    maxWidth: '1280px',
  },
  title: {
    marginBottom: '2rem',
    color: '#222',
    textAlign: 'center',
    fontWeight: '700',
    fontSize: '1.8rem',
  },
  label: {
    display: 'block',
    marginTop: '1rem',
    marginBottom: '0.3rem',
    fontWeight: '600',
    color: '#555',
  },
  input: {
    width: '100%',
    padding: '0.5rem 0.7rem',
    fontSize: '1.1rem',
    borderRadius: '6px',
    border: '1px solid #bbb',
    boxSizing: 'border-box',
  },
  button: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    padding: '0.7rem 1.5rem',
    fontSize: '1.1rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    width: '100%',
    maxWidth: '240px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    tableLayout: 'auto',
    boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
  },
  th: {
    border: '1px solid #ccc',
    padding: '0.9rem 1rem',
    backgroundColor: '#e8f0fe',
    color: '#222',
    position: 'sticky',
    top: 0,
    zIndex: 10,
    fontWeight: '700',
    fontSize: '1rem',
    textAlign: 'center',
    minWidth: '90px',
  },
  td: {
    border: '1px solid #ddd',
    padding: '0.7rem 0.9rem',
    textAlign: 'center',
    color: '#333',
    whiteSpace: 'nowrap',
    fontSize: '0.95rem',
  },
  evenRow: {
    backgroundColor: '#fafafa',
    transition: 'background-color 0.25s ease',
  },
  oddRow: {
    backgroundColor: '#ffffff',
    transition: 'background-color 0.25s ease',
  },
  // ホバー時のスタイルはJSXで直接付与
};

export default ManagerCreate;
